INSERT INTO `posts` VALUES ('75', 'Bộ trưởng Phùng Xuân Nhạ nêu những việc ngành giáo dục cần làm ngay', 'N/a', '2016-05-21 11:00:35', '2016-05-21 10:23:38', '\"Hiện cả nước có gần 1,3 triệu giáo viên, nếu chúng ta tạo được động lực, khơi dậy được tinh thần trách nhiệm, tự trọng nghề nghiệp của gần 1,3 triệu con người ấy, thì sự nghiệp đổi mới giáo dục chắc chắn sẽ thành công\", Bộ trưởng Phùng Xuân Nhạ khẳng định.', 'http://img.f32.vnecdn.net/2016/05/21/Phung-Xuan-Nha-9367-1463798502_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bo-truong-phung-xuan-nha-neu-nhung-viec-nganh-giao-duc-can-lam-ngay-3406714.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('76', 'Trung Quốc khan hiếm thịt lợn', 'N/a', '2016-05-21 11:00:49', '2016-05-21 10:14:52', 'Giá thịt lợn được dự báo sẽ vẫn ở mức cao đến cuối năm, do số hộ gia đình nuôi lợn và doanh nghiệp nước này tiếp tục giảm cung ra thị trường, dù lợi nhuận lớn.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/21/china-pork-8822-1463797842_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/trung-quoc-khan-hiem-thit-lon-3406705.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('77', 'Đại gia ngoại không dễ bán lẻ xăng dầu tại Việt Nam', 'N/a', '2016-05-21 11:00:49', '2016-05-21 09:08:27', 'Theo thỏa thuận đầu tư, liên doanh của Nhật Bản và Kuwait sẽ chỉ phân phối sản phẩm do Lọc dầu Nghi Sơn sản xuất, song nhiều mặt hàng trong số này hiện được đánh giá là chưa đạt chuẩn chất lượng.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/13/NB-1463140383-4342-1463140455_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/dai-gia-ngoai-khong-de-ban-le-xang-dau-tai-viet-nam-3402656.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('78', '10 cách điên rồ để \'đốt tiền\' tại Dubai', 'N/a', '2016-05-21 11:00:49', '2016-05-21 01:00:00', 'Trượt tuyết, đi chợ vàng, nghỉ tại khách sạn 7 sao và đi tour vòng quanh sa mạc sẽ là trải nghiệm rất đáng tiền tại tiểu quốc này.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/10-cach-dien-ro-de-dot-tien-tai-dubai-1463737069_180x108.jpg', 'http://kinhdoanh.vnexpress.net/photo/quoc-te/10-cach-dien-ro-de-dot-tien-tai-dubai-3405889.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('79', 'USD ngân hàng tăng, vàng đi xuống', 'N/a', '2016-05-21 11:00:49', '2016-05-21 09:19:45', 'Chứng kiến phiên đi xuống của thị trường thế giới, các doanh nghiệp kinh doanh vàng trong nước cũng điều chỉnh giảm giá trong sáng nay. Trong khi đó, giá đôla Mỹ trong ngân hàng tăng 5 đồng đầu ngày.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/21/pnjDSC-0970-5454-1463796352_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/usd-ngan-hang-tang-vang-di-xuong-3406675.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('80', 'Mua căn hộ Docklands Saigon nhận ngay sổ hồng', 'N/a', '2016-05-21 11:00:49', '2016-05-21 09:00:00', 'Dự án xây dựng xong, sổ hồng tách riêng, tiện ích hiện hữu... chủ đầu tư mới chào bán sản phẩm ra thị trường.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/19/Image-ExtractWord-1-Out-1312-1463655689_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/mua-can-ho-docklands-saigon-nhan-ngay-so-hong-3405533.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('81', 'Vàng giảm 1,8% giá trị trong tuần', 'N/a', '2016-05-21 11:00:49', '2016-05-21 08:39:28', 'Tiếp tục đi xuống trong phiên thứ Sáu, mỗi ounce vàng đã khép lại tuần giao dịch với mức giảm khá mạnh.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/21/1329371458-2046-1463794494_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/vang-giam-1-8-gia-tri-trong-tuan-3406609.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('82', '5 quán lẩu đông khách dù nắng nóng', 'N/a', '2016-05-21 11:00:49', '2016-05-21 08:00:00', 'Lẩu ếch phố Phó Đức Chính, lẩu Thái tại ThaiExpress hay buffet lẩu Hotpot Story... luôn thu hút đông đảo thực khách dù đã vào hè.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/Image-ExtractWord-0-Out-8712-1463717265_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/5-quan-lau-dong-khach-du-nang-nong-3406022.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('83', 'Có nên bỏ thu nhập 16 triệu đồng để làm công việc yêu thích?', 'N/a', '2016-05-21 11:00:49', '2016-05-21 06:00:00', 'Tôi đang làm nhân viên tiếp thị cám gia súc với mức thu nhập 15-16 triệu đồng mỗi tháng. Liệu tôi có nên nghỉ để chuyển sang làm kỹ thuật trang trại lương thấp nhưng đúng chuyên môn và đam mê? (Văn Nam).', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/18/channuoi-1463559375_1463559408_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/khoi-nghiep/co-nen-bo-thu-nhap-16-trieu-dong-de-lam-cong-viec-yeu-thich-3405123.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('84', 'Sun Group vào top 10 chủ đầu tư hàng đầu Việt Nam', 'N/a', '2016-05-21 11:00:49', '2016-05-21 10:00:00', 'Tập đoàn này được vinh danh nhờ 3 dự án bất động sản nghỉ dưỡng hạng sang tại Phú Quốc, Kiên Giang.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/21/phu-quoc-9574-1463797987_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/sun-group-vao-top-10-chu-dau-tu-hang-dau-viet-nam-3406680.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('85', 'Người Trung Quốc thích mua rượu trên mạng', 'N/a', '2016-05-21 11:00:49', '2016-05-21 08:00:00', 'Phần đông người dùng tại Trung Quốc chuộng mua loại thức uống này qua hình thức online.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/20/72-2702-1463737412_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/thuong-mai-dien-tu/tin-quoc-te/nguoi-trung-quoc-thich-mua-ruou-tren-mang-3406441.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('86', 'Xe sang có thể mất thêm phí bảo vệ môi trường', 'N/a', '2016-05-21 11:00:49', '2016-05-20 17:14:14', 'Bộ Tài chính vừa đề nghị các bộ liên quan đề xuất phương án thu phí bảo vệ môi trường đối với ôtô có dung tích xi-lanh trên 3 lít. ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/oldu00l0hkjt11500x300JPG-1463739002_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/xe-sang-co-the-mat-them-phi-bao-ve-moi-truong-3406406.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('87', 'Xăng tiếp tục tăng giá 240 đồng một lít', 'N/a', '2016-05-21 11:00:49', '2016-05-20 14:33:57', 'Giá bán lẻ mỗi lít xăng RON 92 được các doanh nghiệp đầu mối bán lẻ phổ biến ở mức 15.820 đồng một lít từ 15h chiều nay (20/5).', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/PNTA0168-copy-3293-1463732958_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/xang-tiep-tuc-tang-gia-240-dong-mot-lit-3406150.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('88', 'Gửi tiết kiệm 4 tỷ để lấy lãi 288 triệu mỗi năm hay là đi buôn đất?', 'N/a', '2016-05-21 11:00:49', '2016-05-20 09:08:31', 'Với 4 tỷ đồng đầu tư vào đất đai, sau một năm bán đi tôi gần như chẳng lời gì. Do đó, tôi đang băn khoăn không biết có nên tiếp tục đi buôn đất hay là lấy số tiền này để gửi tiết kiệm và hưởng lãi 288 triệu đồng mỗi năm (lãi suất 7,2%)? (Thanh Nam). ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/vaytiennganhangcanluuygi11-1463680532_1463680553_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/tien-cua-toi/gui-tiet-kiem-4-ty-de-lay-lai-288-trieu-moi-nam-hay-la-di-buon-dat-3405933.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('89', 'Coca-Cola Venezuela ngừng hoạt động vì thiếu đường', 'N/a', '2016-05-21 11:00:49', '2016-05-20 19:31:00', 'Coca-Cola FEMSA - công ty phụ trách khu vực Mỹ Latin của Coca-Cola cho biết họ chưa có kế hoạch đóng cửa văn phòng hay rút khỏi Venezuela, song phải dừng sản xuất tại đây.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/20/coka-1463740205-7510-1463740240_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/coca-cola-venezuela-ngung-hoat-dong-vi-thieu-duong-3406510.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('90', 'Lãnh đạo tài chính G7 chia rẽ vì chính sách tiền tệ', 'N/a', '2016-05-21 11:00:49', '2016-05-20 15:42:40', 'Phiên họp của Bộ trưởng Tài chính và Thống đốc ngân hàng trung ương 7 nền kinh tế hàng đầu thế giới có thể cho thấy sự khác biệt trong quan điểm của của các nước giàu về chính sách tiền tệ và cách thúc đẩy tăng trưởng toàn cầu.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/G7-5129-1463732522_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/lanh-dao-tai-chinh-g7-chia-re-vi-chinh-sach-tien-te-3406386.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('91', 'Kinh tế Venezuela bị đẩy đến bờ sụp đổ', 'N/a', '2016-05-21 11:00:49', '2016-05-20 11:49:24', 'Tỷ lệ thất nghiệp 17%, lạm phát gần 500%, điện - nước - nhu yếu phẩm đều thiếu hụt đang khiến Venezuela ngày càng kiệt quệ.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/20/vene-8342-1463719202_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/kinh-te-venezuela-bi-day-den-bo-sup-do-3406251.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('92', 'Parkson Paragon đóng cửa và lời cảnh báo cho thị trường bán lẻ', 'N/a', '2016-05-21 11:00:50', '2016-05-20 04:00:00', 'Lo ngại <a href=\"http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/nguy-co-boi-thuc-mat-bang-ban-le-3265968.html?utm_source=detail&amp;utm_medium=box_relatedtop&amp;utm_campaign=boxtracking\">bội thực</a> mặt bằng bán lẻ tại TP HCM tăng thêm trong bối cảnh nguồn cung với loại hình bất động sản này tiếp tục dồi dào.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/20/parkson-2778-1463706722_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/parkson-paragon-dong-cua-va-loi-canh-bao-cho-thi-truong-ban-le-3405532.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('93', 'Những câu không bao giờ nên nói với sếp', 'N/a', '2016-05-21 11:00:50', '2016-05-20 01:00:00', 'Đừng nói thẳng với họ là \"Anh sai rồi\", \"Tôi không biết\" hay \"Đó có phải là việc của tôi đâu\", nếu bạn muốn thăng tiến trong sự nghiệp.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/19/boss-1463642843-7010-1463643052_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/tien-cua-toi/nhung-cau-khong-bao-gio-nen-noi-voi-sep-3405727.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('94', '14 doanh nghiệp bị phát hiện bán hàng đa cấp \'chui\'', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:49:50', '11 đơn vị trong số này hoạt động tại Hà Nội, còn lại kinh doanh trên địa bàn các tỉnh Nghệ An, Quảng Nam, Bà Rịa - Vũng Tàu.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/20/dacap-1463734607-9783-1463734624_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/14-doanh-nghiep-bi-phat-hien-ban-hang-da-cap-chui-3406422.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('95', 'Phó Thủ tướng yêu cầu sớm xử lý nợ cho Nông trường Sông Hậu', 'N/a', '2016-05-21 11:00:50', '2016-05-20 15:05:00', 'Chủ tịch UBND TP Cần Thơ đề nghị Chính phủ chỉ đạo sớm xử lý khoản nợ 400 tỷ đồng tại đơn vị một thời là lá cờ đầu của cả nước về hiện đại hóa nông nghiệp nông thôn.', 'http://img.f30.vnecdn.net/2016/05/20/chuoi-1463730928_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/pho-thu-tuong-yeu-cau-som-xu-ly-no-cho-nong-truong-song-hau-3406160.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('96', 'T Du học hè Singapore miễn phí', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:47:02', 'Các bạn đăng ký tham gia chuyến du học hè 7 ngày sẽ được miễn toàn bộ học phí tại AEC College và chi phí đi lại (chỉ cần chi phí sinh hoạt, vé máy bay và tiền đi tham quan).', 'http://img.f29.vnecdn.net/2016/05/20/sing1-1372731577-500x0-1576-14-1495-1595-1463737592_180x108.jpg', 'http://giadinh.vnexpress.net/tin-tuc/folder-test/t-du-hoc-he-singapore-mien-phi-3406472.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('97', 'CEO Wal-Mart \'nóng ruột\' về tốc độ tăng trưởng thương mại điện tử', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:05:04', 'Ông Doug McMillon cho rằng mức tăng trưởng 7% là quá thấp so với tiềm năng của thị trường này.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/walmart-store-interior-rtr-img-6430-1463732290_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/thuong-mai-dien-tu/tin-tuc/ceo-wal-mart-nong-ruot-ve-toc-do-tang-truong-thuong-mai-dien-tu-3406325.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('98', 'Nhựa Thiếu niên Tiền Phong ra mắt sản phẩm mới', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:00:00', 'Công ty vừa tổ chức \"Hội thảo giới thiệu ống nhựa HDPE DN 2000 và một số sản phẩm mới\" nhân kỷ niệm 56 năm thành lập.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/Untitled-3268-1463733991_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/nhua-thieu-nien-tien-phong-ra-mat-san-pham-moi-3406373.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('99', 'Bảo Tín Minh Châu trao quà cho khách trúng thưởng', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:00:00', 'Công ty vừa tổ chức lễ trao giải chương trình \"Mua nhẫn cưới vàng, trúng quà sang\" cho 27 khách hàng may mắn.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/20-5-201636-8535-1463734566_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/bao-tin-minh-chau-trao-qua-cho-khach-trung-thuong-3406318.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('100', 'Các thương hiệu bán lẻ đặt chỗ shophouse Vinhomes Dragon Bay', 'N/a', '2016-05-21 11:00:50', '2016-05-20 16:00:00', 'Theo kế hoạch, chủ đầu tư sẽ bàn giao nhà cho khách vào cuối năm 2016, nhưng hiện tại, nhiều doanh nghiệp bán lẻ đã tìm đến đặt chỗ.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/21/h1-1463796276_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/cac-thuong-hieu-ban-le-dat-cho-shophouse-vinhomes-dragon-bay-3406285.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('101', 'Samsung Galaxy S6, S6 Edge giá chỉ từ 6 triệu đồng', 'N/a', '2016-05-21 11:00:50', '2016-05-20 15:30:00', 'Hàng loạt điện thoại Samsung Galaxy S6, S6 Edge, Note 5... hàng xách tay phiên bản Mỹ đang được bán với giá ưu đãi tại TechOne.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/20/20-5-201644-9773-1463728220_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/khuyen-mai/samsung-galaxy-s6-s6-edge-gia-chi-tu-6-trieu-dong-3406159.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('102', 'Sở hữu Office-tel Sunrise Cityview chỉ từ 280 triệu đồng', 'N/a', '2016-05-21 11:00:50', '2016-05-20 14:30:00', 'Lịch thanh toán 1,2% mỗi tháng kéo dài trong hơn 4 năm giúp khách hàng dễ dàng sở hữu căn hộ đa năng ngay quận 7, TP HCM.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/20/Image-720024301-ExtractWord-1-3061-1516-1463716064_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/so-huu-office-tel-sunrise-cityview-chi-tu-280-trieu-dong-3405848.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('103', 'Những yếu tố kích hoạt bệnh vẩy nến', 'N/a', '2016-05-21 11:00:50', '2016-05-20 14:00:00', 'Sự rối loạn hệ miễn dịch và yếu tố di truyền được xem là nguyên nhân quan trọng làm phát triển căn bệnh.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/Image-471877961-ExtractWord-0-7342-6745-1463723901_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/nhung-yeu-to-kich-hoat-benh-vay-nen-3405860.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('104', 'Cơ hội kinh doanh tại Triển lãm Fi Vietnam', 'N/a', '2016-05-21 11:00:50', '2016-05-20 14:00:00', 'Tham dự triển lãm, các doanh nghiệp có cơ hội hiểu thêm về công nghệ mới, đồng thời tìm kiếm đối tác và khách hàng.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/14/Image-864120624-ExtractWord-1-1395-5639-1463191595_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/co-hoi-kinh-doanh-tai-trien-lam-fi-vietnam-3402373.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('106', 'Giá căn hộ vẫn tăng', 'N/a', '2016-05-21 11:00:50', '2016-05-20 10:46:39', 'Tuy giảm nhẹ so với quý cuối năm 2015 do tính mùa vụ, song chỉ số giá nhà trong quý đầu 2016 vẫn tăng so với cùng kỳ năm ngoái. ', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/20/bdsTam1-1463714267_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/gia-can-ho-van-tang-3406119.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('107', 'Dem.vn ưu đãi tới 50% mừng khai trương 2 showroom mới', 'N/a', '2016-05-21 11:00:50', '2016-05-20 10:32:00', 'Khách đến thăm quan 2 showroom mới được mua các sản phẩm chất lượng với giá ưu đãi, đồng thời nhận quà tặng giá trị.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/19/19-5-201618-283222178-1882-1463650767_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/khuyen-mai/dem-vn-uu-dai-toi-50-mung-khai-truong-2-showroom-moi-3405614.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('108', 'Khu tái định cư hơn 1.000 tỷ đồng ở Sài Gòn chỉ lấp đầy 15%', 'N/a', '2016-05-21 11:00:50', '2016-05-20 09:55:36', 'Được đưa vào sử dụng cách đây nửa thập niên, khu nhà tái định cư Vĩnh Lộc B, huyện Bình Chánh, TP HCM quy mô 1.939 căn hộ, 529 nền nhà nhưng đến nay mới có 306 căn hộ và 222 nền được bàn giao bố trí dân vào ở, chiếm tỷ lệ 15%.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/a-tb-khu-tai-dinh-cu-tay-bac-s-7147-2960-1463711725_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/khu-tai-dinh-cu-hon-1-000-ty-dong-o-sai-gon-chi-lap-day-15-3406063.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('109', 'Tỷ giá trung tâm tiếp tục tăng, giá vàng nhích nhẹ', 'N/a', '2016-05-21 11:00:50', '2016-05-20 09:20:35', 'Sau khi tăng mạnh 21 đồng ngày hôm qua, sáng nay tỷ giá trung tâm được Ngân hàng Nhà nước nâng thêm 7 đồng. Trong khi đó, giá vàng trong nước sáng nay được các doanh nghiệp điều chỉnh lên vài chục nghìn đồng. ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/doji1-7811-1463710521_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/ty-gia-trung-tam-tiep-tuc-tang-gia-vang-nhich-nhe-3406015.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('110', 'Ưu đãi \'mùa Euro\' tại điện máy Thiên Hòa', 'N/a', '2016-05-21 11:00:50', '2016-05-20 09:00:00', 'Ngoài giảm giá hàng loạt sản phẩm, Thiên Hòa còn tổ chức nhiều chương trình khuyến mãi, bốc thăm trúng thưởng với các phần quà giá trị.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/thienhoa-3345-1463709278_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/khuyen-mai/uu-dai-mua-euro-tai-dien-may-thien-hoa-3405611.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('111', 'Đồng USD mạnh tiếp tục gây áp lực lên giá vàng', 'N/a', '2016-05-21 11:00:50', '2016-05-20 08:19:04', 'Chỉ số đồng bạc xanh lên mức cao nhất bốn tuần đã đẩy giá vàng thế giới sụt giảm gần 4 USD khi chốt phiên Mỹ tối 19/5. ', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/gold2-1463706803_1463706824_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/dong-usd-manh-tiep-tuc-gay-ap-luc-len-gia-vang-3405973.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('112', 'Kangaroo lắp đặt hệ thống nước uống miễn phí cho bệnh nhân', 'N/a', '2016-05-21 11:00:50', '2016-05-20 08:00:00', 'Bệnh nhân của bệnh viện Thống Nhất, TP HCM được sử dụng nguồn nước sạch miễn phí từ hệ thống lọc nước uống liền hiện đại của Tập đoàn Kangaroo.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/19/19-5-201649-8281-1463647002_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/kangaroo-lap-dat-he-thong-nuoc-uong-mien-phi-cho-benh-nhan-3405613.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('113', 'Lý do bất động sản hạng sang sẽ còn tăng giá', 'N/a', '2016-05-21 11:00:50', '2016-05-20 11:54:00', 'Vị trí trung tâm, kế cận mặt nước, có tuyến tàu điện ngầm chạy qua là những yếu tố đảm bảo khả năng tăng giá của các dự án, theo phân tích của giới chuyên gia.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/20/20-5-201648-6764-1463711019_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/ly-do-bat-dong-san-hang-sang-se-con-tang-gia-3406021.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('114', 'Hoa Sen khởi công tổ hợp bất động sản 1.200 tỷ đồng tại Yên Bái', 'N/a', '2016-05-21 11:00:50', '2016-05-20 09:00:00', 'Đây là dự án bất động sản, dịch vụ và du lịch đầu tiên của Tập đoàn Hoa Sen.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/19/ths1-JPG-4459-1463649869_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/hoa-sen-khoi-cong-to-hop-bat-dong-san-1-200-ty-dong-tai-yen-bai-3404645.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('115', 'TP HCM tụt hậu do \'đầu tàu mà chạy như toa tàu\'', 'N/a', '2016-05-21 11:00:50', '2016-05-19 16:20:06', 'Các chuyên gia cho rằng có quá nhiều \"vòng kim cô\" về cơ chế, thể chế khiến TP HCM phát triển không như kỳ vọng, tụt hậu so với các đô thị khác trong khu vực.', 'http://img.f29.vnecdn.net/2016/05/19/3-9864-1463645715-2711-1463648449_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/tp-hcm-tut-hau-do-dau-tau-ma-chay-nhu-toa-tau-3405700.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('116', 'Bán lẻ Việt khó tìm chỗ trong \'bữa tiệc\' 180 tỷ USD', 'N/a', '2016-05-21 11:00:50', '2016-05-19 16:07:54', 'Được dự báo có tổng doanh thu lên tới 179 tỷ USD vào năm 2020, song thị trường bán lẻ Việt dường như lại rất khó chen chân với ngay chính các doanh nghiệp nội, nhất là trong bối cảnh phải cạnh tranh với các ông lớn nước ngoài.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/19/sieu-thi-4296-1463648398_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/ban-le-viet-kho-tim-cho-trong-bua-tiec-180-ty-usd-3405354.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('117', 'Thị trường xe máy Việt ngày càng chật chội', 'N/a', '2016-05-21 11:00:50', '2016-05-19 14:35:54', 'Lượng tiêu thụ vẫn thuộc nhóm hàng đầu thế giới song xu hướng giảm dần những năm gần đây cho thấy thị trường xe máy Việt Nam đang dần bão hòa, khiến các hãng xe phải tìm cách thay đổi để bảo vệ những \"đế chế\" tỷ đô của mình.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/19/xemay0-1463632686_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/thi-truong-xe-may-viet-ngay-cang-chat-choi-3403384.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('118', 'Cổ phiếu Airbus mất giá vì máy bay rơi', 'N/a', '2016-05-21 11:00:50', '2016-05-19 16:02:36', 'Thông tin máy bay EgyptAir có khả năng rơi xuống biển khiến cổ phiếu Airbus mở cửa mất 1,3%.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/19/set5-1463647104-9414-1463647159_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/co-phieu-airbus-mat-gia-vi-may-bay-roi-3405801.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('119', 'Xăng nhiều khả năng không tăng giá ngày mai', 'N/a', '2016-05-21 11:00:50', '2016-05-19 11:11:14', 'Giá thế giới chỉ nhích nhẹ trong 2 tuần qua, trong khi cả nước sắp bước vào kỳ bầu cử các cấp cuối tuần này là những lý do khiến các doanh nghiệp đầu mối không kỳ vọng giá tăng trong kỳ điều chỉnh ngày mai (20/5).', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/19/xang-6423-1463627121_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/xang-nhieu-kha-nang-khong-tang-gia-ngay-mai-3405528.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('120', 'Mỹ có thể tăng lãi suất trong tháng 6', 'N/a', '2016-05-21 11:00:50', '2016-05-19 10:27:41', 'Trong biên bản mới nhất, Cục Dự trữ liên bang Mỹ (Fed) hé lộ khả năng tăng lãi trong tháng tới nếu các số liệu kinh tế theo hướng thuận lợi.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/19/fed-1463626448-6575-1463626491_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/my-co-the-tang-lai-suat-trong-thang-6-3405527.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('121', 'Ngân hàng lo \'thắt lưng buộc bụng\'', 'N/a', '2016-05-21 11:00:50', '2016-05-19 08:01:02', 'Hoạt động kinh doanh khó khăn, trích lập dự phòng ngày càng tăng... buộc các ngân hàng phải tìm cách \"thắt lưng, buộc bụng\" lên kế hoạch tiết giảm tối đa chi phí để giải bài toán lợi nhuận cho cổ đông.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/18/tiennganhang14931-5239-1432928-5983-3249-1463563770_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/ngan-hang/ngan-hang-lo-that-lung-buoc-bung-3405179.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('122', 'Gạo Việt ra sao nếu Thái Lan xả kho kỷ lục', 'N/a', '2016-05-21 11:00:50', '2016-05-19 04:00:00', 'Đợt xả hàng lớn nhất từ trước đến nay của Thái Lan đa phần là gạo cũ nên nếu có ảnh hưởng cũng chỉ tác động vào giá gạo cấp thấp của Việt Nam.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/18/gao1-1463582635_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gao-viet-ra-sao-neu-thai-lan-xa-kho-ky-luc-3404570.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('123', 'Người nuôi tôm Cà Mau thiệt hại khoảng 260 tỷ đồng', 'N/a', '2016-05-21 11:00:50', '2016-05-19 09:13:47', 'Hơn 3 tháng sau khi Cà Mau công bố thiên tai cấp độ 1 đối với lúa, Bộ Nông nghiệp đề nghị địa phương này xem xét công bố thiên tai đối với tôm nuôi.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/19/tomCamaust-1463623471-8478-1463623506_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/nguoi-nuoi-tom-ca-mau-thiet-hai-khoang-260-ty-dong-3405471.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('124', 'Giá vàng lao dốc', 'N/a', '2016-05-21 11:00:50', '2016-05-19 08:14:37', 'Đồng đôla tăng mạnh đẩy giá vàng quốc tế giảm hàng chục USD và xuống mức thấp trong tuần.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/19/goldBA1ADB1000005DC-3173762-Kn-9623-2165-1463620207_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-vang-lao-doc-3405358.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('125', '8 địa chỉ ăn uống lâu năm \'thử là ghiền\' ở Sài Gòn', 'N/a', '2016-05-21 11:05:18', '2016-05-21 09:52:04', 'Xe chè 40 năm trên đường Đinh Tiên Hoàng hay quán cà phê vợt 60 năm ở Phan Đinh Phùng là những địa điểm ăn uống thực khách luôn tìm đến và thưởng thức.', 'http://img.f33.dulich.vnecdn.net/2016/05/21/2-1463795603_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/8-dia-chi-an-uong-lau-nam-thu-la-ghien-o-sai-gon-3406669.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('126', 'Quán bar trên bãi biển nổi như cồn ở Quy Nhơn', 'N/a', '2016-05-21 11:05:18', '2016-05-21 08:56:41', 'Quán nằm ngay trên bãi biển lộng gió, du khách đến đây có thể thả chân xuống nền cát mịn, lắng nghe tiếng sóng biển rì rào và ngắm hoàng hôn buông.', 'http://img.f21.ngoisao.vnecdn.net/2016/05/20/sb4-7995-1463480752-3280-1463716758_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/quan-bar-tren-bai-bien-noi-nhu-con-o-quy-nhon-3406169.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('127', 'Ba nơi đáng sợ nhất Hàn Quốc', 'N/a', '2016-05-21 11:05:18', '2016-05-21 07:37:00', 'Đặt chân tới khu phi quân sự DMZ, nhiều du khách cho biết họ có chút e sợ khi phải nộp hộ chiếu, ăn mặc nghiêm túc, chỉ được chụp ảnh tại những khu vực được phép.', 'http://img.f35.dulich.vnecdn.net/2016/05/19/untitled1-1463647201_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/ba-noi-dang-so-nhat-han-quoc-3405715.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('128', 'Sông hoa anh đào đẹp nghẹt thở ở Tokyo', 'N/a', '2016-05-21 11:05:18', '2016-05-21 05:05:00', 'Tokyo là một trong những thành phố hoa anh đào nở muộn nhất nhưng cũng rực rỡ và đông người tham quan nhất vào mùa lễ hội hoa anh đào ở Nhật Bản.', 'http://img.f22.ngoisao.vnecdn.net/2016/05/15/sakura1-1463274866_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/song-hoa-anh-dao-dep-nghet-tho-o-tokyo-3406184.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('129', 'Bên trong ngôi chùa tổng thống Mỹ dự kiến tới thăm', 'N/a', '2016-05-21 11:05:18', '2016-05-21 00:05:00', 'Chùa Ngọc Hoàng hơn 100 năm tuổi ở quận 1, TP HCM nằm trong lịch trình dự kiến ghé thăm của tổng thống Mỹ Obama vào chiều 24/5.', 'http://img.f35.dulich.vnecdn.net/2016/05/20/2-1463732338_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/ben-trong-ngoi-chua-tong-thong-my-du-kien-toi-tham-3406381.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('130', 'Ưu đãi suốt tuần cho golf thủ trên đỉnh Bà Nà', 'N/a', '2016-05-21 11:05:18', '2016-05-21 09:00:00', 'Ngoài thưởng ngoạn vẻ đẹp thơ mộng của thiên nhiên Bà Nà, các golf thủ có cơ hội chinh phục thử thách và nhận ưu đãi tại Ba Na Hills Golf Club.', 'http://img.f34.dulich.vnecdn.net/2016/05/21/2-1463790235_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/da-nang/uu-dai-suot-tuan-cho-golf-thu-tren-dinh-ba-na-3406590.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('131', 'Các tổng thống Mỹ từng ở đâu khi tới Việt Nam', 'N/a', '2016-05-21 11:05:18', '2016-05-20 15:03:00', 'Daewoo, Metropole và New World là nơi các tổng thống Mỹ từng chọn nghỉ khi sang thăm Việt Nam.', 'http://img.f34.dulich.vnecdn.net/2016/05/20/DaeWoo-4945-1463729219_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/cac-tong-thong-my-tung-o-dau-khi-toi-viet-nam-3406329.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('132', 'Bẻ đuôi cá sấu để chụp ảnh selfie', 'N/a', '2016-05-21 11:05:18', '2016-05-20 19:00:00', 'Khi du khách tạo dáng, con cá sấu tỏ ra rất hợp tác và không hề có phản ứng hung dữ nào.', 'http://img.f34.dulich.vnecdn.net/2016/05/20/3452E0C8000005783596306TheCanadianwomangetstogripswiththehugebeastandgoestoa61463563683913-1463705497_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/be-duoi-ca-sau-de-chup-anh-selfie-3405978.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('133', 'Phòng tổng thống trong khách sạn đón đoàn của Obama', 'N/a', '2016-05-21 11:05:18', '2016-05-20 15:47:19', 'Phòng tổng thống của khách sạn JW Marriott Hanoi có diện tích 320 m2, được thiết kế gồm 8 phòng riêng biệt và các cửa kính lớn nhìn ra thành phố.', 'http://img.f35.dulich.vnecdn.net/2016/05/20/1-Presidential-Suite-Bedroom-1463709680_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/phong-tong-thong-trong-khach-san-don-doan-cua-obama-3406040.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('134', 'Cuối tuần du ngoạn \'Hạ Long trên cạn\'', 'N/a', '2016-05-21 11:05:18', '2016-05-20 13:48:26', 'Nằm trên lòng hồ sông Đà thuộc địa phận huyện Cao Phong tỉnh Hòa Bình, Thung Nai phù hợp cho một chuyến đi ngắn ngày cuối tuần để cảm nhận sự hòa quyện của sông nước, núi non và những con người hiền hòa dễ mến.', 'http://img.f33.dulich.vnecdn.net/2016/05/20/3-HA8-5981-15-1463718462_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/cuoi-tuan-du-ngoan-ha-long-tren-can-3406246.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('135', 'Món ngon trên các cung đường miền Trung', 'N/a', '2016-05-21 11:05:18', '2016-05-20 10:31:00', 'Dọc chiều dài từ Thanh Hóa đi hết miền Nam Trung Bộ và Tây Nguyên, bạn sẽ được thử những món ăn mà chắc chắn sẽ nhớ mãi. ', 'http://img.f24.ngoisao.vnecdn.net/2016/05/18/ngontrung-1463565251_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/mon-ngon-tren-cac-cung-duong-mien-trung-3406091.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('136', 'Á hậu Thiên Lý trượt zipline \'như bay\' ở Quảng Bình', 'N/a', '2016-05-21 11:05:18', '2016-05-20 08:26:52', '\"Tôi sẽ quay lại và chinh phục bằng được các thử thách zipline ngoài hang Tối\", Á hậu Dương Trương Thiên Lý chia sẻ về cảm giác tuyệt vời như bay tự do trên đường trượt, trước khi rơi tòm xuống lưới. ', 'http://img.f36.dulich.vnecdn.net/2016/05/20/cover-1463707427_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/a-hau-thien-ly-truot-zipline-nhu-bay-o-quang-binh-3405548.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('137', 'Du thuyền lớn nhất thế giới ra khơi lần đầu', 'N/a', '2016-05-21 11:05:18', '2016-05-20 14:20:42', 'Harmony of the Seas. hay Hòa âm của biển, có chuyến hải hành đầu tiên trong đời. Đây là du thuyền lớn nhất thế giới với chiều dài hơn cả độ cao của tháp Eiffel.', 'http://img.f30.vnecdn.net/2016/05/20/dieu-gi-dac-biet-trong-du-thuyen-lon-nhat-the-gioi-1463729071_180x108.jpg', 'http://vnexpress.net/infographics/quoc-te/du-thuyen-lon-nhat-the-gioi-ra-khoi-lan-dau-3406138.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('138', 'Cây cầu biến mất giữa biển nối Đan Mạch và Thụy Điển', 'N/a', '2016-05-21 11:05:18', '2016-05-20 12:03:00', 'Oresund là sự kết hợp giữa một cầy cầu dây văng và một đường hầm ngầm dưới biển, tạo cảm giác như đột ngột biến mất giữa biển khơi.', 'http://img.f36.dulich.vnecdn.net/2016/05/20/oresund-bridge-1-7917-14637052-4193-4610-1463705330_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/cay-cau-bien-mat-giua-bien-noi-dan-mach-va-thuy-dien-3405975.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('139', 'Khách nhốt mình trong toilet máy bay vì không được uống bia', 'N/a', '2016-05-21 11:05:18', '2016-05-20 08:50:16', 'Khi yêu cầu một cốc bia trên chuyến bay và bị tiếp viên từ chối, hành khách đã tự giam mình trong nhà vệ sinh và đập liên tục vào cửa.', 'http://img.f34.dulich.vnecdn.net/2016/05/20/occupied-airplane-lavatory0516-9621-1463708257_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/khach-nhot-minh-trong-toilet-may-bay-vi-khong-duoc-uong-bia-3406019.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('140', 'Du khách chết đuối vì bị hải mã ôm ghì', 'N/a', '2016-05-21 11:05:18', '2016-05-20 07:37:00', 'Một du khách bị ngã xuống bể nuôi hải mã trong vườn thú. Nhân viên nhảy xuống cứu khách, nhưng cả hai bị hải mã ôm ghì khiến tử vong. ', 'http://img.f35.dulich.vnecdn.net/2016/05/19/020160517170041-2366-1463653849_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-chet-duoi-vi-bi-hai-ma-om-ghi-3405712.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('141', 'Những khoảnh khắc đời thường ở Thái Lan', 'N/a', '2016-05-21 11:05:18', '2016-05-20 02:08:00', 'Thủ đô Bangkok từng được mệnh danh là \"Venice của phương Đông\" vì có nhiều kênh đào và hiện nay nơi này trở thành một điểm đến du lịch nổi tiếng thế giới.', 'http://img.f36.dulich.vnecdn.net/2016/05/19/10-1463623746_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/nhung-khoanh-khac-doi-thuong-o-thai-lan-3405475.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('142', 'Nhiều ưu đãi mùa hè tại Asean Resort &amp; Spa', 'N/a', '2016-05-21 11:05:18', '2016-05-20 15:00:00', 'Khu nghỉ dưỡng cao cấp sẽ dành cho khách voucher ưu đãi gói dịch vụ vui chơi, thư giãn và giảm giá phòng dịp hè.', 'http://img.f33.dulich.vnecdn.net/2016/05/19/5-1463632346_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-noi/nhieu-uu-dai-mua-he-tai-asean-resort-amp-spa-3405603.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('143', 'Tour Phú Quốc - Vinpearl Land gồm vé máy bay từ 3,9 triệu đồng', 'N/a', '2016-05-21 11:05:18', '2016-05-20 09:00:00', 'Hành trình trọn gói và Free &amp; Easy tới Phú Quốc cùng nhiều lựa chọn, khuyến mãi dịp hè.', 'http://img.f34.dulich.vnecdn.net/2016/05/19/3-1463650392_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/chu-de/deal/tour-phu-quoc-vinpearl-land-gom-ve-may-bay-tu-3-9-trieu-dong-3405837.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('144', 'Điều gì xảy ra trong cơ thể khi đi máy bay', 'N/a', '2016-05-21 11:05:18', '2016-05-19 19:00:00', 'Ù tai, chóng mặt là những điều du khách thường xuyên gặp phải khi ngồi trên những chuyến bay dài.', 'http://img.f33.dulich.vnecdn.net/2016/05/18/dieu-gi-xay-ra-trong-co-the-khi-di-may-bay-1463540137_180x108.jpg', 'http://vnexpress.net/infographics/anh-video/dieu-gi-xay-ra-trong-co-the-khi-di-may-bay-3404874.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('145', 'Phòng tổng thống có view đẹp nhất Hà Nội', 'N/a', '2016-05-21 11:05:18', '2016-05-19 14:12:56', 'Phòng Tổng thống là phòng sang trọng bậc nhất của khách sạn InterContinental Hanoi Westlake với diện tích lên đến 305 m2 và 7 ban công riêng biệt.', 'http://img.f32.vnecdn.net/2016/05/19/IMG-7839-1463640943_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/phong-tong-thong-co-view-dep-nhat-ha-noi-3405683.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('146', 'Cuộc sống đêm ở nơi chỉ cách chiến tranh hơn một km', 'N/a', '2016-05-21 11:05:18', '2016-05-19 20:03:00', 'Về đêm bạn có thể bắt gặp giới trẻ tụ tập ăn uống, hút thuốc, trò chuyện trong các quán bar, câu lạc bộ ở Damascus, thủ đô và cũng là thành phố chịu nhiều ảnh hưởng của nội chiến Syria những năm gần đây.', 'http://img.f33.dulich.vnecdn.net/2016/05/19/syrian-war-night-life-damascuss-old-city-rebel-rebel-held_1463653208_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/cuoc-song-dem-o-noi-chi-cach-chien-tranh-hon-mot-km-3405071.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('147', 'Du khách được dạy ném lựu đạn thật tại Campuchia', 'N/a', '2016-05-21 11:05:18', '2016-05-19 15:47:15', '\'Thực đơn\' cho du khách lựa chọn gồm có nhiều loại lựu đạn và các loại súng như AK-47, M16, K50 Tommy Guns, Uzis và súng săn bán tự động.', 'http://img.f36.dulich.vnecdn.net/2016/05/19/TouristthrewagrenadeinthepondinCambodia-1463647011_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-duoc-day-nem-luu-dan-that-tai-campuchia-3405714.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('148', 'Việt Nam không có loại hình du lịch tình dục', 'N/a', '2016-05-21 11:05:18', '2016-05-19 13:52:47', 'Phó tổng cục trưởng Tổng cục Du lịch khẳng định Việt Nam không có loại hình du lịch tình dục, nhưng có thể có tình trạng tội phạm ấu dâm nhập cảnh thông qua con đường du lịch.', 'http://img.f34.dulich.vnecdn.net/2016/05/16/soicowboysextourist-1463369794_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/viet-nam-khong-co-loai-hinh-du-lich-tinh-duc-3403593.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('149', 'Khách Tây nhận xét Việt Nam là điểm đến lý tưởng cho trẻ em', 'N/a', '2016-05-21 11:05:18', '2016-05-19 12:08:47', 'Phong cảnh thiên nhiên tươi đẹp, con người thân thiện, ẩm thực độc đáo ở Việt Nam là những yếu tố quan trọng để đem đến cho trẻ em những kỷ niệm vô giá.', 'http://img.f35.dulich.vnecdn.net/2016/05/19/playing-jpeg-8968-1463634014_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/khach-tay-nhan-xet-viet-nam-la-diem-den-ly-tuong-cho-tre-em-3405657.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('150', 'Côn Đảo vào top 10 điểm đến đáng sợ ở châu Á', 'N/a', '2016-05-21 11:05:18', '2016-05-19 10:54:30', 'Trang CNN đã liệt kê 10 địa điểm được nhiều du khách cảm thấy lạnh gáy nhất khi tới tham quan. Nhà tù Côn Đảo, Việt Nam cũng nằm trong danh sách này.', 'http://img.f35.dulich.vnecdn.net/2016/05/18/9-1463559050_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/con-dao-vao-top-10-diem-den-dang-so-o-chau-a-3405122.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('151', 'Trợ lý Ngoại trưởng Mỹ: \'Sơn Đoòng là một trong những trải nghiệm đáng nhớ nhất\'', 'N/a', '2016-05-21 11:05:18', '2016-05-19 09:59:32', 'Theo trợ lý Ngoại trưởng Mỹ Tom Malinowski, không một phim ảnh nào có thể lột tả hết vẻ đẹp thực tế của hang Sơn Đoòng.', 'http://img.f35.dulich.vnecdn.net/2016/05/19/tro-ly-ngoai-truong-my-son-doong-la-mot-trong-nhung-trai-nghiem-dang-nho-nhat-1463628595_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/tro-ly-ngoai-truong-my-son-doong-la-mot-trong-nhung-trai-nghiem-dang-nho-nhat-3405480.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('152', 'Vách đá bảo toàn danh tiết của 4.000 cung nữ', 'N/a', '2016-05-21 11:05:18', '2016-05-19 05:05:00', 'Hơn 4.000 cung nữ đã chạy tới núi Busosan và đứng từ trên cao nhảy xuống sông tự vẫn khi bị giặc truy đuổi. Những cánh hoa rơi đã quyết chọn cái chết để bảo toàn danh tiết của mình.', 'http://img.f35.dulich.vnecdn.net/2016/05/17/nhung-canh-hoa-roi-o-nakhwaam-1463458608_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/vach-da-bao-toan-danh-tiet-cua-4-000-cung-nu-3404314.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('153', 'Du thuyền Nhiêu Lộc dừng hoạt động vì cá chết', 'N/a', '2016-05-21 11:05:18', '2016-05-19 08:47:15', 'Chuyến đi của du thuyền trên kênh Nhiêu Lộc, TPHCM, dời lại ba ngày do tình hình cá chết la liệt sau cơn mưa đầu mùa. ', 'http://img.f34.dulich.vnecdn.net/2016/05/19/Anh2-9716-1463620363_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/du-thuyen-nhieu-loc-dung-hoat-dong-vi-ca-chet-3405402.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('154', 'Dừng hoạt động tàu bị tố \'chặt chém\' khách tại Hạ Long', 'N/a', '2016-05-21 11:05:18', '2016-05-19 08:05:00', 'Sau khi bị khách tố \"chặt chém\" bữa ăn trưa, tàu du lịch Hồng Long đã bị tạm dừng hoạt động để xác minh.', 'http://img.f34.dulich.vnecdn.net/2016/05/19/tau-khach-du-lich-hong-long-qu-7143-7200-1463618499_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-long/dung-hoat-dong-tau-bi-to-chat-chem-khach-tai-ha-long-3405359.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('155', 'Thung lũng xanh mướt ở Mai Châu', 'N/a', '2016-05-21 11:05:18', '2016-05-19 11:00:00', 'Bao phủ khắp thung lũng là màu xanh mơn mởn, Mai Châu Ecolodge đẹp lung linh níu chân du khách đến chốn này.', 'http://img.f36.dulich.vnecdn.net/2016/05/18/1-1463557610_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/hoa-binh/thung-lung-xanh-muot-o-mai-chau-3405067.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('156', 'Điều \'không hoàn hảo\' ở tượng khỏa thân đẹp nhất thế giới', 'N/a', '2016-05-21 11:05:18', '2016-05-18 12:03:00', 'Nhiều người cho rằng tượng David có một chi tiết thiếu hoàn hảo, đó là dương vật nhỏ hơn bình thường. Tuy nhiên Michelangelo đã tạc bức tượng theo phong cách Hy Lạp cổ điển, nơi mà cơ bắp thực thụ quan trọng hơn bộ phận kia nhiều lần.', 'http://img.f34.dulich.vnecdn.net/2016/05/18/21-1463542925_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/dieu-khong-hoan-hao-o-tuong-khoa-than-dep-nhat-the-gioi-3404817.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('157', 'Nhà sư robot gây sốt ở Trung Quốc', 'N/a', '2016-05-21 11:05:18', '2016-05-18 20:03:00', 'Nhà sư có tên Xian\'er trong trang phục áo cà sa đang gây sốt, thu hút rất nhiều tín đồ đến tham quan chùa Long Tuyền ở thủ đô Bắc Kinh.', 'http://img.f33.dulich.vnecdn.net/2016/05/11/robotmonk55-1462940568-7548-1462941237_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/bac-kinh/nha-su-robot-gay-sot-o-trung-quoc-3401141.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('158', 'Lời nói dối ngọt ngào thường dùng khi đi du lịch', 'N/a', '2016-05-21 11:05:18', '2016-05-18 19:00:00', 'Tôi không biết nói tiếng Anh, em có chồng rồi... là những lời nói được nhiều du khách sử dụng trong trường hợp muốn an toàn hoặc thoát thân nhanh chóng khỏi những kẻ đeo bám phiền phức ở nơi xa lạ.', 'http://img.f34.dulich.vnecdn.net/2016/05/14/1-1463196707_180x108.png', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/loi-noi-doi-ngot-ngao-thuong-dung-khi-di-du-lich-3402878.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('159', 'Cảnh đời thường làm say lòng du khách ở Phú Quốc', 'N/a', '2016-05-21 11:05:18', '2016-05-18 16:03:00', 'Bãi Khem, Bãi Sao, làng chài Hàm Ninh, rừng nguyên sinh, thị trấn Dương Đông....là những điểm dừng chân lý tưởng để trải nghiệm nhiều điều lý thú về cuộc sống bình dị ở nơi đảo xa.', 'http://img.f34.dulich.vnecdn.net/2016/05/14/hinh1PQ-1463200898_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/canh-doi-thuong-lam-say-long-du-khach-o-phu-quoc-3402988.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('160', 'Những điều kiêng kị khi du lịch trên thế giới', 'N/a', '2016-05-21 11:05:18', '2016-05-18 13:16:55', 'Người Latvia luôn tránh phải quay về nhà lấy đồ bỏ quên và người Nga thì không bao giờ ngồi lên hành lý của mình trước khi đi vì những hành động này đều được cho là mang đến đen đủi. ', 'http://img.f35.dulich.vnecdn.net/2016/05/17/woman-sitting-on-suitcase-2163-1463471887_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/nhung-dieu-kieng-ki-khi-du-lich-tren-the-gioi-3404178.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('161', 'Chàng trai gây sốt với chuyến du lịch Quảng Bình 2,6 triệu đồng', 'N/a', '2016-05-21 11:05:18', '2016-05-18 05:05:00', 'Chia sẻ kinh nghiệm du lịch Quảng Bình 3 ngày 2 đêm với chi phí chưa đến 3 triệu đồng, album ảnh của Tuấn Anh nhận được hàng nghìn lượt thích và bình luận.', 'http://img.f33.dulich.vnecdn.net/2016/05/17/5-1463478329_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/chang-trai-gay-sot-voi-chuyen-du-lich-quang-binh-26-trieu-dong-3404608.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('162', 'Mèo thành nhân viên phục vụ trên máy bay', 'N/a', '2016-05-21 11:05:18', '2016-05-18 14:40:06', 'Meow Meow bất ngờ nổi lên như một hiện tượng trên mạng xã hội nhờ vẻ ngoài đáng yêu và phong cách phục vụ chuyên nghiệp trên chuyến bay của Canada North.', 'http://img.f36.dulich.vnecdn.net/2016/05/18/92003cd014204f0895d414cc75ede721-1463546015_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/meo-thanh-nhan-vien-phuc-vu-tren-may-bay-3404994.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('163', 'Bánh xèo vào top món đường phố được yêu thích nhất', 'N/a', '2016-05-21 11:05:18', '2016-05-18 10:31:43', 'Bánh xèo là một trong những món ăn Việt Nam tham gia Đại hội ẩm thực đường phố 2016 (WSFC) tổ chức tại Manila, Philippines.', 'http://img.f36.dulich.vnecdn.net/2016/05/18/banh-xeo-vao-top-mon-duong-pho-duoc-yeu-thich-nhat-1463541635_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/banh-xeo-vao-top-mon-duong-pho-duoc-yeu-thich-nhat-3404827.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('164', 'Singapore \'ghen tỵ\' với thiên nhiên Việt Nam', 'N/a', '2016-05-21 11:05:18', '2016-05-18 09:38:28', 'Những bãi biển trải dài với bờ cát trắng cùng cánh rừng bạt ngàn, hang động kỳ vỹ là điều mà Quốc đảo sư tử không có được như Việt Nam.', 'http://img.f35.dulich.vnecdn.net/2016/05/18/Anh-8732-1463537219_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/singapore-ghen-ty-voi-thien-nhien-viet-nam-3404809.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('165', 'Sư tử rượt đuổi trâu rừng trước mặt du khách', 'N/a', '2016-05-21 11:05:18', '2016-05-18 08:30:26', 'Nhóm du khách được một phen hú hồn khi lạc vào đường chạy của hai con sư tử đang rượt đuổi một đàn trâu.', 'http://img.f35.dulich.vnecdn.net/2016/05/18/lionchase-1463531918_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/su-tu-ruot-duoi-trau-rung-truoc-mat-du-khach-3404738.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('166', 'Quán Pad Thai được mệnh danh ngon nhất Thái Lan', 'N/a', '2016-05-21 11:05:18', '2016-05-18 07:30:00', 'Ở Bangkok du khách không chỉ được thưởng thức món Pad Thai ngon nhất mà còn uống kèm với loại nước cam có hương vị không chê vào đâu được.', 'http://img.f34.dulich.vnecdn.net/2016/05/11/2-4241-1462940999_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/bangkok/quan-pad-thai-duoc-menh-danh-ngon-nhat-thai-lan-3401137.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('167', '10 \'luật chơi khắt khe\' của quán sushi ở Tokyo', 'N/a', '2016-05-21 11:05:18', '2016-05-17 20:00:00', 'Ăn sushi khi còn ấm bằng tay, vừa ăn vừa uống rượu sake... là những nguyên tắc bạn phải tuân thủ khi bước vào quán ăn đặc biệt này.', 'http://img.f36.dulich.vnecdn.net/2016/05/16/1a-6988-1463365913_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/tokyo/10-luat-choi-khat-khe-cua-quan-sushi-o-tokyo-3403574.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('168', 'Khách bắt gặp nhân viên khách sạn dùng cọ toilet rửa cốc', 'N/a', '2016-05-21 11:05:18', '2016-05-17 14:29:44', 'Khách sạn Trung Quốc vướng vào rắc rối sau khi một du khách bắt gặp nhân viên dọn dẹp sử dụng cọ toilet và chất tẩy nhà vệ sinh để rửa cốc.', 'http://img.f36.dulich.vnecdn.net/2016/05/17/toilet-brush-7887-1463467394_1463470336_1463470974_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/khach-bat-gap-nhan-vien-khach-san-dung-co-toilet-rua-coc-3404399.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('169', 'Sao \'High School Musical\' bị phạt 1.000 USD do khắc lên đá', 'N/a', '2016-05-21 11:05:18', '2016-05-17 19:00:00', 'Tội làm hư hại tài sản tự nhiên ở Mỹ có thể khiến du khách đối mặt 6 tháng tù và 5.000 USD tiền phạt.', 'http://img.f33.dulich.vnecdn.net/2016/05/17/vanessa-hudgens-2153-1463479336_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/sao-high-school-musical-bi-phat-1-000-usd-do-khac-len-da-3404624.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('170', 'Đà Nẵng thành lập Sở Du lịch', 'N/a', '2016-05-21 11:05:18', '2016-05-17 16:55:54', 'UBND TP Đà Nẵng thành lập Sở Du lịch trên cơ sở chia tách từ Sở Văn hóa Thể Thao và Du lịch, nhằm quản lý tốt hơn ngành du lịch vốn đang phát triển.', 'http://img.f34.dulich.vnecdn.net/2016/05/17/biendn-1463471322_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/da-nang-thanh-lap-so-du-lich-3404441.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('171', 'Mù Cang Chải mùa nước đổ đẹp như tranh', 'N/a', '2016-05-21 11:05:18', '2016-05-17 16:16:28', 'Tháng 5, vùng rẻo cao của Yên Bái hiện lên với màu của đất nâu hòa quyện cùng trời xanh và mặt nước óng ánh dưới nắng vàng rực rỡ.', 'http://img.f36.dulich.vnecdn.net/2016/05/17/5-1463471024_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/mu-cang-chai-mua-nuoc-do-dep-nhu-tranh-3404471.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('172', '10 trải nghiệm không nên bỏ lỡ ở Quy Nhơn hè này', 'N/a', '2016-05-21 11:05:18', '2016-05-17 13:44:21', 'Ghé bãi đá trứng, đi qua cây cầu vượt biển dài nhất Việt Nam, cắm trại trên bãi Kỳ Co, thưởng thức bánh hỏi lòng heo là những trải nghiệm hút khách ở Quy Nhơn, Bình Định.', 'http://img.f36.dulich.vnecdn.net/2016/05/16/5-1463367468_180x108.jpg', 'http://dulich.vnexpress.net/photo/tu-van/10-trai-nghiem-khong-nen-bo-lo-o-quy-nhon-he-nay-3403606.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('173', '5 món ngon \'phải thử\' ở TP HCM', 'N/a', '2016-05-21 11:05:18', '2016-05-17 10:31:00', 'Trang du lịch Goasean giới thiệu một số đặc sản Việt Nam đi kèm những địa chỉ tin cậy để du khách đến TP HCM có thể tự mình thưởng thức. ', 'http://img.f36.dulich.vnecdn.net/2016/05/16/12070629112996578703350014793155n-1463384776_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ho-chi-minh/5-mon-ngon-phai-thu-o-tp-hcm-3403530.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('174', 'Các khách sạn tổng thống Obama từng ở khi công du nước ngoài', 'N/a', '2016-05-21 11:05:18', '2016-05-17 07:30:00', 'Nơi nghỉ của tổng thống Mỹ Obama trong mỗi chuyến công du nước ngoài thường được giữ kín trước khi ông tới nơi nhằm đảm bảo yếu tố an ninh.', 'http://img.f35.dulich.vnecdn.net/2016/05/16/besthotels869-3332-1463392996_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/cac-khach-san-tong-thong-obama-tung-o-khi-cong-du-nuoc-ngoai-3403988.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('175', 'Giáo sư trẻ nhất Việt Nam và 5 năm loay hoay kiếm tiền', 'N/a', '2016-05-21 11:05:43', '2016-05-21 01:00:00', '5 năm đầu khi từ Hà Lan về Việt Nam, giáo sư Hiếu không xin được đề tài nào nên từng có ý định bỏ nghiên cứu đi kinh doanh. ', 'http://img.f30.vnecdn.net/2016/05/19/hieu4-1463646748_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/giao-su-tre-nhat-viet-nam-va-5-nam-loay-hoay-kiem-tien-3405766.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('176', 'Phẫu thuật lấy điện thoại nhỏ nhất thế giới bị nuốt vào bụng', 'N/a', '2016-05-21 11:05:43', '2016-05-21 09:04:46', 'Các bác sĩ tại bệnh viện Adelaide và Meath ở Dublin, Ireland, tiến hành ca phẫu thuật hy hữu cho một tù nhân bị nôn liên tục sau khi nuốt chiếc điện thoại nhỏ xíu vào bụng.', 'http://img.f30.vnecdn.net/2016/05/21/Anh3-1463791534_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/chuyen-la/phau-thuat-lay-dien-thoai-nho-nhat-the-gioi-bi-nuot-vao-bung-3406598.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('177', 'Ngôi nhà UFO trôi nổi trên đại dương', 'N/a', '2016-05-21 11:05:43', '2016-05-21 07:00:00', 'Ngôi nhà UFO trong tương lai có thể giúp con người du lịch trên biển và ngắm nhìn các sinh vật sống trong lòng đại dương.', 'http://img.f30.vnecdn.net/2016/05/20/1-1463732807_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/san-pham-cong-nghe/ngoi-nha-ufo-troi-noi-tren-dai-duong-3406393.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('178', 'Đây có phải hoa ưu đàm không?', 'N/a', '2016-05-21 11:05:43', '2016-05-21 06:00:00', 'Xin hỏi đây có phải hoa ưu đàm không? (Hoàng)', 'http://img.f32.vnecdn.net/2016/05/20/img20160517084055hdr1463643873-1463738392_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/day-co-phai-hoa-uu-dam-khong-3406487.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('179', 'Các mảng kiến tạo và sự trôi dạt lục địa trên Trái Đất', 'N/a', '2016-05-21 11:05:43', '2016-05-20 20:00:00', 'Quá trình tương tác giữa các mảng kiến tạo, một phần vỏ Trái Đất, là nguyên nhân tạo ra những ngọn núi lửa, động đất và nhiều hiện tượng địa chất khác.', 'http://img.f31.vnecdn.net/2016/05/20/cac-mang-kien-tao-va-su-troi-dat-luc-dia-tren-trai-dat-1463733978_180x108.png', 'http://vnexpress.net/infographics/moi-truong/cac-mang-kien-tao-va-su-troi-dat-luc-dia-tren-trai-dat-3406408.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('180', 'Thụy Điển tính dời cả thành phố vì nguy cơ sụt đất', 'N/a', '2016-05-21 11:05:43', '2016-05-20 19:00:00', 'Trước nguy cơ đổ sụp xuống mỏ quặng bên dưới, chính quyền thành phố Kiruna ở Thụy Điển lên phương án di chuyển toàn bộ cơ sở hạ tầng và dân cư tới vị trí mới.', 'http://img.f29.vnecdn.net/2016/05/20/VNECity1-1463726662_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/thuy-dien-tinh-doi-ca-thanh-pho-vi-nguy-co-sut-dat-3406239.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('181', 'Tại sao hươu cao cổ có chiếc cổ dài', 'N/a', '2016-05-21 11:05:43', '2016-05-20 16:58:08', 'Những thay đổi về mặt di truyền giúp hươu cao cổ phát triển chiếc cổ dài hơn rất nhiều so với tổ tiên của chúng.', 'http://img.f29.vnecdn.net/2016/05/20/1-1463735138_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/tai-sao-huou-cao-co-co-chiec-co-dai-3406435.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('182', 'Trăng xanh và Hỏa tinh cùng chiếu sáng bầu trời đêm mai', 'N/a', '2016-05-21 11:05:43', '2016-05-20 15:26:18', 'Trong hai ngày 21 - 22/5, người yêu thiên văn trên khắp thế giới không chỉ có cơ hội theo dõi trăng tròn màu xanh hiếm gặp mà còn có thể quan sát Hỏa tinh ở vị trí gần Trái Đất nhất.', 'http://img.f30.vnecdn.net/2016/05/20/collagephotocat-1463731913_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trang-xanh-va-hoa-tinh-cung-chieu-sang-bau-troi-dem-mai-3406241.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('183', 'Phát hiện loài mới ở Lâm Đồng', 'N/a', '2016-05-21 11:05:43', '2016-05-20 16:05:37', 'Các nhà khoa học Bảo tàng thiên nhiên Việt Nam vừa công bố loài thực vật mới được phát hiện ở Lâm Đồng.', 'http://img.f30.vnecdn.net/2016/05/20/thucvat-1463717627_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/phat-hien-loai-moi-o-lam-dong-3406215.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('184', '5 nguyên nhân phổ biến nhất dẫn đến thảm họa máy bay rơi', 'N/a', '2016-05-21 11:05:43', '2016-05-20 11:54:08', 'Lỗi do phi công và hỏng hóc máy móc là những nguyên nhân hàng đầu dẫn đến tai nạn máy bay, xếp trên cả hoạt động khủng bố và thời tiết xấu.', 'http://img.f31.vnecdn.net/2016/05/20/VNEAirplane1Pinterest-1463717626_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/5-nguyen-nhan-pho-bien-nhat-dan-den-tham-hoa-may-bay-roi-3406049.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('185', 'Phát hiện kho báu trong tàu La Mã đắm 1.600 năm trước', 'N/a', '2016-05-21 11:05:43', '2016-05-20 10:41:29', 'Nhóm thợ lặn Israel công bố tìm thấy kho báu với nhiều tác phẩm nghệ thuật trong con tàu La Mã đắm cách đây 1.600 năm.', 'http://img.f30.vnecdn.net/2016/05/20/taudam-1463711967_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/chuyen-la/phat-hien-kho-bau-trong-tau-la-ma-dam-1-600-nam-truoc-3405787.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('186', 'Siêu sóng thần cao 50 m có thể từng càn quét sao Hỏa', 'N/a', '2016-05-21 11:05:43', '2016-05-20 10:17:24', 'Những cơn sóng thần với độ cao 50 m có thể đổ bộ vào vùng đất liền trên sao Hỏa cách đây khoảng 3,4 triệu năm sau hai lần tiểu hành tinh va chạm.', 'http://img.f31.vnecdn.net/2016/05/20/VNEMars1-1463713511_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/sieu-song-than-cao-50-m-co-the-tung-can-quet-sao-hoa-3405993.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('187', 'Đây là con gì?', 'N/a', '2016-05-21 11:05:43', '2016-05-20 08:01:50', 'Lần đầu tiên mình nhìn thấy con này, nhìn giống con bật gạo nhưng nó rất lạ vì có 9 cái râu hai bên và đụng vào thì nó giả chết. Xin hỏi đây là con gì? (Nguyễn Viết Nam)', 'http://img.f30.vnecdn.net/2016/05/20/201605192135391463669881-1463705363_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/day-la-con-gi-3405977.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('188', 'Xoáy nước nhân tạo khổng lồ nuốt chửng cả hòn đảo', 'N/a', '2016-05-21 11:05:43', '2016-05-20 07:00:00', 'Xoáy nước hình thành do một sơ suất nhỏ của con người nuốt chửng cả hòn đảo rộng gần 300.000 m2, hai giàn khoan và nhiều sà lan, đồng thời đảo dòng con kênh dẫn ra vịnh Mexico.', 'http://img.f32.vnecdn.net/2016/05/19/VNELake1-1463644214_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/xoay-nuoc-nhan-tao-khong-lo-nuot-chung-ca-hon-dao-3404884.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('189', 'Nấm thần kỳ giúp trị chứng trầm cảm ở con người', 'N/a', '2016-05-21 11:05:43', '2016-05-19 22:00:00', 'Các nhà khoa học phát hiện một hợp chất gây ảo giác lấy từ nấm thần kỳ đem lại nhiều lợi ích chữa trị cho bệnh nhân mắc chứng trầm cảm.', 'http://img.f30.vnecdn.net/2016/05/19/VNEMagic1-1463647416_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/nam-than-ky-giup-tri-chung-tram-cam-o-con-nguoi-3405811.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('190', 'Hình vẽ lạ nghi là UFO trên tranh tường 400 năm tuổi', 'N/a', '2016-05-21 11:05:43', '2016-05-19 21:00:00', 'Du khách đến thăm nhà thờ cổ Transylvania, Romania đều ngạc nhiên trước vật thể lạ giống hệt đĩa bay trong bức tranh tường niên đại 4 thế kỷ.', 'http://img.f32.vnecdn.net/2016/05/19/VNEChurch1JPG-1463649960_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hinh-ve-la-nghi-la-ufo-tren-tranh-tuong-400-nam-tuoi-3404217.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('191', 'Đại dương sâu tới mức nào', 'N/a', '2016-05-21 11:05:43', '2016-05-19 20:00:00', 'Rãnh Mariana ở Thái Bình Dương, nơi được coi là sâu nhất dưới biển, có độ sâu 11.034 m.', 'http://img.f32.vnecdn.net/2016/05/19/CaptionPNG-1463651969_180x108.png', 'http://vnexpress.net/tin-tuc/khoa-hoc/dai-duong-sau-toi-muc-nao-3405880.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('192', 'Cầu lửa vụt sáng lọt ống kính máy quay trên xe cảnh sát', 'N/a', '2016-05-21 11:05:43', '2016-05-19 14:31:33', 'Một hạ sĩ cảnh sát theo dõi xe đi quá tốc độ ghi lại cảnh tượng quả cầu lửa vụt qua bầu trời từ máy quay gắn trên xe.', 'http://img.f31.vnecdn.net/2016/05/19/VNEMeteor-1463635093_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/cau-lua-vut-sang-lot-ong-kinh-may-quay-tren-xe-canh-sat-3405670.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('193', '\'Vườn ảo\' tự tìm chỗ sống lý tưởng cho cây xanh', 'N/a', '2016-05-21 11:05:43', '2016-05-19 14:18:50', 'Các nhà khoa học Anh thiết kế một hệ thống \"vườn ảo\" giúp cây xanh trồng trong đó tự động di chuyển tới nơi có điều kiện sống lý tưởng.', 'http://img.f29.vnecdn.net/2016/05/17/GardenBall1024-1463476736_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/san-pham-cong-nghe/vuon-ao-tu-tim-cho-song-ly-tuong-cho-cay-xanh-3404558.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('194', 'Bảo tàng vũ trụ quốc gia sẽ mở cửa năm 2017', 'N/a', '2016-05-21 11:05:43', '2016-05-19 14:15:21', 'Không chỉ trưng bày hiện vật, bảo tàng còn có nhiều loại hình tương tác và dẫn dắt người xem tự khám phá. Một công ty của Nhật đang thiết kế công trình, dự kiến đi vào hoạt động cuối năm 2017.', 'http://img.f29.vnecdn.net/2016/05/19/baotang1-1463640816_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/bao-tang-vu-tru-quoc-gia-se-mo-cua-nam-2017-3405654.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('195', 'Lịch sử vũ trụ nén lại trong một năm', 'N/a', '2016-05-21 11:05:43', '2016-05-19 07:41:00', 'Nếu có thể nén toàn bộ lịch sử gần 14 tỷ năm của vũ trụ vào thời gian một năm, con người hiện đại (Homo sapiens) sẽ xuất hiện vào ngày cuối cùng trong năm ở thời điểm 23 giờ 53 phút.', 'http://img.f30.vnecdn.net/2016/05/17/lich-su-vu-tru-nen-lai-trong-mot-nam-1463476271_180x108.jpg', 'http://vnexpress.net/infographics/khoa-hoc/lich-su-vu-tru-nen-lai-trong-mot-nam-3404568.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('196', '7 lý do khiến bạn bị muỗi đốt nhiều hơn', 'N/a', '2016-05-21 11:05:43', '2016-05-19 12:32:45', 'Nhóm máu, hoạt động thể chất, uống bia nằm trong số những nguyên nhân khiến bạn bị muỗi đốt nhiều hơn so với những người khác.', 'http://img.f29.vnecdn.net/2016/05/19/7-ly-do-khien-ban-bi-muoi-dot-nhieu-hon-1463619955_180x108.jpg', 'http://vnexpress.net/photo/khoa-hoc/7-ly-do-khien-ban-bi-muoi-dot-nhieu-hon-3405395.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('197', 'Khám phá tòa nhà cao nhất trên Trái Đất', 'N/a', '2016-05-21 11:05:43', '2016-05-19 09:05:37', 'Tòa nhà Burj Khalifa tại Dubai cao 828 m, bao gồm 163 tầng, giữ vị trí tòa nhà cao nhất thế giới từ khi khánh thành vào năm 2010 đến nay.', 'http://img.f31.vnecdn.net/2016/05/18/kham-pha-toa-nha-cao-nhat-tren-trai-dat-hien-nay-1463567360_180x108.jpg', 'http://vnexpress.net/photo/khoa-hoc/kham-pha-toa-nha-cao-nhat-tren-trai-dat-3405277.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('198', 'Sân bay ma khổng lồ nơi \'chim sắt\' ngủ quên', 'N/a', '2016-05-21 11:05:43', '2016-05-19 08:19:00', 'Thay vì hoạt động tấp nập như một sân bay quốc tế, Teruel giống nghĩa địa \"chim sắt\" hơn, với từng hàng dài máy bay nằm im lìm giữa quang cảnh khô cằn.', 'http://img.f31.vnecdn.net/2016/05/17/VNE-Airport-6-1463460064_180x108.jpg', 'http://vnexpress.net/photo/khoa-hoc/san-bay-ma-khong-lo-noi-chim-sat-ngu-quen-3404386.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('199', 'Tuổi thọ của vũ trụ được tính như thế nào?', 'N/a', '2016-05-21 11:05:43', '2016-05-19 08:03:13', 'Các nhà khoa học kết luận vũ trụ có niên đại gần 14 tỷ năm. Xin hỏi phương pháp nào được dùng để tính tuổi thọ vũ trụ? (Hoàng Anh)', 'http://img.f31.vnecdn.net/2016/05/19/WDF2705073-1463618989_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/tuoi-tho-cua-vu-tru-duoc-tinh-nhu-the-nao-3405367.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('225', 'Các cách phát âm chữ N trong tiếng Anh', 'N/a', '2016-05-21 11:06:19', '2016-05-21 02:00:00', 'Thông thường người ta nghĩ chữ N chỉ có thể phát âm là /n/, nhưng trong thực tế thì không hẳn như vậy, khi mà N có thể được phát âm là /ŋ/ và cũng có thể câm.', 'http://img.f32.vnecdn.net/2016/05/20/chuN-1463740552_1463740587_1463742140_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-tieng-anh/cac-cach-phat-am-chu-n-trong-tieng-anh-3406514.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('226', 'Ông bố 8x vừa bế con vừa học đại học', 'N/a', '2016-05-21 11:06:19', '2016-05-21 07:58:00', 'Sau một ngày làm việc, về tới nhà khi trời đã chập choạng tối, anh Lân vội vã ăn cơm rồi giỗ con ngủ và tranh thủ học bài.', 'http://img.f30.vnecdn.net/2016/05/21/13278133-1728531957361578-1681-2494-6113-1463793400_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/ong-bo-8x-vua-be-con-vua-hoc-dai-hoc-3405750.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('227', 'Học sinh chuyên Lê Hồng Phong bật khóc trong lễ chia tay', 'N/a', '2016-05-21 11:06:19', '2016-05-20 18:39:37', 'Hàng trăm học sinh khối 12 trường THPT chuyên Lê Hồng Phong (TP HCM) đã òa khóc trong buổi lễ chia tay cuối năm học, sáng 20/5.', 'http://img.f29.vnecdn.net/2016/05/20/hoc-sinh-chuyen-le-hong-phong-bat-khoc-trong-le-chia-tay-1463734117_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/hoc-sinh-chuyen-le-hong-phong-bat-khoc-trong-le-chia-tay-3406328.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('228', 'Cơ sở đào tạo tiến sĩ phải công khai toàn văn luận án trên website', 'N/a', '2016-05-21 11:06:19', '2016-05-20 14:55:26', 'Bộ Giáo dục yêu cầu các cơ sở đào tạo tiến sĩ công khai danh mục đề tài luận án tiến sĩ đang nghiên cứu, công khai toàn văn luận án trên trang thông tin điện tử của mình.', 'http://img.f30.vnecdn.net/2016/05/20/tiensi-1463729715_1463729733_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/co-so-dao-tao-tien-si-phai-cong-khai-toan-van-luan-an-tren-website-3406338.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('229', 'Tỷ lệ chọi vào lớp 10 không chuyên ở Hà Nội', 'N/a', '2016-05-21 11:06:19', '2016-05-20 13:06:44', 'Có 1.180 học sinh đăng ký nguyện vọng 1 vào trường THPT Trung Văn trong khi chỉ tiêu 400, nghĩa là 3 học sinh mới có một em vào trường.', 'http://img.f31.vnecdn.net/2016/05/20/thituyenlop10597014604483-1463724050_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ty-le-choi-vao-lop-10-khong-chuyen-o-ha-noi-3406262.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('230', 'Sinh viên y khoa tri ân người hiến xác', 'N/a', '2016-05-21 11:06:19', '2016-05-20 01:16:06', 'Dọc hành lang xếp hoa cúc, trên cầu thang là những đài sen bằng giấy và hàng nghìn con hạc, 300 sinh viên y khoa nghiêm trang xếp hàng tri ân những người hiến xác.', 'http://img.f29.vnecdn.net/2016/05/19/hienxacc-1463662164_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/sinh-vien-y-khoa-tri-an-nguoi-hien-xac-3405924.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('231', 'Ba cha con ở Sài Gòn cùng thi vào đại học', 'N/a', '2016-05-21 11:06:19', '2016-05-20 00:02:00', 'Người cha đã ngoài 50 tuổi cùng hai cậu con trai sẽ cùng thi THPT quốc gia năm nay với ước mơ bước vào cánh cửa đại học.', 'http://img.f30.vnecdn.net/2016/05/18/1-1463568284_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ba-cha-con-o-sai-gon-cung-thi-vao-dai-hoc-3405102.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('232', 'Học mà không hỏi sẽ mất cơ hội giao tiếp và tiến bộ', 'N/a', '2016-05-21 11:06:19', '2016-05-20 00:01:00', 'Thiên tài Albert Eistein từng chia sẻ, không phải là vì ông quá thông minh mà nhờ kiên trì đặt câu hỏi lâu hơn.', 'http://img.f32.vnecdn.net/2016/05/20/3-1463705981_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/hoc-ma-khong-hoi-se-mat-co-hoi-giao-tiep-va-tien-bo-3405983.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('233', 'Cách phát âm chữ M trong tiếng Anh', 'N/a', '2016-05-21 11:06:19', '2016-05-20 00:00:00', 'Chữ M là một chữ dễ phát âm trong tiếng Anh. Vì cơ bản chữ M được phát âm là /m/.', 'http://img.f31.vnecdn.net/2016/05/19/chuM-1463649095_1463649113_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-tieng-anh/cach-phat-am-chu-m-trong-tieng-anh-3405829.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('234', 'Năm trường tham gia trao đổi giáo viên Việt - Australia', 'N/a', '2016-05-21 11:06:19', '2016-05-20 20:05:35', 'Các giáo viên tham gia chương trình nói tiếng Anh tốt, có kỹ năng vi tính và hứng thú với công nghệ thông tin.', 'http://img.f32.vnecdn.net/2016/05/20/3-OK-DSC03854-4973-1463744602_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/nam-truong-tham-gia-trao-doi-giao-vien-viet-australia-3406529.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('235', 'Chàng trai chuyên Toán giành huy chương vàng Tin học', 'N/a', '2016-05-21 11:06:19', '2016-05-19 15:54:05', 'Phan Đức Nhật Minh đến với Tin học không phải vì đam mê ban đầu như nhiều bạn trẻ khác mà thấy \"hay hay\" nên theo học và vào đội tuyển.', 'http://img.f30.vnecdn.net/2016/05/19/nhatminh-1463644298_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/chang-trai-chuyen-toan-gianh-huy-chuong-vang-tin-hoc-3405738.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('236', 'Ông Đinh La Thăng: \'Cần loại bỏ tư tưởng ỷ lại bằng cấp\'', 'N/a', '2016-05-21 11:06:19', '2016-05-19 14:07:14', '\"Cần loại bỏ tư tưởng ỷ lại bằng cấp. Không phải những người có bằng cấp cao là những nhân lực chất lượng cao\", Bí thư Thành ủy TP HCM', 'http://img.f31.vnecdn.net/2016/05/19/bitho-1463639601_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ong-dinh-la-thang-can-loai-bo-tu-tuong-y-lai-bang-cap-3405666.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('237', 'Nữ sinh bị đánh hội đồng vì chế ảnh thần tượng', 'N/a', '2016-05-21 11:06:19', '2016-05-19 13:56:00', 'Bực tức vì ảnh thần tượng bị chế, nhóm học sinh nam nữ đã đánh dã man một nữ sinh lớp 8 được cho là tác giả.', 'http://img.f29.vnecdn.net/2016/05/16/13244459271289843215994897270356nmp4snapshot000820160515232644-1463380389_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/nu-sinh-bi-danh-hoi-dong-vi-che-anh-than-tuong-3403800.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('238', 'Sinh viên đại học ở tuổi 15', 'N/a', '2016-05-21 11:06:19', '2016-05-19 09:45:20', 'Dù chưa học hết bậc THPT nhưng nhiều em đã trở thành sinh viên ở độ tuổi trăng tròn.', 'http://img.f30.vnecdn.net/2016/05/19/12049572-764536783691990-14716-5501-2295-1463624712_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/sinh-vien-dai-hoc-o-tuoi-15-3405373.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('239', 'Đáp án bài toán \'chú lùn lấy cắp vàng\'', 'N/a', '2016-05-21 11:06:19', '2016-05-19 08:25:05', 'Các câu nói là hoán vị vòng quanh, do đó vai trò của Anne, Bert, Chris và Dirk là hoàn toàn như nhau.', 'http://img.f30.vnecdn.net/2016/05/18/chulun-1463536461_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/dap-an-bai-toan-chu-lun-lay-cap-vang-3404816.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('240', 'Giải nhanh câu điểm 9 bằng phương pháp hàm số sử dụng kỹ thuật điểm uốn', 'N/a', '2016-05-21 11:06:19', '2016-05-19 08:23:13', 'Phương pháp hàm số sử dụng kỹ thuật điểm uốn do thầy Nguyễn Bá Tuấn phát triển trên nền tảng kiến thức về hàm số, giúp học sinh có cách đặt để đưa về phương trình hàm số nhanh hơn và có định hướng rõ ràng.', 'http://img.f29.vnecdn.net/2016/05/18/giai-nhanh-cau-diem-9-bang-phuong-phap-ham-so-su-dung-ki-thuat-diem-uon-1463537832_180x108.jpg', 'http://vnexpress.net/photo/tuyen-sinh/giai-nhanh-cau-diem-9-bang-phuong-phap-ham-so-su-dung-ky-thuat-diem-uon-3404838.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('241', 'Bài toán thừa - thiếu giáo viên THPT', 'N/a', '2016-05-21 11:06:19', '2016-05-19 09:03:42', 'Theo tính toán của độc giả Nguyễn Hoàng Chương, cán bộ quản lý một trường THPT, đến năm 2020 mỗi trường THPT bổ sung 9 giáo viên hướng nghiệp, dạy kỹ năng sống, tâm lý học đường, thì cả nước cần thêm 24.900 giáo viên.', 'http://img.f30.vnecdn.net/2016/05/19/giaovien-1463622794_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bai-toan-thua-thieu-giao-vien-thpt-3405445.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('242', 'Trường trả lại 120 triệu đồng cho phụ huynh vì thu sai', 'N/a', '2016-05-21 11:06:19', '2016-05-18 20:59:22', 'Trong số tiền đóng nộp đầu năm mà trường Tiểu học Thạch Hạ (Hà Tĩnh) phải hoàn trả, có một số khoản bất hợp lý như thu gộp tiền bàn ghế bán trú nằm buổi trưa của học sinh khối lớp 1 trong 5 năm, tiền học buổi hai.', 'http://img.f32.vnecdn.net/2016/05/18/IMG3467JPG-1463571642_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/truong-tra-lai-120-trieu-dong-cho-phu-huynh-vi-thu-sai-3405294.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('243', 'Viện hàn lâm Khoa học Xã hội có Chủ tịch mới', 'N/a', '2016-05-21 11:06:19', '2016-05-18 18:14:50', 'Viện trưởng Viện Nghiên cứu Trung Quốc Nguyễn Quang Thuấn được bổ nhiệm giữ chức Chủ tịch Viện hàn lâm Khoa học Xã hội Việt Nam ', 'http://img.f31.vnecdn.net/2016/05/18/PTT-Vu-Duc-Dam-2984-1463565591_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/vien-han-lam-khoa-hoc-xa-hoi-co-chu-tich-moi-3405254.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('244', 'Hai cách phát âm chữ L trong tiếng Anh', 'N/a', '2016-05-21 11:06:19', '2016-05-18 09:03:10', 'Chữ L tưởng như chỉ có một cách phát âm duy nhất là /l/, nhưng sự thực là có 2 cách phát âm.', 'http://img.f29.vnecdn.net/2016/05/18/chuL-1463535980_1463536622_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-tieng-anh/hai-cach-phat-am-chu-l-trong-tieng-anh-3404807.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('245', 'Trăn trở của một phụ huynh trước vấn nạn dạy thêm', 'N/a', '2016-05-21 11:06:19', '2016-05-18 03:00:00', '\"Học thêm tràn lan, không đúng cách đang tàn phá đi nền tri thức của thế hệ trẻ, làm hỏng thế hệ trồng người, làm tha hóa tâm đức của người cầm phấn khi đặt nặng vấn đề lợi nhuận\", phụ huynh Minh Vấn nêu quan điểm.', 'http://img.f32.vnecdn.net/2016/05/17/hocthemtp2f401-1463484471_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/tran-tro-cua-mot-phu-huynh-truoc-van-nan-day-them-3404664.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('246', 'Những bóng hồng mê lập trình', 'N/a', '2016-05-21 11:06:19', '2016-05-18 09:50:00', 'Mong muốn tìm được công việc tốt hơn nhưng vẫn chủ động được thời gian nên nhiều chị em chọn học lập trình online.', 'http://img.f31.vnecdn.net/2016/05/18/Y2K0487-1463537313_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/nhung-bong-hong-me-lap-trinh-3404759.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('247', 'Bài toán \'chú lùn lấy cắp vàng\'', 'N/a', '2016-05-21 11:06:19', '2016-05-18 09:26:33', 'Một trong bốn chú lùn Anne, Bert, Chris và Dirk đã lấy cắp vàng của vua.', 'http://img.f32.vnecdn.net/2016/05/18/chulun-1463536282_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bai-toan-chu-lun-lay-cap-vang-3404812.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('248', 'Năm 2020 Việt Nam thừa 70.000 giáo viên', 'N/a', '2016-05-21 11:06:19', '2016-05-18 00:12:00', '\"Cho dù tăng số học sinh trên giáo viên bình quân lên tương đương các nước công nghiệp phát triển, thì tại thời điểm năm 2020 hệ thống cũng không thể tuyển dụng hết số giáo viên mới tốt nghiệp ra trường\", PGS Bùi Văn Quân nhận định.', 'http://img.f30.vnecdn.net/2016/05/17/Bui-Van-Quan-9973-1463494393_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/nam-2020-viet-nam-thua-70-000-giao-vien-3404557.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('249', 'Gareth Bale đã \'đâm sau lưng\' Carlo Ancelotti như thế nào', 'N/a', '2016-05-23 15:04:15', '2016-05-21 10:16:17', 'Trong phần tiếp theo cuốn tự truyện vừa xuất bản, HLV người Italy kể thêm về việc Roman Abramovich đối xử ra sao và Gareth Bale góp phần khiến ông mất việc ở Real Madrid sau đó như thế nào.', 'http://img.f2.thethao.vnecdn.net/2016/05/21/Bale2-4750-1463795792_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/gareth-bale-da-dam-sau-lung-carlo-ancelotti-nhu-the-nao-3406645.html', '6', '5', '1', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('250', 'Chung kết các Cup Quốc gia châu Âu: Vui buồn ngày chia tay', 'N/a', '2016-05-21 11:08:27', '2016-05-21 06:28:27', 'Pep Guardiola, Zlatan Ibrahimovic, Mats Hummels, và có thể cả Louis Van Gaal, đều muốn kết thúc công việc hiện tại trong vinh quang.', 'http://img.f2.thethao.vnecdn.net/2016/05/21/chung-ket-cac-cup-quoc-gia-chau-au-vui-buon-ngay-chia-tay-1463786892_180x108.jpg', 'http://thethao.vnexpress.net/photo/bong-da/chung-ket-cac-cup-quoc-gia-chau-au-vui-buon-ngay-chia-tay-3406576.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('251', 'VFF cân nhắc chuyện loại Đình Luật khỏi tuyển Việt Nam', 'N/a', '2016-05-21 11:08:27', '2016-05-21 10:10:36', 'Thành viên lớn tuổi nhất của tuyển Việt Nam có hành vi phi thể thao trong trận đấu giữa Quảng Nam và Bình Dương, bị treo giò ba trận và phải nộp phạt 15 triệu đồng.', 'http://img.f1.thethao.vnecdn.net/2016/05/21/a1-1636-1463797043_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/vff-can-nhac-chuyen-loai-dinh-luat-khoi-tuyen-viet-nam-3406690.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('252', 'Kaka lĩnh lương cao nhất MLS, gấp bảy lần Lee Nguyễn', 'N/a', '2016-05-21 11:08:27', '2016-05-21 06:07:06', 'Ngôi sao tiền vệ người Brazil giữ vị trí số một trên bảng lương giải bóng đá nhà nghề Mỹ, trong màu áo đội Orlando City.', 'http://img.f1.thethao.vnecdn.net/2016/05/21/Kaka-2-3093-1463785335_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/kaka-linh-luong-cao-nhat-mls-gap-bay-lan-lee-nguyen-3406582.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('253', 'Đam mê sự nghiệp, Ronaldo từng bỏ mặc Irina ở nhà đến 3h sáng', 'N/a', '2016-05-21 11:08:27', '2016-05-21 10:14:23', 'HLV Carlo Ancelotti vừa tiết lộ một bằng chứng cho thấy Cristiano Ronaldo là cầu thủ có tinh thần chuyên nghiệp rất cao.', 'http://img.f1.thethao.vnecdn.net/2016/05/21/ro-5378-1463796853_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/dam-me-su-nghiep-ronaldo-tung-bo-mac-irina-o-nha-den-3h-sang-3406683.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('254', 'Messi, Neymar chê cười kỹ thuật chạm bóng của Suarez', 'N/a', '2016-05-21 11:08:27', '2016-05-21 10:11:46', 'Vua phá lưới La Liga Luis Suarez thừa nhận anh thường trở thành mục tiêu châm chọc của hai đồng đội thân thiết vì thói quen chạm bóng nhiều lần.', 'http://img.f3.thethao.vnecdn.net/2016/05/21/mes-5999-1463800175_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/messi-neymar-che-cuoi-ky-thuat-cham-bong-cua-suarez-3406728.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('255', 'Cựu danh thủ Barca: \'Lý trí nghiêng về Real, tình cảm ngả theo Atletico\'', 'N/a', '2016-05-21 11:08:27', '2016-05-21 08:58:29', 'Miguel Nadal cho rằng Real sẽ có nhiều lợi thế hơn Atletico trong trận chung kết Champions League.', 'http://img.f1.thethao.vnecdn.net/2016/05/21/miguel-5907-1463795428_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/champions-league/cuu-danh-thu-barca-ly-tri-nghieng-ve-real-tinh-cam-nga-theo-atletico-3406630.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('256', 'Bayern và Dortmund được dỡ lệnh cấm ăn mừng bằng bia', 'N/a', '2016-05-21 11:08:27', '2016-05-21 07:25:09', 'Cuộc so tài giữa hai kình địch bóng đá Đức tại chung kết Cup quốc gia sẽ kết thúc bằng màn tắm bia như thường lệ sau khi lệnh cấm bị hủy bỏ.', 'http://img.f1.thethao.vnecdn.net/2016/05/21/Untitled-1-8798-1463789812_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da/bayern-va-dortmund-duoc-do-lenh-cam-an-mung-bang-bia-3406592.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('257', 'Van Gaal có duyên thắng các trận chung kết đấu Cup', 'N/a', '2016-05-21 11:08:27', '2016-05-21 07:07:10', 'HLV người Hà Lan đang thất bại ở Ngoại hạng Anh, nhưng ông có thành tích rất tốt ở các giải Cup trước khi cùng Man Utd tranh Cup FA với Crystal Palace hôm nay 21/5.', 'http://img.f2.thethao.vnecdn.net/2016/05/21/vg-2856-1463789193_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/van-gaal-co-duyen-thang-cac-tran-chung-ket-dau-cup-3406588.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('258', 'Arsenal sắp giật bom tấn với đội trưởng Monchengladbach', 'N/a', '2016-05-21 11:08:27', '2016-05-21 00:38:04', 'Granit Xhaka sẽ bay tới London trong tuần này, kiểm tra y tế và hoàn tất hợp đồng đầu quân cho Arsenal.', 'http://img.f3.thethao.vnecdn.net/2016/05/21/328FDF2B00000578-3600986-Borus-2334-9558-1463765849_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/arsenal-sap-giat-bom-tan-voi-doi-truong-monchengladbach-3406575.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('259', 'HLV của Dortmund: \'Nước Đức sẽ phải nhớ Pep Guardiola\'', 'N/a', '2016-05-21 11:08:27', '2016-05-21 10:10:45', 'HLV của CLB Borussia Dortmund, Thomas Tuchel đánh giá việc Pep Guardiola bỏ Bayern Munich để tới Man City sẽ là một tổn thất lớn cho bóng đá Đức.', 'http://img.f2.thethao.vnecdn.net/2016/05/21/tuchel-4200-1463798266_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/hlv-cua-dortmund-nuoc-duc-se-phai-nho-pep-guardiola-3406706.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('260', 'Thái Sơn Nam vô địch futsal toàn quốc 2016', 'N/a', '2016-05-21 11:08:27', '2016-05-21 10:06:59', 'Đội bóng quy tụ hàng loạt tuyển thủ quốc gia đã lên ngôi vô địch giải futsal quốc gia 2016.', 'http://img.f2.thethao.vnecdn.net/2016/05/21/132328931022318437857869532345-7071-8660-1463798413_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/thai-son-nam-vo-dich-futsal-toan-quoc-2016-3406716.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('261', 'Danny Willett duy trì lợi thế dẫn đầu ở Irish Open', 'N/a', '2016-05-21 11:08:27', '2016-05-21 07:23:01', 'Golf thủ người Anh chia sẻ vị trí đầu bảng cùng Marc Warren nhưng khoảng cách với Rory McIlroy lúc này chỉ còn một gậy âm.', 'http://img.f3.thethao.vnecdn.net/2016/05/21/20160520T143247Z10675636-14637-4497-7971-1463788212_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/golf/danny-willett-duy-tri-loi-the-dan-dau-o-irish-open-3406587.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('262', 'Sân Muirfield mất quyền đăng cai British Open vì từ chối golf thủ nữ', 'N/a', '2016-05-21 11:08:27', '2016-05-21 07:20:58', 'Không chỉ bị tước vinh dự tổ chức giải golf lâu đời nhất thế giới, việc duy trì quy định không chấp nhận golf thủ nữ còn khiến Muirfield nhận nhiều chỉ trích.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/Untitled-5-3456-1463735400_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/golf/san-muirfield-mat-quyen-dang-cai-british-open-vi-tu-choi-golf-thu-nu-3406442.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('263', 'Djokovic có thể chạm trán Nadal ở bán kết Pháp Mở rộng 2016', 'N/a', '2016-05-21 11:08:27', '2016-05-20 19:22:40', 'Tay vợt số một và số năm thế giới nằm chung nhánh ở giải Grand Slam sân đất nện, diễn ra ở Paris từ ngày 22/5 đến 5/6. Nhánh còn lại có Andy Murray và nhà ĐKVĐ Wawrinka.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/1-1463746876_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/djokovic-co-the-cham-tran-nadal-o-ban-ket-phap-mo-rong-2016-3406538.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('264', 'Bí mật về những năm tháng của Carlo Ancelotti ở Chelsea', 'N/a', '2016-05-21 11:08:27', '2016-05-20 14:38:00', 'Câu chuyện hậu trường tại Chelsea được hé lộ qua cách kể chuyện bình tĩnh của Carlo Ancelotti trong cuốn sách mới được phát hành \"Quiet Leadership: Winning Hearts, Minds and Matches\" (Tạm dịch: Nhà lãnh đạo thầm lặng).', 'http://img.f3.thethao.vnecdn.net/2016/05/20/1-1463729638_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/bi-mat-ve-nhung-nam-thang-cua-carlo-ancelotti-o-chelsea-3406093.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('265', 'Van Gaal: \'Thà thắng 1-0 còn hơn chẳng được gì\'', 'N/a', '2016-05-21 11:08:27', '2016-05-20 16:48:20', 'Trước trận chung kết FA Cup, HLV Louis Van Gaal khẳng định muốn Man Utd thắng bằng mọi giá để chấm dứt giai đoạn ba năm trắng tay liên tục.', 'http://img.f2.thethao.vnecdn.net/2016/05/20/van-1802-1463735652_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/van-gaal-tha-thang-1-0-con-hon-chang-duoc-gi-3406419.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('266', 'Cựu danh thủ Ginola đột quỵ trong trận giao hữu', 'N/a', '2016-05-21 11:08:27', '2016-05-20 16:46:10', 'Tiền vệ một thời của Tottenham, David Ginola phải nhập viện khẩn cấp sau khi rơi vào tình trạng bất tỉnh.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/ginola-4603-1463736772_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/cuu-danh-thu-ginola-dot-quy-trong-tran-giao-huu-3406460.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('267', 'Antonio Reyes trở thành \'ông vua\' của Europa League', 'N/a', '2016-05-21 11:08:27', '2016-05-20 14:48:33', 'Với chức vô địch mới giành được cùng Sevilla, tiền vệ kỳ cựu Jose Antonio Reyes có tổng cộng năm danh hiệu Europa League trong sự nghiệp.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/reyes-6429-1463719089_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/antonio-reyes-tro-thanh-ong-vua-cua-europa-league-3406227.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('268', 'Casillas là thủ môn tệ nhất của Porto trong 15 năm', 'N/a', '2016-05-21 11:08:27', '2016-05-20 14:42:25', 'Thủ môn Iker Casillas vừa phải trải qua mùa giải đầu tiên với kết quả rất tệ trong màu áo FC Porto.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/casillas-8779-1463729835_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/casillas-la-thu-mon-te-nhat-cua-porto-trong-15-nam-3406327.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('269', '15 đội châu Âu giàu thành tích nhất trên đấu trường quốc tế', 'N/a', '2016-05-21 11:08:27', '2016-05-20 11:09:58', 'Nhờ Europa League 2016 mới giành được, Sevilla nhảy vọt qua Chelsea, Valencia và Anderlecht trong danh sách những đội châu Âu giàu thành tích nhất trên các đấu trường quốc tế.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/14-1463712192_180x108.jpg', 'http://thethao.vnexpress.net/photo/hinh-bong-da/15-doi-chau-au-giau-thanh-tich-nhat-tren-dau-truong-quoc-te-3406101.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('270', 'Cú sút kiểu bọ cạp của Di Stefano được tái hiện trên hình ảnh 3D', 'N/a', '2016-05-21 11:08:27', '2016-05-20 10:51:58', 'Một nhóm kỹ thuật viên vừa mô phỏng lại bàn thắng đẹp mắt của cố danh thủ Alfredo Di Stefano ghi cho Real năm 1957.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/di-1330-1463710471_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/cu-sut-kieu-bo-cap-cua-di-stefano-duoc-tai-hien-tren-hinh-anh-3d-3406054.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('271', 'Ibrahimovic dẫn đầu dàn sao tự do trên phiên chợ hè 2016', 'N/a', '2016-05-21 11:08:27', '2016-05-20 10:28:40', 'Nếu chịu khó nhặt nhạnh, các đội bóng hoàn toàn có thể kiếm được những sự bổ sung chất lượng cao mà không mất phí chuyển nhượng hè này.', 'http://img.f1.thethao.vnecdn.net/2016/05/20/ibrahimovic-dan-dau-dan-sao-tu-do-tren-phien-cho-he-2016-1463715034_180x108.jpg', 'http://thethao.vnexpress.net/photo/hinh-bong-da/ibrahimovic-dan-dau-dan-sao-tu-do-tren-phien-cho-he-2016-3405915.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('272', 'Costa và đội tuyển Tây Ban Nha: Sự lựa chọn sai lầm', 'N/a', '2016-05-21 11:08:27', '2016-05-20 10:25:41', 'Bị gạt khỏi danh sách chuẩn bị dự Euro 2016 là điều mà trung phong gốc Brazil có lẽ không thể ngờ tới hai năm trước, khi anh quyết tâm gạt quê nhà sang một bên để khoác áo tuyển Tây Ban Nha.', 'http://img.f4.thethao.vnecdn.net/2016/05/19/costa4-1463629357_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/costa-va-doi-tuyen-tay-ban-nha-su-lua-chon-sai-lam-3405325.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('273', 'Beckham: \'Zidane khổ vì phải đối đầu Barca\'', 'N/a', '2016-05-21 11:08:27', '2016-05-20 14:49:27', 'Danh thủ người Anh cho rằng Zidane là huấn luyện viên hoàn hảo cho Real dù nhiệm vụ trước mắt rất khó khăn vì phải chống lại đội bóng hay nhất lịch sử.', 'http://img.f2.thethao.vnecdn.net/2016/05/20/zi-4809-1463728694_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/beckham-zidane-kho-vi-phai-doi-dau-barca-3406321.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('274', 'Besiktas thuê du thuyền mừng chức vô địch trên biển', 'N/a', '2016-05-21 11:08:27', '2016-05-20 11:11:21', 'Để mừng chức vô địch Thổ Nhĩ Kỳ giành được lần đầu tiên trong bảy năm, Besiktas cùng các CĐV thuê một loạt du thuyền diễu hành tại eo biển Bosphorus.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/1-1463716093_180x108.jpg', 'http://thethao.vnexpress.net/photo/hau-truong/besiktas-thue-du-thuyen-mung-chuc-vo-dich-tren-bien-3406185.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('275', 'Van Gaal và Mourinho có thể cùng làm việc ở Man Utd', 'N/a', '2016-05-21 11:08:27', '2016-05-20 09:01:06', 'Đội chủ sân Old Trafford có thể mời Jose Mourinho về làm HLV và bổ nhiệm Louis Van Gaal vào vị trí Giám đốc bóng đá.', 'http://img.f2.thethao.vnecdn.net/2016/05/20/Untitled-3-1741-1463703099_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/van-gaal-va-mourinho-co-the-cung-lam-viec-o-man-utd-3405963.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('276', 'Huyền thoại Barca dẫm lên áo Real, nâng niu áo Atletico', 'N/a', '2016-05-21 11:08:27', '2016-05-20 08:55:24', 'Cựu danh thủ Hristo Stoichkov một lần nữa gây tranh cãi khi công kích Real Madrid, đại kình địch của đội bóng cũ Barca.', 'http://img.f4.thethao.vnecdn.net/2016/05/20/stoi-7040-1463705692_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/champions-league/huyen-thoai-barca-dam-len-ao-real-nang-niu-ao-atletico-3405974.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('277', 'Bangkok náo động vì màn rước Cup thứ hai của Leicester City', 'N/a', '2016-05-21 11:08:27', '2016-05-20 08:23:39', 'Hàng nghìn CĐV Thái Lan đổ ra đường chào đón những nhà tân vô địch Anh, đồng thời đi theo xe diễu hành khắp thủ đô Bangkok.', 'http://img.f4.thethao.vnecdn.net/2016/05/20/bangkok-nao-dong-vi-man-ruoc-cup-thu-hai-cua-leicester-city-1463713553_180x108.jpg', 'http://thethao.vnexpress.net/photo/hau-truong/bangkok-nao-dong-vi-man-ruoc-cup-thu-hai-cua-leicester-city-3405964.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('278', 'Việt Nam nằm cùng bảng Italy và Paraguay ở Futsal World Cup 2016', 'N/a', '2016-05-21 11:08:27', '2016-05-20 07:38:15', 'Đội tuyển Việt Nam nằm ở bảng C cùng ba đội khác, theo kết quả bốc thăm vừa diễn ra ở Colombia sáng nay 20/5 (theo giờ Hà Nội).', 'http://img.f3.thethao.vnecdn.net/2016/05/20/1-1463704659-4286-1463704661_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/viet-nam-nam-cung-bang-italy-va-paraguay-o-futsal-world-cup-2016-3405968.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('279', 'Tây Ban Nha năm thứ ba liên tiếp thống trị bóng đá châu Âu', 'N/a', '2016-05-21 11:08:27', '2016-05-20 07:01:43', 'Các đại diện La Liga mùa thứ ba liên tiếp giành cả Champions League lẫn Europa League. Từ sau năm 2001, các đại diện Tây Ban Nha cùng nhau thắng tới 13 trận chung kết của hai Cup châu Âu.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/TBN-5194-1463702470_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/tay-ban-nha-nam-thu-ba-lien-tiep-thong-tri-bong-da-chau-au-3405960.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('280', 'Messi đã nói gì với Aguero về Pep Guardiola', 'N/a', '2016-05-21 11:08:27', '2016-05-20 06:34:16', 'Hai ngôi sao người Argentina đã trao đổi với nhau về tân HLV của Man City.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/top-1463699135_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/messi-da-noi-gi-voi-aguero-ve-pep-guardiola-3405952.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('281', '22% số tay vợt chuyên nghiệp biết đồng nghiệp dùng chất kích thích', 'N/a', '2016-05-21 11:08:27', '2016-05-20 06:34:02', 'Hơn một phần năm số tay vợt chuyên nghiệp biết ít nhất một đồng nghiệp sử dụng doping. Khoảng một phần tư số tay vợt nghi ngờ có đối thủ từng buông trận đấu.', 'http://img.f1.thethao.vnecdn.net/2016/05/20/doping-4778-1463700815_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/22-so-tay-vot-chuyen-nghiep-biet-dong-nghiep-dung-chat-kich-thich-3405955.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('282', 'Danny Willett dẫn McIlroy hai gậy sau vòng một Irish Open', 'N/a', '2016-05-21 11:08:27', '2016-05-20 06:32:39', 'Nhà vô địch Masters 2016 ghi bảy gậy âm trong ngày thi đấu đầu tiên để chiếm vị trí dẫn đầu giải đấu tại Ireland.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/Untitled-1-5870-1463701065_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/golf/danny-willett-dan-mcilroy-hai-gay-sau-vong-mot-irish-open-3405957.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('283', 'Sếp quần vợt Nga dự đoán Sharapova không thi đấu trở lại', 'N/a', '2016-05-21 11:08:27', '2016-05-20 05:49:13', 'Chủ tịch Liên đoàn Quần vợt Nga Shamil Tarpishchev không tin Maria Sharapova sẽ thi đấu trở lại sau scandal chất cấm.', 'http://img.f4.thethao.vnecdn.net/2016/05/20/masha-1899-1463698142_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/sep-quan-vot-nga-du-doan-sharapova-khong-thi-dau-tro-lai-3405948.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('284', 'Võ sĩ Mexico chơi ngông, trả lại đai WBC', 'N/a', '2016-05-21 11:08:27', '2016-05-20 05:22:54', 'Saul Alvarez quyết định từ bỏ đai vô địch WBC hạng trung mà anh vừa bảo vệ thành công cách đây hai tuần vì không muốn chịu áp lực từ Hội đồng.  ', 'http://img.f3.thethao.vnecdn.net/2016/05/19/2016-05-08T040906Z-10568214-3824-1463649870_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-mon-khac/vo-si-mexico-choi-ngong-tra-lai-dai-wbc-3405845.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('285', 'Federer bỏ lỡ Roland Garros vì chấn thương lưng', 'N/a', '2016-05-21 11:08:27', '2016-05-20 01:18:26', 'Tay vợt người Thụy Sỹ không kịp bình phục chấn thương lưng và quyết định rút khỏi giải Grand Slam thứ hai trong năm.', 'http://img.f1.thethao.vnecdn.net/2016/05/20/Untitled-4-7454-1463681699_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/federer-bo-lo-roland-garros-vi-chan-thuong-lung-3405947.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('287', 'John Terry chịu thiệt gần 14 triệu đôla để ở lại Chelsea', 'N/a', '2016-05-21 11:08:27', '2016-05-20 06:02:02', 'Trung vệ 35 tuổi mới ký gia hạn hợp đồng để tiếp tục gắn bó với sân Stamford Bridge đến năm 2017, thay vì chuyển tới Trung Quốc vào hè này như dự định trước đó.', 'http://img.f3.thethao.vnecdn.net/2016/05/20/Terry-1425-1463698849_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/john-terry-chiu-thiet-gan-14-trieu-dola-de-o-lai-chelsea-3405950.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('288', 'Chơi cá độ bóng đá, sao Man City bị phạt 32.000 đôla', 'N/a', '2016-05-21 11:08:27', '2016-05-20 08:55:07', 'Hậu vệ Martin Demichelis mới phải nhận án phạt từ Liên đoàn bóng đá Anh (FA) do vi phạm luật.', 'http://img.f4.thethao.vnecdn.net/2016/05/20/demichelis-3052-1463706340_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/choi-ca-do-bong-da-sao-man-city-bi-phat-32-000-dola-3405990.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('289', 'CĐV Quảng Ninh doạ tẩy chay V-League vì chuyện chiếc loa', 'N/a', '2016-05-21 11:08:27', '2016-05-19 16:46:48', 'Sau khi VPF cấm mang loa điện vào sân cổ vũ, người hâm mộ đất Mỏ bất bình. Họ lên kế hoạch tẩy chay V-League bằng việc cổ vũ ngoài sân, hoặc vào sân nhưng đeo khẩu trang.', 'http://img.f4.thethao.vnecdn.net/2016/05/19/a2-5524-1463649381_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/cdv-quang-ninh-doa-tay-chay-v-league-vi-chuyen-chiec-loa-3405835.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('290', 'Vì sao Sevilla liên tục thành công tại Europa League', 'N/a', '2016-05-21 11:08:27', '2016-05-19 16:01:21', 'Đoạt Cup châu Âu không hề là chuyện dễ dàng, nhưng đội bóng của Tây Ban Nha đã làm việc đó ba năm liền tại Europa League. ', 'http://img.f3.thethao.vnecdn.net/2016/05/19/20160518T212742Z106608462MT1ACI14389746RTRMADP3SOCCEREUROPALIVSEVJPG-1463646458_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/vi-sao-sevilla-lien-tuc-thanh-cong-tai-europa-league-3405748.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('291', 'Bầu Đức chỉ cho Công Phượng ở lại Nhật Bản nếu được thi đấu nhiều hơn', 'N/a', '2016-05-21 11:08:27', '2016-05-19 14:36:31', 'CLB Mito Hollyhock muốn gia hạn hợp đồng với Công Phượng thêm một năm, và về cơ bản bầu Đức đã đồng ý.', 'http://img.f3.thethao.vnecdn.net/2016/05/19/91442151127660x0-1463625721-6965-1463625723_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/bau-duc-chi-cho-cong-phuong-o-lai-nhat-ban-neu-duoc-thi-dau-nhieu-hon-3405516.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('292', '5 câu chuyện cuốn hút nhất của Ngoại hạng Anh 2015-2016', 'N/a', '2016-05-21 11:08:27', '2016-05-19 11:19:50', 'Cuộc phiêu lưu của Leicester, số phận của Mourinho và những thay đổi trên băng ghế huấn luyện là ba chủ đề thu hút giới quan sát nhất.', 'http://img.f3.thethao.vnecdn.net/2016/05/17/1-1463458524_180x108.jpg', 'http://thethao.vnexpress.net/photo/hinh-bong-da/5-cau-chuyen-cuon-hut-nhat-cua-ngoai-hang-anh-2015-2016-3404350.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('293', 'Ferguson: \'Không như Messi, Ronaldo có thể đá cho đội hạng Sáu\'', 'N/a', '2016-05-21 11:08:27', '2016-05-19 09:12:40', 'Cựu HLV của Man Utd ám chỉ rằng cậu học trò cũ Cristiano Ronaldo toàn diện hơn, còn Lionel Messi chỉ thành công trong màu áo Barcelona.', 'http://img.f2.thethao.vnecdn.net/2016/05/19/sir-8657-1463618764_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/la-liga/ferguson-khong-nhu-messi-ronaldo-co-the-da-cho-doi-hang-sau-3405357.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('294', 'Vì Messi, ngôi sao Real không dám đi dạo với chó', 'N/a', '2016-05-21 11:08:27', '2016-05-19 16:07:12', 'Tiền vệ của Isco thừa nhận, việc đi dạo với chú chó cưng là một khó khăn vì không muốn gọi tên Messi.', 'http://img.f2.thethao.vnecdn.net/2016/05/19/isco-7102-1463648633_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/vi-messi-ngoi-sao-real-khong-dam-di-dao-voi-cho-3405821.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('295', '\'Guardiola cúi xin Pellegrini đừng đẩy Man City xuống Europa League\'', 'N/a', '2016-05-21 11:08:27', '2016-05-19 15:05:42', 'Trang EuroSport thể hiện một cách cường điệu những sự kiện bóng đá châu Âu diễn ra tuần qua.', 'http://img.f2.thethao.vnecdn.net/2016/05/19/guardiola-cui-xin-pellegrini-dung-day-man-city-xuong-europa-league-1463645290_180x108.jpg', 'http://thethao.vnexpress.net/photo/hau-truong/guardiola-cui-xin-pellegrini-dung-day-man-city-xuong-europa-league-3405706.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('296', 'Giả mạo cầu thủ, CĐV đột nhập sân tập đội tuyển Anh', 'N/a', '2016-05-21 11:08:27', '2016-05-19 14:49:00', 'Một CĐV trẻ thành công trong việc giả làm một tuyển thủ bóng đá Anh để trà trộn vào sân tập St George\'s Park.', 'http://img.f1.thethao.vnecdn.net/2016/05/19/1-1463630536_180x108.jpg', 'http://thethao.vnexpress.net/photo/hau-truong/gia-mao-cau-thu-cdv-dot-nhap-san-tap-doi-tuyen-anh-3405600.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('297', 'Hot boy của Đan Trường là quán quân \'Thần tượng Bolero\'', 'N/a', '2016-05-21 11:12:50', '2016-05-21 08:21:13', 'Thí sinh Trung Quang được 34,77 % bình chọn từ khán giả, trở thành người chiến thắng trong đêm chung kết.', 'http://img.f11.giaitri.vnecdn.net/2016/05/21/hot-boy-cua-dan-truong-doat-quan-quan-than-tuong-bolero-1463789873_180x108.jpg', 'http://giaitri.vnexpress.net/photo/truyen-hinh/hot-boy-cua-dan-truong-la-quan-quan-than-tuong-bolero-3406571.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('298', 'Dàn diễn viên phim \'Mùi ngò gai\' ngày ấy - bây giờ', 'N/a', '2016-05-21 11:12:50', '2016-05-21 05:09:00', 'So với Ngọc Trinh, Lý Thanh Thảo, Angela Phương Trinh, ngoại hình của Lương Thế Thành hay Kim Hiền không có nhiều thay đổi sau 10 năm.', 'http://img.f10.giaitri.vnecdn.net/2016/05/09/page-1462788550_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/dan-dien-vien-phim-mui-ngo-gai-ngay-ay-bay-gio-3399953.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('299', 'Charlize Theron khoe vẻ gợi cảm với sơ mi hở cúc', 'N/a', '2016-05-21 12:25:00', '2016-05-21 11:06:20', 'Mỹ nhân \'Max Điên\' diện bộ suit đen, gây ấn tượng khác biệt bên cạnh dàn mỹ nhân gợi cảm của Hollywood hôm 20/5.', 'http://img.f9.giaitri.vnecdn.net/2016/05/21/my-nhan-max-dien-nam-tinh-voi-so-mi-coi-cuc-1463802237_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/charlize-theron-khoe-ve-goi-cam-voi-so-mi-ho-cuc-3406753.html', '6', '6', '1', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('300', 'Lâm Tâm Như phủ nhận công khai tình yêu vì có thai', 'N/a', '2016-05-21 11:12:50', '2016-05-21 09:22:05', 'Người đẹp \"Mỹ nhân tâm kế\" tiết lộ thêm về quan hệ yêu đương với Hoắc Kiến Hoa.', 'http://img.f9.giaitri.vnecdn.net/2016/05/21/nhu1-3929-1463796325_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/quoc-te/lam-tam-nhu-phu-nhan-cong-khai-tinh-yeu-vi-co-thai-3406658.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('301', 'Hoài Linh \'nổi da gà\' trước tiết mục có 128 diễn viên tham gia', 'N/a', '2016-05-21 11:12:50', '2016-05-21 09:07:00', 'Phần trình diễn có tên \"Tự hào sử Việt\" giúp đội Hương Phù Sa giành giải quán quân \"Ngôi sao phương Nam 2016\", tối 20/5 tại TP HCM. ', 'http://img.f11.giaitri.vnecdn.net/2016/05/21/hoai-linh-1-5420-1463796046_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/hoai-linh-noi-da-ga-truoc-tiet-muc-co-128-dien-vien-tham-gia-3406611.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('302', 'Con trai tin đồn cổ vũ Hoài Linh làm giám khảo', 'N/a', '2016-05-21 11:12:50', '2016-05-21 08:04:29', 'Chàng trai Võ Lê Thành Vinh - được cho là con trai ruột của NSƯT - xuất hiện trong chung kết \"Ngôi sao phương Nam\", tối 20/5 tại TP HCM.', 'http://img.f12.giaitri.vnecdn.net/2016/05/20/con-trai-hoai-linh-1-4664-1463761341_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/con-trai-tin-don-co-vu-hoai-linh-lam-giam-khao-3406567.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('303', 'Phim \'Nữ đại gia\' lỏng kịch bản, thiếu kịch tính', 'N/a', '2016-05-21 11:12:50', '2016-05-21 00:30:00', 'Tác phẩm mới của đạo diễn Lê Văn Kiệt chứa các tình tiết dễ đoán, nhân vật chưa đủ chiều sâu, nội dung không chặt chẽ khiến chất hình sự trong phim bị nhạt.', 'http://img.f9.giaitri.vnecdn.net/2016/05/20/Nu-Dai-Gia-Nguyen-Cao-Ky-20-40-5773-5538-1463756508_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/diem-phim/phim-nu-dai-gia-long-kich-ban-thieu-kich-tinh-3404824.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('304', 'Diễm My 9X khoe chân dài với váy xẻ đùi', 'N/a', '2016-05-21 11:12:50', '2016-05-21 09:12:47', 'Nữ diễn viên hội ngộ dàn mỹ nhân Việt trong một bữa tiệc bên bể bơi tại TP HCM, tối 20/5.', 'http://img.f12.giaitri.vnecdn.net/2016/05/21/diem-my-9x-khoe-chan-dai-voi-vay-xe-dui-1463795466_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/diem-my-9x-khoe-chan-dai-voi-vay-xe-dui-3406662.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('305', 'Chọn kính mắt thế nào để phù hợp khuôn mặt', 'N/a', '2016-05-21 11:12:50', '2016-05-21 08:49:38', 'Những người có gương mặt góc cạnh nên tìm những loại kính có khung mềm mại và ngược lại.', 'http://img.f10.giaitri.vnecdn.net/2016/05/21/cach-chon-kinh-mat-phu-hop-guong-mat-1463793882_180x108.jpg', 'http://vnexpress.net/infographics/lang-mot/chon-kinh-mat-the-nao-de-phu-hop-khuon-mat-3406639.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('306', 'Á hậu Ngọc Oanh làm mới với phong cách menswear', 'N/a', '2016-05-21 11:12:50', '2016-05-21 00:02:00', 'Người đẹp gây ấn tượng bằng trang phục hàng hiệu trong một sự kiện thời trang ở Hà Nội, tối 20/5.', 'http://img.f11.giaitri.vnecdn.net/2016/05/20/a-hau-ngoc-oanh-lam-moi-voi-phong-cach-menswear-1463762651_180x108.jpg', 'http://giaitri.vnexpress.net/photo/trong-nuoc/a-hau-ngoc-oanh-lam-moi-voi-phong-cach-menswear-3406568.html', '6', '6', '0', '0', '1', null, '58');
