INSERT INTO `posts` VALUES ('316', 'Phong cách của Lý Nhã Kỳ được báo Pháp khen ngợi', 'N/a', '2016-05-21 11:12:50', '2016-05-20 11:29:06', 'Kênh FashionTV và tạp chí Gala đưa hình ảnh người đẹp Việt vào danh sách nhân vật mặc đẹp trên thảm đỏ Cannes những ngày qua. ', 'http://img.f9.giaitri.vnecdn.net/2016/05/20/topp-1463719206_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/sao-dep-sao-xau/phong-cach-cua-ly-nha-ky-duoc-bao-phap-khen-ngoi-3406050.html', '6', '6', '0', '0', '1', null, '58');
