INSERT INTO `posts` VALUES ('619', 'Bình nhiên liệu nặng 30 tấn diễu phố Los Angeles', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:27:31', 'Bình nhiên liệu nặng 30 tấn, cao ngang một tòa nhà 15 tầng, được đưa qua các đường phố Los Angeles trên hành trình dài 26 km đến Trung tâm Khoa học California, Mỹ.', 'http://news.img.video.vnecdn.net/web/2016/05/24/binh-nhien-lieu-nang-30-tan-dieu-pho-los-angeles-1464076322_180x108.jpg', 'http://video.vnexpress.net/khoa-hoc/binh-nhien-lieu-nang-30-tan-dieu-pho-los-angeles-3408299.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('620', 'Những vần thơ, câu hát Việt được Tổng thống Obama trích dẫn', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:26:27', 'Tổng thống Mỹ Barack Obama vận dụng triệt để thi ca Việt Nam trong những bài phát biểu về quan hệ song phương, với những câu tục ngữ, bài thơ từ hàng trăm năm trước, đến những bài hát của Trịnh Công Sơn, Văn Cao.', 'http://news.img.video.vnecdn.net/web/2016/05/24/nhung-van-tho-cau-hat-viet-duoc-tong-thong-obama-trich-dan-1464094341_180x108.jpg', 'http://video.vnexpress.net/the-gioi/nhung-van-tho-cau-hat-viet-duoc-tong-thong-obama-trich-dan-3408502.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('621', 'Việt Nam yêu cầu Ukraine làm rõ vụ đặc nhiệm đụng độ người Việt ở Làng Sen', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:25:09', 'Việt Nam hôm nay triệu đại sứ Ukraine đến Bộ Ngoại giao, đề nghị Ukraine lập tức điều tra vụ đặc nhiệm nước này đụng độ người Việt ở khu vực Làng Sen.', 'http://img.f32.vnecdn.net/2016/05/24/langsen413111464069386-1464099540_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/nguoi-viet-5-chau/viet-nam-yeu-cau-ukraine-lam-ro-vu-dac-nhiem-dung-do-nguoi-viet-o-lang-sen-3408508.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('622', 'Rising fuel prices push CPI up in May: GSO report', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:22:07', 'The General Statistics Office has announced that May\'s Consumer Price Index (CPI) rose by 0.54 percent compared to last month and 2.28 percent on-year.', 'http://img.f46.english.vnecdn.net/2016/05/24/123PNG-1464089523_1464089537_180x108.png', 'http://e.vnexpress.net/news/business/data-speaks/rising-fuel-prices-push-cpi-up-in-may-gso-report-3408393.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('623', 'Vietnam hopes to cash in on cashew exports to the U.S after Obama visit', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:21:58', 'The Vietnam Cashew Association (VCA) expects Obama’s visit to Vietnam will attract more traders from the U.S, which accounts for more than 50 percent of the country’s cashew export volume.', 'http://img.f48.english.vnecdn.net/2016/05/24/dieuO86671384560520-1464088701-8762-1464088729_180x108.jpg', 'http://e.vnexpress.net/news/business/markets/vietnam-hopes-to-cash-in-on-cashew-exports-to-the-u-s-after-obama-visit-3408481.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('624', 'Vietnam and U.S. have reached a new point of relationship: John Kerry', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:21:46', 'U.S. Secretary of State John Kerry said Tuesday the relationship between Vietnam and the United States have reached a new level.', 'http://img.f45.english.vnecdn.net/2016/05/24/ngoaitruongMy31551464091385-14-3757-3005-1464099680_180x108.jpg', 'http://e.vnexpress.net/news/news/vietnam-and-u-s-have-reached-a-new-point-of-relationship-john-kerry-3408512.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('625', 'Nadal thắng dễ, Murray chật vật đi tiếp', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:18:36', 'Hai ứng viên vô địch khởi đầu chiến dịch Roland Garros 2016 theo cách khác nhau nhưng đều giành quyền đi tiếp.', 'http://img.f3.thethao.vnecdn.net/2016/05/24/2016-05-24T134614Z-65306752-7357-1464099471_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/nadal-thang-de-murray-chat-vat-di-tiep-3408510.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('626', 'And the gift from Vietnamese PM to Obama is...', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:12:46', 'On behalf of the Vietnamese people, Prime Minister Phuc presented U.S. President Barack Obama with a special sculpture known as \'Thong diep ngan nam\' (millennium’s message), which is considered a symbol of Vietnamese culture.', 'http://img.f46.english.vnecdn.net/2016/05/24/mongtron-1464099106_1464099142_180x108.jpg', 'http://e.vnexpress.net/news/life/culture/and-the-gift-from-vietnamese-pm-to-obama-is-3408499.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('627', 'Hệ thống vũ khí trên siêu khu trục hạm Zumwalt Mỹ', 'N/a', '2016-05-24 23:41:00', '2016-05-24 21:00:00', 'Tàu khu trục tàng hình mới của Mỹ được trang bị các hệ thống vũ khí hiện đại, uy lực cùng các công nghệ tối tân.', 'http://img.f30.vnecdn.net/2016/05/24/1-1464083005_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/quoc-phong/he-thong-vu-khi-tren-sieu-khu-truc-ham-zumwalt-my-3408321.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('628', 'Bom nặng gần 350 kg dưới hồ nuôi cá', 'N/a', '2016-05-24 23:41:00', '2016-05-24 20:52:05', 'Quá trình đào đất nạo vét lòng hồ, một số công nhân ở Hà Tĩnh phát hiện quả bom nặng 340 kg ở dưới lớp bùn.', 'http://img.f29.vnecdn.net/2016/05/24/d5c43749f97742f99966ac30db9f68b1-1464092949_180x108.jpg', 'http://vnexpress.net/tin-tuc/thoi-su/bom-nang-gan-350-kg-duoi-ho-nuoi-ca-3408501.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('629', 'Ai Cập bác thông tin phi cơ chở 66 người phát nổ', 'N/a', '2016-05-24 23:41:00', '2016-05-24 20:49:22', 'Ai Cập phủ nhận thông tin kết quả khám nghiệm những phần thi thể hành khách trên MS804 cho thấy phi cơ đã phát nổ.', 'http://img.f32.vnecdn.net/2016/05/24/20160521T152322Z267104039S1BETFIOACAARTRMADP3EGYPTAIRAIRPLANEJPG-1464097471_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/ai-cap-bac-thong-tin-phi-co-cho-66-nguoi-phat-no-3408503.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('630', 'An ninh đội mưa bảo vệ ông Obama ngày cuối ở Hà Nội', 'N/a', '2016-05-24 23:41:00', '2016-05-24 20:48:12', 'Thời tiết không thuận lợi khiến lực lượng dẫn đoàn hộ tống xe của người đứng đầu Nhà Trắng đi trong mưa lớn.', 'http://img.f29.vnecdn.net/2016/05/24/6-1000x667-1464086194_180x108.jpg', 'http://vnexpress.net/photo/phap-luat/an-ninh-doi-mua-bao-ve-ong-obama-ngay-cuoi-o-ha-noi-3408462.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('631', 'Mưa tại Yên Bái làm chết người, tắc quốc lộ', 'N/a', '2016-05-24 23:41:00', '2016-05-24 20:30:48', 'Tính đến chiều 24/5, mưa lũ tại Yên Bái làm một người chết, 200 nhà bị ngập úng, quốc lộ 70 bị ách tắc và sạt ta luy.', 'http://news.img.video.vnecdn.net/web/2016/05/24/videocapture_1464096619397_180x108.jpg', 'http://video.vnexpress.net/xa-hoi/mua-tai-yen-bai-lam-chet-nguoi-tac-quoc-lo-3408507.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('632', 'Tổng thống Mỹ làm MC khi đối thoại với start-up Việt', 'N/a', '2016-05-24 23:41:17', '2016-05-24 17:13:25', 'Trong một giờ giao lưu, Tổng thống Obama đã hoàn toàn chinh phục hơn 100 doanh nhân, doanh nghiệp có mặt tại không gian khởi nghiệp Dreamplex khi trực tiếp giữ vai trò dẫn dắt cuộc trò chuyện với 3 doanh nhân trẻ tiêu biểu của Việt Nam.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/24/Obama-1464097602_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/tong-thong-my-lam-mc-khi-doi-thoai-voi-start-up-viet-3408354.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('633', 'Phải trả địa điểm đón đoàn Obama, Đại hội cổ đông Eximbank lại bất thành', 'N/a', '2016-05-24 23:53:47', '2016-05-24 15:36:55', 'Kéo dài sang buổi chiều, khi khách sạn InterContinental phải lấy lại mặt bằng để đảm bảo an ninh đón phái đoàn Tổng thống Mỹ, cuộc họp cổ đông Eximbank sẽ phải thực hiện một lần nữa, dù đã bước sang phần kiểm phiếu.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/24/eximbankIMG-0487-8906-1464077054_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/ngan-hang/phai-tra-dia-diem-don-doan-obama-dai-hoi-co-dong-eximbank-lai-bat-thanh-3408268.html', '6', '2', '1', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('634', 'Núi tiền nghìn tỷ USD của các công ty Mỹ', 'N/a', '2016-05-24 23:41:17', '2016-05-24 13:46:24', 'Các công ty Mỹ nắm 1.680 tỷ USD tiền mặt năm ngoái, tăng nhẹ so với năm trước đó và hơn gấp đôi năm 2007 - thời điểm trước khủng hoảng tài chính.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/24/apple-5612-1464063778_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/nui-tien-nghin-ty-usd-cua-cac-cong-ty-my-3408181.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('635', 'Cất nóc và mở bán dự án The Southern Dragon', 'N/a', '2016-05-24 23:41:17', '2016-05-24 16:00:00', 'Sự kiện diễn ra tại Trung tâm hội nghị Queen Plaza số 218 Thành Thái, quận 10, TP HCM ngày 29/5 tới.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/05/24/Image-ExtractWord-1-Out-5291-1464077667_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/cat-noc-va-mo-ban-du-an-the-southern-dragon-3408301.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('636', 'Aeon Mall chuẩn bị khai trương trung tâm mua sắm thứ 4', 'N/a', '2016-05-24 23:41:17', '2016-05-24 16:00:00', 'Aeon Mall Bình Tân, TP HCM  sẽ được khai trương vào 1/7 tới.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/20/Long-bien-3280-1463734167_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/aeon-mall-chuan-bi-khai-truong-trung-tam-mua-sam-thu-4-3406087.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('637', 'Quán lẩu Hà thành sở hữu loạt hotline ấn tượng', 'N/a', '2016-05-24 23:41:17', '2016-05-24 16:00:00', 'Dù mới khai trương, quán lẩu Đức Trọc được nhiều thực khách biết đến với các món ăn thơm ngon và loạt số hotline mang ý nghĩa tài lộc.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/24/Image-ExtractWord-0-Out-6258-1-1260-2392-1464079854_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/quan-lau-ha-thanh-so-huu-loat-hotline-an-tuong-3406029.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('638', 'Sacombank đạt giải dịch vụ ngân hàng trực tuyến tốt nhất', 'N/a', '2016-05-24 23:41:17', '2016-05-24 15:00:00', 'Dịch vụ ngân hàng điện tử Sacombank hỗ trợ người dùng chuyển tiền trong và ngoài hệ thống, thanh toán hóa đơn, thẻ tín dụng...', 'http://img.f25.kinhdoanh.vnecdn.net/2016/05/24/Sacombank-Best-use-of-online-b-5330-4980-1464077619_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/ngan-hang/sacombank-dat-giai-dich-vu-ngan-hang-truc-tuyen-tot-nhat-3408281.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('639', 'VinaPhone khuyến mãi thuê bao trả trước nạp tiền điện tử', 'N/a', '2016-05-24 23:41:17', '2016-05-24 15:00:00', '25% giá trị tặng thêm cho khách hàng gọi và nhắn tin liên mạng, 25% còn lại cộng vào tài khoản nội mạng của VinaPhone và cố định VNPT.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/24/khuyen-mai-ngay-vang-vinaphone-5917-9501-1464075189_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/tin-khuyen-mai/vinaphone-khuyen-mai-thue-bao-tra-truoc-nap-tien-dien-tu-3408270.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('640', 'Marina Danang Bay hưởng lợi thế hạ tầng', 'N/a', '2016-05-24 23:41:17', '2016-05-24 13:00:00', 'Marina Danang Bay sẽ trở thành đầu mối giao thông khu vực với loại hình vận tải đường bộ, đường biển, đường sắt khi các công trình hạ tầng tại đây đưa vào sử dụng.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/24/24-5-201641-90364110-5519-1464066358_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/marina-danang-bay-huong-loi-the-ha-tang-3408059.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('641', 'Thực vật xâm lấn rao bán tràn lan trên chợ điện tử', 'N/a', '2016-05-24 23:41:17', '2016-05-24 12:00:00', 'Khách hàng có thể đặt mua nhiều loại thực vật được rao bán trên Amazon và eBay.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/05/24/1174-3741-1464063692_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/thuong-mai-dien-tu/tin-tuc/thuc-vat-xam-lan-rao-ban-tran-lan-tren-cho-dien-tu-3408027.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('642', 'Khu đô thị sinh thái đầu tiên ở phía Tây Hà Nội', 'N/a', '2016-05-24 23:41:17', '2016-05-24 17:00:00', 'Điểm nhấn của Vinhomes Thăng Long là hồ cảnh quan điều hòa rộng 10ha với đường chạy bộ ven hồ.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/05/24/24-5-201658-158416119-7103-1464078929_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/khu-do-thi-sinh-thai-dau-tien-o-phia-tay-ha-noi-3408319.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('643', 'Hồ nước nhiễm phóng xạ thành điểm du lịch', 'N/a', '2016-05-24 23:41:27', '2016-05-24 20:30:00', 'Từ một khu thử nghiệm hạt nhân nằm ở vùng Semey, đông Kazakhstan, hồ Chagan trở thành điểm đến đặc biệt để du khách ưa mạo hiểm muốn khám phá khu vực thiên nhiên bị nhiễm phóng xạ. ', 'http://img.f34.dulich.vnecdn.net/2016/05/24/2-1464060239_180x108.png', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/ho-nuoc-nhiem-phong-xa-thanh-diem-du-lich-3408035.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('644', 'Những quy định khó hiểu của bang California', 'N/a', '2016-05-24 23:41:27', '2016-05-24 19:00:00', 'Mặc đồ ngủ lái xe là phạm pháp, cấm động vật giao phối nơi công cộng hay cấm phương tiện cơ giới không người điều khiển chạy quá 100km/ giờ là những điều luật khó hiểu của bang California, Mỹ.', 'http://img.f33.dulich.vnecdn.net/2016/05/24/peacock-1-2436-1464087370_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/nhung-quy-dinh-kho-hieu-cua-bang-california-3408463.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('645', 'Phòng tổng thống trong khách sạn đón đoàn Obama ở Sài Gòn', 'N/a', '2016-05-24 23:41:27', '2016-05-24 13:04:55', 'Tổng thống Mỹ Obama khi đến Sài Gòn dự kiến sẽ nghỉ tại phòng nguyên thủ nằm trên tầng 20 của khách sạn Intercontinental Asiana Saigon.', 'http://img.f33.dulich.vnecdn.net/2016/05/24/4-1464066113_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/phong-tong-thong-trong-khach-san-don-doan-obama-o-sai-gon-3408192.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('646', 'Quán bún chả Hà Nội đông nghịt khách sau khi Obama ghé ăn', 'N/a', '2016-05-24 23:41:27', '2016-05-24 08:14:37', 'Gần 2h chiều, trời mưa to không ngớt mà quán bún chả số 24 Lê Văn Hưu vẫn đông khách, thậm chí có vị khách còn phải ngồi chờ rất lâu vì nhà hàng chưa kịp quạt xong chả.', 'http://img.f35.dulich.vnecdn.net/2016/05/24/1-1464072088_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-noi/quan-bun-cha-ha-noi-dong-nghit-khach-sau-khi-obama-ghe-an-3407936.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('647', 'Du khách tử nạn khi chinh phục đỉnh Everest', 'N/a', '2016-05-24 23:41:27', '2016-05-24 15:03:00', 'Hai vợ chồng du khách người Australia đặt mục tiêu chinh phục đỉnh Everest, nhưng cuối cùng người vợ chết trong quá trình leo núi còn chồng phải đưa vào viện cấp cứu.', 'http://img.f33.dulich.vnecdn.net/2016/05/24/MountEverestasseenfromDrukair2PLWedit-1464063085_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-tu-nan-khi-chinh-phuc-dinh-everest-3408111.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('648', 'Ký ức của du khách Mỹ đầu tiên đặt chân đến Việt Nam sau chiến tranh', 'N/a', '2016-05-24 23:41:27', '2016-05-24 12:03:00', '\"Khác với những gì tôi nghĩ, người Sài Gòn vô cùng thân thiện. Họ chào đón tôi với nụ cười rạng rỡ\", Tamar Lowell là một trong những khách Mỹ đầu tiên đến Việt Nam vào năm 1994 nhớ lại.', 'http://img.f33.dulich.vnecdn.net/2016/05/24/13293084102016358647761811447761650n-1464099017_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/ky-uc-cua-du-khach-my-dau-tien-dat-chan-den-viet-nam-sau-chien-tranh-3407661.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('649', 'Bình nhiên liệu tàu con thoi nặng 30 tấn diễu phố Los Angeles', 'N/a', '2016-05-24 23:41:40', '2016-05-24 15:42:51', 'Bình nhiên liệu nặng 30 tấn, cao ngang một tòa nhà 15 tầng, được đưa qua các đường phố Los Angeles trên hành trình dài 26 km đến Trung tâm Khoa học California, Mỹ.', 'http://img.f31.vnecdn.net/2016/05/24/VNETank2-1464076048_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/binh-nhien-lieu-tau-con-thoi-nang-30-tan-dieu-pho-los-angeles-3408226.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('650', 'Tài săn mồi chớp nhoáng của sát thủ sâu dài ba mét', 'N/a', '2016-05-24 23:41:40', '2016-05-24 12:23:42', 'Loài sâu Bobbit dài ba mét thường ẩn mình dưới đáy đại dương chờ con mồi đến gần để tóm gọn và xẻ nạn nhân làm đôi bằng bộ răng sắc nhọn.', 'http://img.f31.vnecdn.net/2016/05/24/VNEWorm-1464061899_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/tai-san-moi-chop-nhoang-cua-sat-thu-sau-dai-ba-met-3407973.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('651', 'Cách đo chiều cao núi so với mực nước biển?', 'N/a', '2016-05-24 23:41:40', '2016-05-24 11:15:48', 'Tôi được biết sau vài năm, đỉnh Everest lại thấp đi vài cm so với mực nước biển. Xin hỏi cách tính toán thế nào? (Đỗ Hải Đăng)', 'http://img.f31.vnecdn.net/2016/05/24/everest-1464055073_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/cach-do-chieu-cao-nui-so-voi-muc-nuoc-bien-3408002.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('652', 'PETA phơi bày sự thật tàn nhẫn phía sau đồ da hàng hiệu', 'N/a', '2016-05-24 23:41:40', '2016-05-24 11:02:08', 'Đằng sau những chiếc túi xách và giày da hàng hiệu là hơn một tỷ động vật bò sát bị đánh chết và lột da sống mỗi năm.', 'http://img.f31.vnecdn.net/2016/05/23/VNEPETA-1463998189_180x108.png', 'http://vnexpress.net/tin-tuc/khoa-hoc/peta-phoi-bay-su-that-tan-nhan-phia-sau-do-da-hang-hieu-3407836.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('653', 'Bé trai bị sư tử lôi đi trong đêm và vồ đến chết', 'N/a', '2016-05-24 23:41:40', '2016-05-24 10:13:26', 'Một cậu bé Ấn Độ 14 tuổi đang nằm ngủ bên cạnh cha trong vườn cây ăn quả ở trang trại thì bị sư tử lôi đi ăn thịt.', 'http://img.f31.vnecdn.net/2016/05/24/VNELions-1464057491_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/be-trai-bi-su-tu-loi-di-trong-dem-va-vo-den-chet-3408006.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('654', 'Nhện độc bò lổm ngổm trên máy bay khiến hành khách khiếp vía', 'N/a', '2016-05-24 23:41:40', '2016-05-24 06:00:00', 'Hai con nhện taratula to lớn bất ngờ xuất hiện trên một chuyến bay sang Canada, gây kinh hãi cho toàn bộ hành khách.', 'http://img.f29.vnecdn.net/2016/05/23/VNETarantula-1463979840_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/nhen-doc-bo-lom-ngom-tren-may-bay-khien-hanh-khach-khiep-via-3407572.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('655', 'Kho báu giấu dưới đáy cốc hơn 70 năm', 'N/a', '2016-05-24 23:41:40', '2016-05-23 21:00:00', 'Nhẫn và dây chuyền vàng được bọc cẩn thận bằng vải bạt trước khi giấu dưới đáy giả của một chiếc cốc trong trại tập trung lớn nhất dưới thời Đức Quốc xã.', 'http://img.f30.vnecdn.net/2016/05/23/VNETreasure3-1463992986_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/kho-bau-giau-duoi-day-coc-hon-70-nam-3407761.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('656', 'Phát hiện dạng ánh sáng mới làm tăng tốc độ Internet', 'N/a', '2016-05-24 23:41:40', '2016-05-23 20:00:00', 'Các nhà vật lý Ireland tìm ra một dạng tồn tại mới của ánh sáng có hình vít xoắn và không tuân theo định luật góc động lượng.', 'http://img.f31.vnecdn.net/2016/05/23/anh1-1463987026_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/phat-hien-dang-anh-sang-moi-lam-tang-toc-do-internet-3407647.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('657', 'Bí quyết chế tạo chiếc khiên của \'đại úy siêu nhân\'', 'N/a', '2016-05-24 23:41:40', '2016-05-23 19:00:00', 'Một kỹ sư người Canada chia sẻ bí quyết chế tạo chiếc khiên nổi tiếng trong siêu phẩm phim khoa học viễn tưởng Captain America (Đại úy Mỹ).', 'http://img.f29.vnecdn.net/2016/05/23/VNECaptain-1463990705_180x108.png', 'http://vnexpress.net/tin-tuc/khoa-hoc/bi-quyet-che-tao-chiec-khien-cua-dai-uy-sieu-nhan-3407725.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('658', 'Cá ma cà rồng hút máu tràn ngập nhiều sông ngòi nước Anh', 'N/a', '2016-05-24 23:41:40', '2016-05-23 11:50:22', 'Người đi bơi được cảnh báo khẩn cấp sau khi số lượng cá mút đá dài một mét với hàm răng sắc nhọn đạt mức kỷ lục trên nhiều con sông tại Anh.', 'http://img.f30.vnecdn.net/2016/05/23/VNELamprey3-1463976969_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/ca-ma-ca-rong-hut-mau-tran-ngap-nhieu-song-ngoi-nuoc-anh-3407388.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('659', 'Cá sấu ăn thịt người sông Nile xuất hiện ở Mỹ', 'N/a', '2016-05-24 23:41:40', '2016-05-23 10:43:42', 'Những con cá sấu sông Nile nổi tiếng do giết chết hàng trăm người mỗi năm ở châu Phi, có thể trở thành loài xâm hại gây lo ngại ở Florida, Mỹ.', 'http://img.f30.vnecdn.net/2016/05/23/VNECroc1-1463971094_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/ca-sau-an-thit-nguoi-song-nile-xuat-hien-o-my-3407359.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('660', 'Thời gian phân hủy của các loại rác thải', 'N/a', '2016-05-24 23:41:40', '2016-05-23 09:40:41', 'Trong khi lõi táo chỉ mất hai tháng để phân hủy, thời gian tồn tại của tã bỉm trẻ em và chai lọ thủy tinh lên tới 450 năm và một triệu năm tương ứng.', 'http://img.f30.vnecdn.net/2016/05/22/tuoi-tho-cua-cac-loai-rac-thai-1463880285_180x108.jpg', 'http://vnexpress.net/photo/moi-truong/thoi-gian-phan-huy-cua-cac-loai-rac-thai-3407054.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('661', 'Everest không phải đỉnh núi cao nhất thế giới', 'N/a', '2016-05-24 23:41:40', '2016-05-23 08:07:08', 'Dựa theo cách đo truyền thống, với độ cao gần 9.000 m so với mực nước biển, núi Everest vẫn cao hơn những đối thủ gần nhất cả nghìn mét, vượt xa 2.600 m so với núi Chimborazo (6.248 m).', 'http://img.f31.vnecdn.net/2016/05/22/MtChimborazo-1463892867_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/everest-khong-phai-dinh-nui-cao-nhat-the-gioi-3407138.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('662', 'Diễn viên Hollywood đến Việt Nam kêu gọi bảo vệ tê giác', 'N/a', '2016-05-24 23:41:40', '2016-05-23 02:00:00', '\'Việt Nam ơi, bảo vệ tê giác\' là chiến dịch do diễn viên Hollywood Paul Blackthorne thực hiện nhằm nâng cao nhận thức và giảm tiêu thụ sừng tê giác ở Việt Nam.', 'http://img.f31.vnecdn.net/2016/05/21/tegiac1-4671-1463803659_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/dien-vien-hollywood-den-viet-nam-keu-goi-bao-ve-te-giac-3406791.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('663', 'Scotland xây nhà máy phong năng nổi lớn nhất thế giới', 'N/a', '2016-05-24 23:41:40', '2016-05-23 09:31:03', 'Nhà máy phong năng nổi lớn nhất thế giới sẽ khởi công trong năm nay ở ngoài khơi bờ biển phía tây của Scotland.', 'http://img.f31.vnecdn.net/2016/05/22/CaptureJPG-1463913075_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/ky-thuat-moi/scotland-xay-nha-may-phong-nang-noi-lon-nhat-the-gioi-3407194.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('664', 'Cây ăn thịt hoạt động như thế nào?', 'N/a', '2016-05-24 23:41:40', '2016-05-23 06:00:00', 'Tôi thấy mỗi lần côn trùng bay vào, cây ăn thịt lại khép vào. Xin hỏi tại sao có hiện tượng này? Cơ chế hoạt động của nó thế nào? (Trần An Bình)', 'http://img.f30.vnecdn.net/2016/05/21/maxresdefault-1463805561_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/cay-an-thit-hoat-dong-nhu-the-nao-3406854.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('665', 'Thị trấn không rác thải tại Nhật', 'N/a', '2016-05-24 23:41:40', '2016-05-22 19:00:00', 'Một thị trấn tại Nhật áp dụng chương trình phân loại và tái chế tới 80% rác thải và dự kiến đến năm 2020, thị trấn sẽ hoàn toàn không còn rác thải.', 'http://img.f32.vnecdn.net/2016/05/22/VNETown-1463878488_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/moi-truong/thi-tran-khong-rac-thai-tai-nhat-3407040.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('666', 'Đảo Hải Nam tách ra từ Việt Nam cách đây hàng triệu năm', 'N/a', '2016-05-24 23:41:40', '2016-05-22 07:00:00', 'Phần lớn thực vật và động vật trên đảo Hải Nam ở cực nam Trung Quốc có nguồn gốc từ Việt Nam, nơi cách đó hàng trăm kilomet.', 'http://img.f29.vnecdn.net/2016/05/21/VNEHainan-1463804133_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/moi-truong/dao-hai-nam-tach-ra-tu-viet-nam-cach-day-hang-trieu-nam-3406832.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('667', 'Nguồn gốc và sức mạnh phá hủy của núi lửa', 'N/a', '2016-05-24 23:41:40', '2016-05-22 14:00:00', 'Núi lửa sẽ phun trào nếu có một lượng lớn dung nham nóng chảy cùng khí tích tụ dưới lòng đất.', 'http://img.f30.vnecdn.net/2016/05/21/nguon-goc-va-suc-manh-pha-huy-cua-nui-lua-1463803651_180x108.jpg', 'http://vnexpress.net/infographics/khoa-hoc/nguon-goc-va-suc-manh-pha-huy-cua-nui-lua-3406827.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('668', 'Những vật liệu đắt nhất thế giới tính theo gram', 'N/a', '2016-05-24 23:41:40', '2016-05-22 08:00:00', 'Khi tính theo trọng lượng gram, nhiều vật liệu trên thế giới còn đắt hơn vàng, với mức giá lên đến hàng chục tỷ USD.', 'http://img.f29.vnecdn.net/2016/05/19/VNEWorth0-1463627657_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/nhung-vat-lieu-dat-nhat-the-gioi-tinh-theo-gram-3405508.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('669', 'Đây là nấm gì?', 'N/a', '2016-05-24 23:41:40', '2016-05-22 06:00:00', 'Nấm này hái ở trong rừng, xin hỏi đó là nấm gì? Có dùng được không? (Vũ Kiên)', 'http://img.f31.vnecdn.net/2016/05/21/14637962253731463796280-1463805268_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/day-la-nam-gi-3406851.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('670', 'Trung Quốc đang nhanh chóng hóa thành sa mạc', 'N/a', '2016-05-24 23:41:40', '2016-05-21 19:00:00', 'Sa mạc hóa đang nuốt chửng hàng nghìn km2 đất sản xuất nông nghiệp của Trung Quốc mỗi năm, trở thành thách thức lớn chưa từng có ở quốc gia này.', 'http://img.f29.vnecdn.net/2016/05/21/Anh1-1463797316_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trung-quoc-dang-nhanh-chong-hoa-thanh-sa-mac-3406696.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('671', 'Em bé tự chui ra khỏi bụng mẹ trong ca sinh mổ tự nhiên', 'N/a', '2016-05-24 23:41:40', '2016-05-21 13:26:54', 'Khoảnh khắc em bé nhúc nhích tự chui ra khỏi tử cung người mẹ theo phương pháp sinh mổ tự nhiên gây xúc động cho nhiều người.', 'http://img.f30.vnecdn.net/2016/05/21/VNEBirth1-1463807244_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/em-be-tu-chui-ra-khoi-bung-me-trong-ca-sinh-mo-tu-nhien-3406879.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('672', 'Sinh viên xúc động khi ông Obama rành lịch sử Việt Nam', 'N/a', '2016-05-24 23:41:55', '2016-05-24 16:58:40', '\"Điều em cảm phục nhất ở ông Obama là sự hiểu biết về lịch sử Việt Nam khi nhắc nhiều đến các nhân vật lịch sử như Lý Thường Kiệt, Hai Bà Trưng, các danh nhân như Nguyễn Du, tướng Võ Nguyên Giáp\", Trần Khánh, Đại học FPT chia sẻ.', 'http://img.f29.vnecdn.net/2016/05/24/svvn-5499-1464082249_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/sinh-vien-xuc-dong-khi-ong-obama-ranh-lich-su-viet-nam-3408397.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('673', 'Các cách phát âm chữ P trong tiếng Anh', 'N/a', '2016-05-24 23:41:55', '2016-05-24 11:35:34', 'Chữ P trong tiếng Anh thường được phát âm là /p/ dù nó được viết với một chữ P hay hai chữ P đứng cạnh nhau (pp).', 'http://img.f29.vnecdn.net/2016/05/24/chuP-1464063049_1464063089_1464064527_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-tieng-anh/cac-cach-phat-am-chu-p-trong-tieng-anh-3408153.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('674', 'Sinh viên Việt háo hức gặp Tổng thống Mỹ', 'N/a', '2016-05-24 23:41:55', '2016-05-24 11:17:55', 'Có mặt rất sớm trước giờ nói chuyện của ông Obama tại Mỹ Đình sáng 24/5, nhiều sinh viên chia sẻ những nhận xét về Tổng thống Mỹ và mong được đặt câu hỏi với ông', 'http://news.img.video.vnecdn.net/web/2016/05/24/sinh-vien-hao-huc-truoc-khi-gap-tong-thong-my-1464072679_180x108.jpg', 'http://video.vnexpress.net/xa-hoi/sinh-vien-viet-hao-huc-gap-tong-thong-my-3408243.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('675', 'Tổng thống Obama mang sách điện tử đến cho người nghèo', 'N/a', '2016-05-24 23:41:55', '2016-05-24 18:08:00', 'Ứng dụng đọc và tải sách miễn phí Open eBooks giúp người dân Mỹ tiếp cận tri thức dễ dàng hơn.', 'http://img.f31.vnecdn.net/2016/05/24/24-b2-a1-3507-1464085625_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/tong-thong-obama-mang-sach-dien-tu-den-cho-nguoi-ngheo-3408356.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('676', 'Bài toán được đề xuất trong cuộc thi quốc tế IMC', 'N/a', '2016-05-24 23:41:55', '2016-05-24 11:28:08', 'Bài toán sau được đề xuất ở vòng loại thứ hai cho cuộc thi toán quốc tế IMC (International Mathematics Contest) 2015.', 'http://img.f31.vnecdn.net/2016/05/24/chongoi2-1464062822_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bai-toan-duoc-de-xuat-trong-cuoc-thi-quoc-te-imc-3408146.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('677', 'Được và mất khi theo học trực tuyến', 'N/a', '2016-05-24 23:41:55', '2016-05-24 00:00:00', 'Có thể vừa học vừa làm, tiếp cận kiến thức thực tế, song giáo dục trực tuyến chưa được nhiều người coi trọng.', 'http://img.f32.vnecdn.net/2016/05/23/23-b1-a1-jpeg-7459-1463975612_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/duoc-va-mat-khi-theo-hoc-truc-tuyen-3407526.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('678', 'Cách viết mở bài môn Văn lấy lòng giám khảo chấm thi', 'N/a', '2016-05-24 23:41:55', '2016-05-24 11:36:44', 'Đối với những đề bài dài, vấn đề được gửi gắm trong một mẫu truyện, bài thơ, hoạc một trích đoạn bài báo…thì ở phần mở bài học sinh không nên trích dẫn lại toàn bộ ngữ liệu mà phải phân tích kĩ đề bài để khái quát nên vấn đề cần giải quyết trong 1 hoạc 2 câu văn ngắn gọn.', 'http://img.f32.vnecdn.net/2016/05/24/thay-Khuong-4247-1464062648_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/cach-viet-mo-bai-mon-van-lay-long-giam-khao-cham-thi-3408142.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('679', 'Chương trình Anh ngữ theo phương pháp giáo dục Mỹ', 'N/a', '2016-05-24 23:41:55', '2016-05-24 10:00:00', 'Học sinh được trau dồi tiếng Anh và kỹ năng mềm qua các bài học theo chủ đề thuộc nhiều lĩnh vực khác nhau.', 'http://img.f29.vnecdn.net/2016/05/24/H1-GD-5634-1464074840_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/chuong-trinh-anh-ngu-theo-phuong-phap-giao-duc-my-3407925.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('680', 'Giảm chỉ tiêu tuyển sinh sư phạm', 'N/a', '2016-05-24 23:41:55', '2016-05-23 16:54:12', 'Bộ Giáo dục và Đào tạo yêu cầu các cơ sở đào tạo giảm chỉ tiêu tuyển sinh sư phạm năm 2017 để khắc phục tình trạng tốt nghiệp không có việc làm.', 'http://img.f29.vnecdn.net/2016/05/23/sv-9628-1463995043_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/giam-chi-tieu-tuyen-sinh-su-pham-3407786.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('681', 'Nữ sinh tặng hoa Tổng thống Obama từng \'chất vấn\' thứ trưởng Mỹ', 'N/a', '2016-05-24 23:41:55', '2016-05-23 15:58:03', '\"Liệu Mỹ có tiếp tục duy trì sự can dự của mình với khu vực châu Á - Thái Bình Dương hay không?\", Trần Mỹ Linh đặt câu hỏi cho ông Antony Blinken.', 'http://img.f30.vnecdn.net/2016/05/23/top-1463992069_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/nu-sinh-tang-hoa-tong-thong-obama-tung-chat-van-thu-truong-my-3407681.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('682', 'Các người đẹp gốc Việt quyến rũ trên thảm đỏ Hollywood', 'N/a', '2016-05-24 23:42:07', '2016-05-24 20:23:00', 'Trần Đồng Lan (Lana Condor), Karrueche Tran, Maggie Q... đều ghi dấu ấn với vẻ ngoài sexy khi tham gia các sự kiện lớn tại kinh đô điện ảnh.', 'http://img.f10.giaitri.vnecdn.net/2016/05/24/nguoidepgocViet-1464085251_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/quoc-te/cac-nguoi-dep-goc-viet-quyen-ru-tren-tham-do-hollywood-3408420.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('683', 'Victoria Beckham mặc nội y chụp ảnh bìa tạp chí', 'N/a', '2016-05-24 23:42:07', '2016-05-24 19:15:00', 'Bà mẹ bốn con tạo dáng nhí nhảnh trên giường khi thực hiện bộ ảnh cho Elle Hong Kong.', 'http://img.f9.giaitri.vnecdn.net/2016/05/24/victoria-beckham-khoe-noi-y-tren-bia-tap-chi-trung-quoc-1464079656_180x108.jpg', 'http://giaitri.vnexpress.net/photo/quoc-te/victoria-beckham-mac-noi-y-chup-anh-bia-tap-chi-3408359.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('684', 'Lý do làm nên phong cách giản dị của Tổng thống Obama', 'N/a', '2016-05-24 23:42:07', '2016-05-24 18:23:42', 'Người đứng đầu nước Mỹ chung thủy với đôi giày cũ đã thủng đế, thích mặc chiếc áo vest ai cũng chê và đôi lúc ông nhận mình là người lôi thôi.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/obama2-1464089816_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/lang-mot/ly-do-lam-nen-phong-cach-gian-di-cua-tong-thong-obama-3408340.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('685', 'Hari Won đổi hình tượng chững chạc từ khi yêu Trấn Thành', 'N/a', '2016-05-24 23:42:07', '2016-05-24 15:21:18', 'Sau một thời gian dài im tiếng, nữ ca sĩ trở lại với dự án âm nhạc mới, đánh dấu sự thay đổi về phong cách.', 'http://img.f9.giaitri.vnecdn.net/2016/05/24/hari-won-doi-hinh-tuong-chung-chac-tu-khi-yeu-tran-thanh-1464073736_180x108.jpg', 'http://giaitri.vnexpress.net/photo/trong-nuoc/hari-won-doi-hinh-tuong-chung-chac-tu-khi-yeu-tran-thanh-3407887.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('686', 'Kỳ Duyên ra sân bay đón Hoa hậu Pháp', 'N/a', '2016-05-24 23:42:07', '2016-05-24 16:48:08', 'Hoa hậu Pháp 2014 Flora Coquerel tới TP HCM để tham gia catwalk trong show thời trang \"Ngôn ngữ hoa\" của Hoàng Hải.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/ky-duyen-ra-san-bay-don-hoa-hau-phap-1464080665_180x108.jpg', 'http://giaitri.vnexpress.net/photo/trong-nuoc/ky-duyen-ra-san-bay-don-hoa-hau-phap-3408333.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('687', 'Taylor Swift khoe biệt thự 25 triệu đô', 'N/a', '2016-05-24 23:42:07', '2016-05-24 16:30:21', 'Trong cuộc phỏng vấn với báo Vogue, \"Công chúa nhạc đồng quê\" giới thiệu căn nhà ở Beverly Hills (Mỹ) và trả lời 73 câu hỏi ngẫu nhiên.', 'http://news.img.video.giaitri.vnecdn.net/web/2016/05/24/taylor-swift-khoe-dinh-thu-25-trieu-do-1464082312_180x108.jpg', 'http://video.vnexpress.net/video/taylor-swift-khoe-biet-thu-25-trieu-do-3406869.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('688', '15 bộ váy xa xỉ của Lý Nhã Kỳ qua ba mùa Cannes', 'N/a', '2016-05-24 23:42:07', '2016-05-24 14:28:41', 'Cựu Đại sứ Du lịch chuộng các thiết kế hàng hiệu, đặc biệt là váy Haute Couture của Georges Hobeika - nhà mốt có lịch sử lâu đời ở Pháp.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/vay-ao-long-lay-cua-ly-nha-ky-tren-tham-do-cac-mua-cannes-1464060506_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/15-bo-vay-xa-xi-cua-ly-nha-ky-qua-ba-mua-cannes-3405570.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('689', 'Mỹ Linh: \'Tôi hát bằng cả trái tim chào đón Tổng thống Obama\'', 'N/a', '2016-05-24 23:42:07', '2016-05-24 13:10:26', 'Diva nhạc Việt chia sẻ cảm xúc khi vinh dự là ca sĩ có mặt trong buổi gặp gỡ giữa Tổng thống Mỹ với trí thức Việt Nam tại thủ đô.', 'http://img.f10.giaitri.vnecdn.net/2016/05/24/my-linh-2-4980-1464069032_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/my-linh-toi-hat-bang-ca-trai-tim-chao-don-tong-thong-obama-3408219.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('690', 'Tổng thống Obama là \'mọt sách\' như thế nào', 'N/a', '2016-05-24 23:42:07', '2016-05-24 11:30:25', 'Chính trị gia Mỹ lừng danh từng đọc hàng nghìn tác phẩm, trong đó \"Chuông nguyện hồn ai\", \"Đảo giấu vàng\"... là tiểu thuyết yêu thích của ông.', 'http://img.f12.giaitri.vnecdn.net/2016/05/24/topp-1464061781_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/sach/lang-van/tong-thong-obama-la-mot-sach-nhu-the-nao-3407767.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('691', 'Gu xem phim của Tổng thống Obama', 'N/a', '2016-05-24 23:42:07', '2016-05-24 05:00:00', 'Người đứng đầu Nhà Trắng Mỹ chuộng phim hình sự, viễn tưởng và phim ưa thích mới nhất của ông là series \"Game of Thrones\".', 'http://img.f10.giaitri.vnecdn.net/2016/05/23/doc-gu-dien-anh-cua-tong-thong-barack-obama-1463992313_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sau-man-anh/gu-xem-phim-cua-tong-thong-obama-3407723.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('692', 'Lý Hải: \'Tôi không dùng con cái để giữ chân vợ\'', 'N/a', '2016-05-24 23:42:07', '2016-05-24 00:07:00', 'Nam ca sĩ trân trọng sự hy sinh của bà xã khi bỏ qua tấm bằng thạc sĩ để hỗ trợ anh trong việc làm phim và chăm sóc con cái.', 'http://img.f11.giaitri.vnecdn.net/2016/05/23/ly-hai-6-2849-1463936410_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/ly-hai-toi-khong-dung-con-cai-de-giu-chan-vo-3407223.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('693', 'Thanh Hằng diện đồ hiệu đi chấm thi Next Top', 'N/a', '2016-05-24 23:42:07', '2016-05-24 15:11:37', 'Host của chương trình cùng dàn giám khảo gây chú ý khi xuất hiện trong ngày casting đầu tiên tại Hà Nội, trưa 24/5.', 'http://img.f12.giaitri.vnecdn.net/2016/05/24/thanhhangnexttop-1464076615_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/sao-dep-sao-xau/thanh-hang-dien-do-hieu-di-cham-thi-next-top-3408289.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('694', 'Gia đình Thúy Hạnh đùa nghịch với tranh 3D tại Singapore', 'N/a', '2016-05-24 23:42:07', '2016-05-24 14:12:22', 'Vợ chồng cựu người mẫu đưa hai con gái đi du lịch nhân dịp nghỉ hè.', 'http://img.f12.giaitri.vnecdn.net/2016/05/24/thuy-hanh-2-1357-1464059362-6180-1464061785_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/gia-dinh-thuy-hanh-dua-nghich-voi-tranh-3d-tai-singapore-3408075.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('695', 'Tặng độc giả quà từ bom tấn ‘X-Men: Apocalypse’', 'N/a', '2016-05-24 23:42:07', '2016-05-24 17:24:59', 'VnExpress tặng độc giả ở Hà Nội và TP HCM 12 món quà gồm USB, mũ, móc khóa, áo từ bộ phim đang “sốt” tại các rạp chiếu.', 'http://img.f12.giaitri.vnecdn.net/2016/05/24/page-1464079451_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/cong-dong/tang-doc-gia-qua-tu-bom-tan-x-men-apocalypse-3408341.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('696', 'Emma Watson lấp ló sau hoa hồng trong teaser \'Người đẹp và quái thú\'', 'N/a', '2016-05-24 23:42:07', '2016-05-24 13:55:47', 'Phiên bản mới của \"Beauty and the Beast\" hé lộ những hình ảnh đầu tiên trong giai điệu của ca khúc kinh điển cùng tên được phối lại.', 'http://img.f9.giaitri.vnecdn.net/2016/05/24/1328909310208764209957156770735072n-1464064882_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/emma-watson-lap-lo-sau-hoa-hong-trong-teaser-nguoi-dep-va-quai-thu-3408162.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('697', 'Bí quyết chọn trang sức đẹp như sao Hollywood', 'N/a', '2016-05-24 23:42:07', '2016-05-24 13:00:00', 'Ngoài phong thái tự tin, cách trang điểm tinh tế thì trang sức là bí quyết quan trọng giúp các ngôi sao Hollywood thêm nổi bật và quyến rũ.', 'http://img.f9.giaitri.vnecdn.net/2016/05/23/2055-6117-1463988072_180x108.png', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/lang-mot/bi-quyet-chon-trang-suc-dep-nhu-sao-hollywood-3407496.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('698', '\'Captain America: Civil War\' cán mốc một tỷ USD', 'N/a', '2016-05-24 23:42:07', '2016-05-24 11:09:17', 'Bom tấn \"Nội chiến siêu anh hùng\" của Marvel là phim đầu tiên có doanh thu vượt mốc tỷ USD sau gần một tháng ra mắt.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/top-5602-1464061650_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/captain-america-civil-war-can-moc-mot-ty-usd-3407754.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('699', 'Trương Nam Thành kể chuyện quay cảnh \'nóng\' ở thời tiết 10 độ', 'N/a', '2016-05-24 23:42:07', '2016-05-24 10:38:51', 'Trong phim \"Ma nữ báo thù\", cảnh nhạy cảm của nam người mẫu được quay tại vùng rừng núi trong thời tiết lạnh lẽo.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/truong-nam-thanh-1-1791-146405-9678-7829-1464061131_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/truong-nam-thanh-ke-chuyen-quay-canh-nong-o-thoi-tiet-10-do-3407898.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('700', 'Mỹ Linh sẽ hát Quốc ca Việt Nam chào đón Tổng thống Obama', 'N/a', '2016-05-24 23:42:07', '2016-05-24 09:59:30', 'Diva nhạc Việt đang ráo riết chuẩn bị cho tiết mục đặc biệt vào trưa 24/5 tại Hà Nội.', 'http://img.f11.giaitri.vnecdn.net/2016/05/24/top-1464057307_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/my-linh-se-hat-quoc-ca-viet-nam-chao-don-tong-thong-obama-3408043.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('701', 'Lý Nhã Kỳ gặp ông trùm Cannes trong ngày bế mạc', 'N/a', '2016-05-24 23:42:07', '2016-05-23 13:03:32', 'Người đẹp phối váy Couture màu xanh dương tôn vòng eo với lối trang điểm cổ điển.', 'http://img.f10.giaitri.vnecdn.net/2016/05/23/ly-nha-ky-gap-ong-trum-cannes-trong-ngay-be-mac-1463983792_180x108.jpg', 'http://giaitri.vnexpress.net/photo/trong-nuoc/ly-nha-ky-gap-ong-trum-cannes-trong-ngay-be-mac-3407608.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('702', 'Lý Nhã Kỳ vào top sao đẹp nhờ biến hóa kiểu trang điểm', 'N/a', '2016-05-24 23:42:07', '2016-05-23 16:16:01', 'Kiều nữ không bị lặp lại chính mình do luôn đổi phong cách từ nhẹ nhàng, nền nã tới quý phái, cổ điển.', 'http://img.f12.giaitri.vnecdn.net/2016/05/23/LyNhaKyt-1463991693-6975-1463993612_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/lam-dep/ly-nha-ky-vao-top-sao-dep-nho-bien-hoa-kieu-trang-diem-3407766.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('703', '10 lần gây bão của Van Gaal tại Man Utd', 'N/a', '2016-05-24 23:42:24', '2016-05-24 16:22:19', 'Trong hai mùa giải làm việc ở Man Utd, Louis Van Gaal là tâm điểm của truyền thông Anh với những hành động và phát ngôn gây chú ý.', 'http://img.f2.thethao.vnecdn.net/2016/05/24/03-28174241-440ded-2713884a-1464078266_180x108.jpg', 'http://thethao.vnexpress.net/photo/giai-ngoai-hang-anh/10-lan-gay-bao-cua-van-gaal-tai-man-utd-3408315.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('704', 'Obama và chuyện các Tổng thống Mỹ yêu thể thao', 'N/a', '2016-05-24 23:42:24', '2016-05-24 12:02:25', 'Barack Obama và nhiều đời Tổng thống Mỹ  trước đó đều là những người yêu thích thể thao, có nhiều đóng góp để phát triển thể thao tại Mỹ.', 'http://img.f2.thethao.vnecdn.net/2016/05/24/Obama-2-8523-1464065274-9262-1464066085_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-mon-khac/obama-va-chuyen-cac-tong-thong-my-yeu-the-thao-3408148.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('705', 'Chấm điểm triều đại Van Gaal tại Man Utd', 'N/a', '2016-05-24 23:42:24', '2016-05-24 08:17:21', 'Theo chuyên gia của tờ Daily Mail (Anh), nếu như HLV Van Gaal chỉ đạt 1 điểm về chuyển nhượng, 2 điểm cho lối chơi thì mức độ phát triển tài năng trẻ cho CLB lại đạt tới 8 điểm.', 'http://img.f1.thethao.vnecdn.net/2016/05/24/2-1464052482_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/cham-diem-trieu-dai-van-gaal-tai-man-utd-3407954.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('706', 'Công Vinh: ‘Nên rạch ròi giữa án phạt ở V-League và đội tuyển’', 'N/a', '2016-05-24 23:42:24', '2016-05-24 17:19:09', 'Chân sút đội tuyển Việt Nam không đồng tình với việc cầu thủ bị xử phạt tại V-League thì không được lên đội tuyển Việt Nam.', 'http://img.f4.thethao.vnecdn.net/2016/05/24/a0-4430-1464068292-2203-1464085150_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/cong-vinh-nen-rach-roi-giua-an-phat-o-v-league-va-doi-tuyen-3408218.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('707', 'Trụ cột Man Utd chỉ trích quyết định sa thải Van Gaal', 'N/a', '2016-05-24 23:42:24', '2016-05-24 15:02:54', 'Daley Blind không giấu sửng sốt và thất vọng khi ông thầy mà anh kính ngưỡng phải rời Old Trafford.', 'http://img.f1.thethao.vnecdn.net/2016/05/24/Blind-8128-1464076744_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/tru-cot-man-utd-chi-trich-quyet-dinh-sa-thai-van-gaal-3408278.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('708', 'Ronaldo khổ luyện trước chung kết Champions League', 'N/a', '2016-05-24 23:42:24', '2016-05-24 14:53:22', 'Tiền đạo của Real Madrid tranh thủ ngày nghỉ để tăng cường thể lực, trước trận đại chiến với Atletico Madrid cuối tuần này. ', 'http://img.f2.thethao.vnecdn.net/2016/05/24/a-1464076297_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/ronaldo-kho-luyen-truoc-chung-ket-champions-league-3408298.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('709', 'Cầu thủ gốc Việt: \'Bố mẹ bật khóc vì tôi được khoác áo đội tuyển\'', 'N/a', '2016-05-24 23:42:24', '2016-05-24 11:47:20', 'Đặng Văn Lâm chia sẻ rằng việc được khoác áo tuyển Việt Nam là giấc mơ lớn thành hiện thực đối với gia đình anh.', 'http://img.f1.thethao.vnecdn.net/2016/05/24/a11-7933-1464064843_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/bong-da-trong-nuoc/cau-thu-goc-viet-bo-me-bat-khoc-vi-toi-duoc-khoac-ao-doi-tuyen-3408203.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('710', 'HLV Hữu Thắng cho tuyển thủ chơi đuổi bắt, dùng bóng ném nhau', 'N/a', '2016-05-24 23:42:24', '2016-05-24 11:24:45', 'Sáng 24/5, tuyển Việt Nam tập buổi đầu tiên chuẩn bị cho trận giao hữu với Syria và giải tứ hùng tại Myanmar.', 'http://img.f4.thethao.vnecdn.net/2016/05/24/a12-1464062287_180x108.jpg', 'http://thethao.vnexpress.net/photo/bong-da-trong-nuoc/hlv-huu-thang-cho-tuyen-thu-choi-duoi-bat-dung-bong-nem-nhau-3408150.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('711', 'Các danh thủ Man Utd e ngại tính cách và lối chơi của Mourinho', 'N/a', '2016-05-24 23:42:24', '2016-05-24 11:16:21', 'Peter Schmeichel cho rằng ban lãnh đạo Man Utd đã phải cân nhắc rất nhiều mới bổ nhiệm Jose Mourinho, trong khi Phil Neville tin HLV Bồ Đào Nha có thể đưa danh hiệu Ngoại hạng Anh trở lại Old Trafford.', 'http://img.f1.thethao.vnecdn.net/2016/05/24/1-1464063245_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/cac-danh-thu-man-utd-e-ngai-tinh-cach-va-loi-choi-cua-mourinho-3408159.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('712', 'Italy gạt Pirlo, Balotelli khỏi Euro 2016', 'N/a', '2016-05-24 23:42:24', '2016-05-24 10:43:37', '30 cái tên được HLV Antonio Conte đăng ký vào danh sách sơ bộ, nhưng trong đó không có hai nhân vật quan trọng nhất khi Italy về nhì ở Euro 2012.', 'http://img.f1.thethao.vnecdn.net/2016/05/24/top-7002-1464060559_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/cac-giai-khac/italy-gat-pirlo-balotelli-khoi-euro-2016-3408102.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('713', 'Roland Garros thắt chặt an ninh để tránh khủng bố', 'N/a', '2016-05-24 23:42:24', '2016-05-24 17:09:29', 'Những con đường xung quanh khu vực diễn ra giải Grand Slam thứ hai trong năm bị phong tỏa và thủ tục kiểm tra an ninh trước khi vào sân khắt khe hơn.', 'http://img.f2.thethao.vnecdn.net/2016/05/24/Untitled-11-8431-1464084052_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/roland-garros-that-chat-an-ninh-de-tranh-khung-bo-3408436.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('714', 'Simona Halep bị chê bai vì trang phục kiểu ngựa vằn', 'N/a', '2016-05-24 23:42:24', '2016-05-24 16:14:20', 'Tay vợt nữ số sáu thế giới khiến các khán giả của Roland Garros bàn tán vì phong cách thời trang khác thường', 'http://img.f4.thethao.vnecdn.net/2016/05/24/Untitled-2-1348-1464080600_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tennis/simona-halep-bi-che-bai-vi-trang-phuc-kieu-ngua-van-3408372.html', '6', '5', '0', '0', '1', null, '57');
INSERT INTO `posts` VALUES ('715', 'Người đàn ông trung niên hạ sát vợ lúc nửa đêm', 'N/a', '2016-05-24 23:42:36', '2016-05-24 17:57:28', 'Trong lúc to tiếng vì nghi vợ ngoại tình, ông Sơn đâm hai nhát dao khiến bà này tử vong, con dâu không kịp can ngăn.', 'http://img.f30.vnecdn.net/2016/05/24/vo-1464086072_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/nguoi-dan-ong-trung-nien-ha-sat-vo-luc-nua-dem-3408461.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('716', 'Dí kiếm, bắt chủ trang trại quỳ van xin vì nghi nói xấu', 'N/a', '2016-05-24 23:42:36', '2016-05-24 17:49:16', 'Chém người làm công, Quân cầm kiếm uy hiếp ông chủ trang trại bắt quỳ xin lỗi vì trước đó đã nói xấu mình', 'http://img.f32.vnecdn.net/2016/05/24/image-1464082112_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/di-kiem-bat-chu-trang-trai-quy-van-xin-vi-nghi-noi-xau-3408404.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('717', 'Cầm gậy sắt đánh người yêu khi bị từ chối hẹn hò', 'N/a', '2016-05-24 23:42:36', '2016-05-24 17:36:53', 'Hai lần trong một buổi chiều không thuyết phục được bạn gái đi chơi cùng, Đức cầm gậy sắt rình bên đường, đánh cô này gãy 4 chiếc răng.', 'http://img.f31.vnecdn.net/2016/05/24/105d2145330t7469l12-1464084338_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/cam-gay-sat-danh-nguoi-yeu-khi-bi-tu-choi-hen-ho-3408443.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('718', '\'Kiều nữ\' mang vàng giả đi cầm cố', 'N/a', '2016-05-24 23:42:36', '2016-05-24 16:14:29', 'Trong vai vợ chồng son túng thiếu phải mang quà cưới là nữ trang vàng đi cầm cố, Diễm và Tuấn đã lừa 8 tiệm vàng, chiếm đoạt hàng trăm triệu đồng.', 'http://img.f32.vnecdn.net/2016/05/24/vanggia3-1464080937_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/kieu-nu-mang-vang-gia-di-cam-co-3408384.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('719', 'Nhiều phụ nữ tiếp tục sập bẫy quà hàng hiệu của \'người tình ảo\'', 'N/a', '2016-05-24 23:42:36', '2016-05-24 14:51:57', 'Chưa đầy 2 tháng, Công an Quảng Ngãi đã phong tỏa tới 2,5 tỷ đồng do những phụ nữ tại đây chuyển vào tài khoản theo lời đường mật nhóm \"trai Tây\" quen qua mạng.', 'http://img.f30.vnecdn.net/2016/05/24/anhluadao-1464064118_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/nhieu-phu-nu-tiep-tuc-sap-bay-qua-hang-hieu-cua-nguoi-tinh-ao-3408188.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('720', '\'Qua mặt\' công an khi dùng tiền ảo chơi game mua bán ma túy', 'N/a', '2016-05-24 23:42:36', '2016-05-24 14:12:12', 'Bên bán không nhận tiền mặt mà yêu cầu người mua quy đổi ra tiền ảo Vcoin, nạp vào tài khoản chơi game.', 'http://img.f32.vnecdn.net/2016/05/24/matuy-1464061445_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/qua-mat-cong-an-khi-dung-tien-ao-choi-game-mua-ban-ma-tuy-3408118.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('721', 'Nữ Việt kiều gian nan đòi căn nhà nhờ chú ruột đứng tên', 'N/a', '2016-05-24 23:42:36', '2016-05-24 12:08:17', '24 năm từ khi đưa vàng cho chú ruột mua giúp ngôi nhà ở quận Cầu Giấy (Hà Nội) và thay mặt đứng tên, bà Việt kiều 59 tuổi mới được tuyên \"nhận nhà\" sau hơn 10 năm đâm đơn khởi kiện.', 'http://img.f32.vnecdn.net/2016/05/24/minhhoadoinha-1464058124_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/nu-viet-kieu-gian-nan-doi-can-nha-nho-chu-ruot-dung-ten-3408065.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('722', 'An ninh TP HCM được siết chặt trước giờ đón Tổng thống Mỹ Obama', 'N/a', '2016-05-24 23:42:36', '2016-05-24 11:22:58', 'Lực lượng cảnh sát, an ninh của Việt Nam và Mỹ chia nhau kiểm soát gắt gao khu vực trung tâm Sài Gòn, các khách sạn Tổng thống Mỹ và phái đoàn ở.', 'http://img.f30.vnecdn.net/2016/05/24/obama-2-3546-1464069952-8415-1464070013_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/an-ninh-tp-hcm-duoc-siet-chat-truoc-gio-don-tong-thong-my-obama-3408079.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('723', 'Công an được bắt người trong những trường hợp nào từ ngày 1/7', 'N/a', '2016-05-24 23:42:36', '2016-05-24 07:45:49', 'Bộ luật Tố tụng Hình sự 2015 quy định không được bắt người vào ban đêm, trừ trường hợp phạm tội quả tang hoặc bắt người đang bị truy nã.', 'http://img.f29.vnecdn.net/2016/05/24/conganbatnguoi-1464050807_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/tu-van/cong-an-duoc-bat-nguoi-trong-nhung-truong-hop-nao-tu-ngay-1-7-3406777.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('724', 'Bán ma túy đá với giá 28 triệu đồng một lạng', 'N/a', '2016-05-24 23:42:36', '2016-05-24 13:58:59', 'Vừa mang 700g ma túy đá vào nhà nghỉ giao cho khách, Thành và Tiến bị công an bắt quả tang.', 'http://img.f31.vnecdn.net/2016/05/24/20nammatuyJPG-1464066600_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/ban-ma-tuy-da-voi-gia-28-trieu-dong-mot-lang-3408216.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('725', 'Chưa ngã ngũ vụ án nghi can 17 tuổi bị đánh chết trong buồng giam', 'N/a', '2016-05-24 23:42:36', '2016-05-24 13:03:33', 'Chi phí điều trị những ngày cuối đời của nghi can Đỗ Đăng Dư sau khi bị bạn cùng buồng giam hành hung chưa được làm rõ có phải do công an bỏ tiền ra hay không.', 'http://img.f29.vnecdn.net/2016/05/24/binh1-1464065206_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/chua-nga-ngu-vu-an-nghi-can-17-tuoi-bi-danh-chet-trong-buong-giam-3408207.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('726', 'Con rể chém người để \'rửa hận\' cho bố vợ', 'N/a', '2016-05-24 23:42:36', '2016-05-24 10:47:22', 'Cùng bạn mang theo 2 mã tấu, Vũ rình trước cửa nhà ông Quýt rồi lao vào chém gục gia chủ.', 'http://img.f31.vnecdn.net/2016/05/24/Bonbicaotaitoa2-1464059670_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/con-re-chem-nguoi-de-rua-han-cho-bo-vo-3408084.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('727', 'Trùm ma túy và tình nhân bị bắt trên ôtô có súng, tiền tỷ', 'N/a', '2016-05-24 23:42:36', '2016-05-23 15:53:03', 'Giấu nhiều kg \"hàng đá\" trên ôtô, Hùng bị bắt khi thủ súng giảm thanh trong người cùng 50.000 USD, 2 tỷ đồng...', 'http://img.f32.vnecdn.net/2016/05/23/Hung-1463991187_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/trum-ma-tuy-va-tinh-nhan-bi-bat-tren-oto-co-sung-tien-ty-3407721.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('728', 'Hàng trăm cảnh sát đặc nhiệm bảo vệ Tổng thống Mỹ ở Hà Nội', 'N/a', '2016-05-24 23:42:36', '2016-05-22 19:52:16', 'Ngoài 100 cảnh sát dẫn đoàn và phân luồng giao thông, 80 cảnh sát cơ động đặc nhiệm cũng trực chiến, sẵn sàng đối phó tình huống bất ngờ khi đoàn Tổng thống Mỹ tới Hà Nội đêm nay.', 'http://img.f29.vnecdn.net/2016/05/22/csdn-1866-1463918277_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/hang-tram-canh-sat-dac-nhiem-bao-ve-tong-thong-my-o-ha-noi-3407201.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('729', 'ĐKVĐ Australian Open, Kerber dừng bước sớm tại Roland Garros', 'N/a', '2016-05-24 23:42:50', '2016-05-24 21:21:24', 'Ngược dòng bất thành, nhà ĐKVĐ Australian Open, Angelique Kerber đã bị tay vợt ít tên tuổi Kiki Bertens người Hà Lan loại ngay ở trận đầu tiên tại Roland Garros.', 'http://img.v97.bdpcdn.net/Assets/Media/2016/05/24/18/angelique-kerber-480.jpg', 'http://bongdaplus.vn/tin-bai/95/154824/dkvd-australian-open-kerber-dung-buoc-som-tai-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('730', 'Nadal thắng hủy diệt, Murray vất vả vào vòng 2 Roland Garros', 'N/a', '2016-05-24 23:42:50', '2016-05-24 20:49:13', 'Sự chênh lệch đẳng cấp giữa đối thủ với Nadal là thấy rõ nhưng chiến thắng áp đảo chỉ sau 3 set với cùng tỷ số 6-1 lại khiến người hâm mộ ngạc nhiên về phong độ của \"Vua sân đất nện\".', 'http://img.v97.bdpcdn.net/Assets/Media/2016/05/24/18/rafa-480.jpg', 'http://bongdaplus.vn/tin-bai/95/154823/nadal-thang-huy-diet-murray-vat-va-vao-vong-2-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('731', 'Djokovic diễn trò hề tại Pháp mở rộng', 'N/a', '2016-05-24 23:42:50', '2016-05-24 13:55:36', 'Ở buổi tập trước trận ra quân tại Roland Garros, xuất hiện với chiếc mũ beret và bộ ria mép ngộ nghĩnh, nhìn tay vợt số 1 thế giới Djokovic trông giống như một chú hề trên sân đất nện.', 'http://img.v97.bdpcdn.net/Assets/Media/2016/05/24/18/djokovic-480.jpg', 'http://bongdaplus.vn/tin-bai/95/154798/djokovic-dien-tro-he-tai-phap-mo-rong.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('732', 'Nadal sẽ công bố kết quả thử doping', 'N/a', '2016-05-24 23:42:50', '2016-05-24 09:37:50', 'Trả lời phỏng vấn mới đây của tờ L\"Equipe, tay vợt người Tây Ban Nha Rafael Nadal cho biết sẽ sớm công bố kết quả các mẫu thử doping của mình.', 'http://img.v97.bdpcdn.net/Assets/Media/2016/05/24/18/nadal-480.jpg', 'http://bongdaplus.vn/tin-bai/95/154774/nadal-se-cong-bo-ket-qua-thu-doping.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('733', 'Wawrinka thoát hiểm trận đầu tại Roland Garros', 'N/a', '2016-05-24 23:42:50', '2016-05-24 04:41:56', 'Sau 3 giờ 11 phút và phải mất đến 5 set đấu, nhà ĐKVĐ Roland Garros, Stan Wawrinka mới giành chiến thắng chung cuộc trước Lukas Rosol người Czech sau khi bất ngờ thua set đầu tiên.', 'http://img.v97.bdpcdn.net/Assets/Media/2016/05/24/18/wawrinka-480.jpg', 'http://bongdaplus.vn/tin-bai/95/154734/wawrinka-thoat-hiem-tran-dau-tai-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('734', 'Á đù', '<p>Chẳng c&oacute; c&aacute;i quần qu&egrave; g&igrave; hết ak</p>\r\n', '2016-05-29 20:07:00', '2016-05-25 00:29:17', 'Á đù là? Nếu bạn thắc mắc hãy vào xem câu trả lời nhóe...', '/resources/upload/images/6f9a4f96f503d10a6b8d040901935287.jpg', 'A-du', '6', '11', '1', '0', '2', 'baotoan.95@gmail.com', null);
INSERT INTO `posts` VALUES ('735', 'Serena Williams đối đầu Muguruza ở chung kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-04 05:17:21', 'Serena Williams chỉ cần 2 set đấu để vượt qua tay vợt xếp hạng 58 thế giới Kiki Bertens ở bán kết Roland Garros. Đối thủ của cô trong trận chung kết sẽ là Garbine Muguruza.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/04/63/serena.jpg', 'http://bongdaplus.vn/tin-bai/95/155901/serena-williams-doi-dau-muguruza-o-chung-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('736', 'Murray lần đầu tiên vào chung kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-03 22:09:42', 'Xuất sắc hạ gục nhà ĐKVĐ của giải Stan Wawrinka lần lượt với tỷ số sau 4 set đấu là 6-4 6-2 4-6 6-2, Murray lần đầu tiên giành quyền vào chung kết và sẽ chạm trán tay vợt số 1 thế giới, Novak Djokovic.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/03/18/murray-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155831/murray-lan-dau-tien-vao-chung-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('737', 'Djokovic hẹn tái ngộ Wawrinka ở chung kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-03 20:56:40', 'Chỉ phải gặp đối thủ kém mình tới 15 bậc trên BXH nên không khó khăn mấy cho tay vợt số 1 thế giới, Novak Djokovic giành chiến thắng để lần thứ 3 liên tiếp vào chung kết Roland Garros.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/03/18/djokovic-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155829/djokovic-hen-tai-ngo-wawrinka-o-chung-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('738', 'Federer tái xuất tại Stuttgart tuần tới', 'N/a', '2016-06-04 16:49:05', '2016-06-03 10:04:16', 'Ban tổ chức Stuttgart Open, giải đấu trên mặt sân cỏ ở Stuttgart (Đức) vừa thông báo, Roger Federer sẽ trở lại thi đấu sau thời gian nghỉ đưỡng thương với sự góp mặt tại giải đấu này vào tuần sau.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/03/18/federer-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155790/federer-tai-xuat-tai-stuttgart-tuan-toi.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('739', 'Djokovic lần thứ 8 vào bán kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-02 21:09:21', 'Chỉ cần 3 set đấu, Djokovic đã đánh bại được tay vợt hạt giống số 7, Tomas Berdych với các tỷ số là 6-3 7-5 6-3. Đây là lần thứ 8 Nole vào bán kết Roland Garros.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/02/63/djokovic.jpg', 'http://bongdaplus.vn/tin-bai/95/155742/djokovic-lan-thu-8-vao-ban-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('740', 'Nadal bỏ Aegon Championships trước thềm Wimbledon', 'N/a', '2016-06-04 16:49:05', '2016-06-02 09:11:18', 'Chấn thương cổ tay vừa qua đã khiến “Vua sân đất nện” Rafael Nadal phải rút khỏi Roland Garros ngay trước thềm vòng 3, và tình hình diễn biến đã nghiêm trọng hơn dự đoán ban đầu.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/02/18/nadal-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155672/nadal-bo-aegon-championships-truoc-them-wimbledon.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('741', 'Murray chạm trán nhà ĐKVĐ Wawrinka ở bán kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-01 22:48:44', 'Tuy chỉ gặp đối thủ không xếp hạng hạt giống Ramos-Vinolas, nhưng phải sau xấp xỉ 2 giờ đồng hồ thì nhà ĐKVĐ của giải, Stan Wawrinka mới giành vé vào bán kết Roland Garros và sẽ đối đầu hạt giống số 2, Andy Murray.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/01/18/murray-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155636/murray-cham-tran-nha-dkvd-wawrinka-o-ban-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('742', 'Djokovic vượt ngưỡng 100 triệu USD tiền thưởng', 'N/a', '2016-06-04 16:49:05', '2016-06-01 22:23:47', 'Chiến thắng nhọc nhằn trước Roberto Bautista Agut không chỉ mang lại tấm vé vào tứ kết Roland Garros cho Djokovic, mà còn giúp anh xác lập kỷ lục trở thành tay vợt đầu tiên trong lịch sử kiếm được hơn 100 triệu USD tiền thưởng.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/01/18/djokovic-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155635/djokovic-vuot-nguong-100-trieu-usd-tien-thuong.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('743', 'Djokovic, Serena Williams cùng vào tứ kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-06-01 20:31:29', 'Phải mất tới 2 ngày, Novak Djokovic mới giành chiến thắng trước tay vợt Bautista Agut. Trong khi đó, Serena Williams dễ dàng vượt qua hạt giống số 18 Svitolina với cùng tỷ số 6-1.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/01/63/djokovic.jpg', 'http://bongdaplus.vn/tin-bai/95/155629/djokovic-serena-williams-cung-vao-tu-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('744', 'Roland Garros xáo trộn lịch thi đấu vì thời tiết xấu', 'N/a', '2016-06-04 16:49:05', '2016-06-01 09:10:37', 'Roland Garros là giải duy nhất trong hệ thống 4 giải Grand Slam không có sân đấu có mái che và do vậy thời tiết mưa nhiều sẽ làm cho các trận đấu liên tục bị gián đoạn.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/01/18/rg-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155575/roland-garros-xao-tron-lich-thi-dau-vi-thoi-tiet-xau.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('745', 'Radwanska và Halep bị loại, Roland Garros lại hoãn thi đấu', 'N/a', '2016-06-04 16:49:05', '2016-05-31 22:04:28', 'Nắng mưa thất thường tại Paris trong ngày thi đấu thứ 10 tiếp tục làm cho các trận đấu đang diễn ra bị gián đoạn. Sau khi trở lại sân thi đấu, đã có tới 2 cú sốc xảy ra ở nội dung đơn nữ.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/31/18/radwanska-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155529/radwanska-va-halep-bi-loai-roland-garros-lai-hoan-thi-dau.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('746', 'Sharapova cặp kè trai lạ ở Paris', 'N/a', '2016-06-04 16:49:05', '2016-05-31 13:57:50', 'Người ta đã bắt gặp “Búp bê Nga” đang tay trong tay với người mẫu nam khá nổi tiếng của Tây Ban Nha, Andres Velencoso tại thủ đô Paris, nơi đang diễn ra Roland Garros mà Masha không thể tham dự vì lệnh cấm thi đấu.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/31/18/shaparova-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155484/sharapova-cap-ke-trai-la-o-paris.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('747', 'Federer nâng tạ, Serena Williams thân mật đối thủ', 'N/a', '2016-06-04 16:49:05', '2016-05-31 10:04:28', 'Dưới đây là 10 hình ảnh ấn tượng nhất trong tuần đầu tiên diễn ra Roland Garros.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/31/18/rg-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155463/federer-nang-ta-serena-williams-than-mat-doi-thu.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('748', 'Roland Garros hoãn tất cả các trận đấu ngày thứ 9 vì mưa', 'N/a', '2016-06-04 16:49:05', '2016-05-30 19:44:41', 'Ban tổ chức giải Pháp mở rộng vừa ra thông báo tạm hoãn tất cả các trận thi đấu trong ngày hôm nay (30/5) vì lý do mưa lớn.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/30/18/rg-4801.jpg', 'http://bongdaplus.vn/tin-bai/95/155408/roland-garros-hoan-tat-ca-cac-tran-dau-ngay-thu-9-vi-mua.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('749', 'Wawrinka nhận lời thách đấu của cậu bé nhặt bóng', 'N/a', '2016-06-04 16:49:05', '2016-05-30 09:29:39', 'Trong khoảng thời gian đối thủ Viktor Troicki đang được chăm sóc y tế vì bị đau hông, nhà ĐKVĐ Roland Garros, Stan Wawrinka xách vợt ra sân đấu với cậu bé nhặt bóng.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/30/18/wawrinka-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155354/wawrinka-nhan-loi-thach-dau-cua-cau-be-nhat-bong.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('750', 'Radwanska cùng đối thủ ‘vật lộn’ trên sân đấu Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-05-30 08:55:20', 'Thi đấu trên mặt sân đất nện cũng tạo nên khá nhiều tình huống hài hước. Một trong số đó là pha bóng cả hai tay vợt nữ đều ngã trượt xuống sân nhưng vẫn đôi công hấp dẫn.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/30/18/radwanska-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155349/radwanska-cung-doi-thu-vat-lon-tren-san-dau-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('751', 'Hạ gục Isner, Murray tiến vào tứ kết Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-05-30 06:48:48', 'Chỉ mất 3 set đấu, Andy Murray đã đánh bại tay vợt xếp hạng 17 thế giới, John Isner tại vòng 4 Roland Garros. Qua đó, bước tiếp vào tứ kết, đối thủ của Murray là Richard Gasquet.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/30/63/murray-1.jpg', 'http://bongdaplus.vn/tin-bai/95/155329/ha-guc-isner-murray-tien-vao-tu-ket-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('752', 'Nishikori, Raonic bất ngờ bị loại ở vòng 4 Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-05-29 22:38:00', 'Bất ngờ lớn đã xảy ra ở vòng 4 Roland Garros khi hai tay vợt hạt giống là Kei Nishikori (số 5) và Milos Raonic (số 8) đều bị loại trước đối thủ thứ hạng thấp hơn mình.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/29/63/raonic.jpg', 'http://bongdaplus.vn/tin-bai/95/155312/nishikori-raonic-bat-ngo-bi-loai-o-vong-4-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('753', 'Djokovic tiến vào vòng 4 Roland Garros mà chưa thua set nào', 'N/a', '2016-06-04 16:49:05', '2016-05-29 02:16:40', 'Trước đối thủ kém xa về đẳng cấp, không mấy khó khăn để hạt giống số 1 người Serbia, Novak Djokovic kết thúc trận đấu chỉ sau xấp xỉ 2 giờ đồng hồ, qua đó giành vé đi tiếp vào vòng 4.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/29/18/djokovic-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155229/djokovic-tien-vao-vong-4-roland-garros-ma-chua-thua-set-nao.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('754', 'Serena Williams vất vả đi tiếp ở Roland Garros', 'N/a', '2016-06-04 16:49:05', '2016-05-28 23:17:38', 'Sau khi thi đấu trở lại do cơn mưa làm gián đoạn, hạt giống số 1 Serena Williams phải tốn khá nhiều sức mới đánh bại được đối thủ ở loạt tie-break kéo dài tới tận tỷ số 12-10.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/05/28/18/serena-williams-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155227/serena-williams-vat-va-di-tiep-o-roland-garros.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('755', 'Ông Đinh La Thăng: \'Giáo dục đại học hướng tới chất lượng con người\'', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:43:14', 'Bí thư Thành ủy TP HCM khẳng định giáo dục đại học phải lấy chất lượng con người làm mục tiêu cao nhất mới có được những cá nhân có năng lực, nhân cách.', 'http://img.f32.vnecdn.net/2016/06/04/ongThang-1465032799_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ong-dinh-la-thang-giao-duc-dai-hoc-huong-toi-chat-luong-con-nguoi-3414353.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('756', 'Vietnam\'s gasoline price climbs to six-month high', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:34:56', 'Vietnam’s top oil importer and distributor Petrolimex Corp. on Saturday, June 4, raised retail price for RON 92 gasoline by more than 4 percent, the highest rate since December last year.', 'http://img.f46.english.vnecdn.net/2016/06/04/xang1aq50064711458037142-14650-4421-6338-1465013389_180x108.jpg', 'http://e.vnexpress.net/news/business/markets/vietnam-s-gasoline-price-climbs-to-six-month-high-3414266.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('757', 'Trung Quốc phản pháo cảnh báo \'tự cô lập\' của Mỹ', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:31:45', 'Một quan chức hải quân cao cấp Trung Quốc hôm nay nói rằng Mỹ đang cố gắng cô lập nước này, phản pháo lại bình luận của bộ trưởng quốc phòng Mỹ.', 'http://img.f29.vnecdn.net/2016/06/04/BL1804GUAN2624020f-1465032821_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/trung-quoc-phan-phao-canh-bao-tu-co-lap-cua-my-3414343.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('758', 'Sức mạnh của hệ thống tên lửa Triều Tiên', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:21:00', 'Triều Tiên liên tục tiến hành phóng Musudan, tên lửa tầm trung có vai trò then chốt trong kho tên lửa nhiều loại của nước này. Tuy nhiên các vụ thử gần đây đều thất bại.', 'http://img.f29.vnecdn.net/2016/06/04/tam-hoat-dong-cua-ten-lua-trieu-tien-1465013018_180x108.jpg', 'http://vnexpress.net/infographics/tu-lieu/suc-manh-cua-he-thong-ten-lua-trieu-tien-3414263.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('759', 'Công Vinh có đáng bị anti fan dè bỉu khi liên tục ghi bàn, phá kỷ lục?', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:11:42', 'Bước sang tuổi 31 với hơn chục năm cống hiến cho ĐTQG Việt Nam và sở hữu chuỗi kỷ lục \"vô tiền khoáng hậu\" nhưng Công Vinh vẫn luôn phải hứng chịu những chỉ trích thậm tệ.', 'http://img.f29.vnecdn.net/2016/06/04/cvkhoc1464955440667mru3t1e2sjf27eu5m8i2mktn7i1q6ipr-1465013722_1465013777_180x108.jpg', 'http://vnexpress.net/tin-tuc/cong-dong/cong-vinh-co-dang-bi-anti-fan-de-biu-khi-lien-tuc-ghi-ban-pha-ky-luc-3414280.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('760', 'TP HCM chuyển giao kỹ thuật cắt thanh quản chữa ung thư cho Nghệ An', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:06:12', 'Các bác sĩ Bệnh viện Chợ Rẫy vừa chuyển giao kỹ thuật cắt thanh quản cho đồng nghiệp Bệnh viện Đa khoa Hữu nghị Nghệ An để giúp giải quyết những bệnh lý ung thư về thanh quản.', 'http://img.f44.suckhoe.vnecdn.net/2016/06/04/image003-7766-1465009751_180x108.jpg', 'http://suckhoe.vnexpress.net/tin-tuc/suc-khoe/tp-hcm-chuyen-giao-ky-thuat-cat-thanh-quan-chua-ung-thu-cho-nghe-an-3414188.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('761', 'Quán cà phê cảnh báo tai nạn giao thông ở Đà Nẵng', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:03:00', 'Day dứt vì những vụ tai nạn xe cộ đau lòng, một nhiếp ảnh gia ở thành phố sông Hàn đã mở quán cà phê với hàng trăm biển báo và phụ tùng ô tô, xe máy để tuyên truyền về an toàn giao thông.', 'http://img.f35.dulich.vnecdn.net/2016/06/01/bien-bao-9-1464770413_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/quan-ca-phe-canh-bao-tai-nan-giao-thong-o-da-nang-3412577.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('762', 'Nhìn lưỡi đoán bệnh', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:02:40', 'Lưỡi mịn, nhạt màu cho thấy cơ thể thiếu B12 hoặc sắt; còn lưỡi xuất hiện vết nứt chứng tỏ bạn đang bị rối loạn miễn dịch.', 'http://img.f41.suckhoe.vnecdn.net/2016/06/04/tongue0-1465007979-6185-1465007990_180x108.jpg', 'http://suckhoe.vnexpress.net/tin-tuc/cac-benh/nhin-luoi-doan-benh-3414185.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('763', 'Cô bé kiên quyết đếm thiếu mặc bố mẹ mỏi miệng dạy', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:00:00', 'Cô bé người Mỹ nhất quyết không chịu đếm số 4 khiến ông bố giáo viên \'chán chẳng buồn nói\'.', 'http://img.f14.giadinh.vnecdn.net/2016/06/04/my-2231-1465018035_180x108.jpg', 'http://giadinh.vnexpress.net/tin-tuc/to-am/co-be-kien-quyet-dem-thieu-mac-bo-me-moi-mieng-day-3414324.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('764', 'Cô bé nhất quyết đếm thiếu dù bố mẹ mỏi tay dạy', 'N/a', '2016-06-04 16:49:29', '2016-06-04 16:00:00', 'Đoạn video ghi lại cảnh bé gái 2 tuổi người Mỹ, Aaliyah, tập đếm số từ 1 đến 5, nhưng luôn bỏ qua số 4, đang gây sốt khắp mạng xã hội.', 'http://news.img.video.vnecdn.net/web/2016/06/04/co-be-nhat-quyet-dem-so-1235-du-bo-me-moi-tay-day-dem-1465018125_180x108.jpg', 'http://video.vnexpress.net/gia-dinh/co-be-nhat-quyet-dem-thieu-du-bo-me-moi-tay-day-3414326.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('765', 'Thủ tướng yêu cầu chấm dứt tư duy \'Hà Nội không vội được đâu\'', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:59:37', 'Người đứng đầu Chính phủ yêu cầu lãnh đạo Hà Nội phải thực sự cầu thị, chấm dứt lối tư duy, hành xử cũ của cán bộ - công chức, coi hỗ trợ doanh nghiệp là nhiệm vụ ưu tiên để phát triển Thủ đô.', 'http://img.f30.vnecdn.net/2016/06/04/Thu-tuong-du-hoi-nghi-Ha-Noi-6-9238-5569-1465030765_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/thu-tuong-yeu-cau-cham-dut-tu-duy-ha-noi-khong-voi-duoc-dau-3414327.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('766', 'Vietnam’s gifted schools: The juvenile war', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:50:51', 'Yesterday, a school for gifted students in Hanoi opened for enrolment and the number of applicants stood at about 10 times the school’s capacity, 480, meaning for each student accepted 48 fail. Though it\'s not news, these battles have become a standard dinner table discussion.', 'http://img.f46.english.vnecdn.net/2016/06/03/PNTA5204-1464839955-1200x0-5425-1464957751_180x108.jpg', 'http://e.vnexpress.net/news/life/vietnam-s-gifted-schools-the-juvenile-war-3413991.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('767', 'Tìm ra lý do thật sự khiến phụ nữ không đạt cực khoái', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:50:15', 'Một nghiên cứu mới tại Mỹ cho thấy cực khoái ở người phụ nữ thực sự phụ thuộc rất nhiều vào cấu tạo giải phẫu của cơ thể. ', 'http://img.f44.suckhoe.vnecdn.net/2016/06/04/shutterstock297070292-1465014299_180x108.jpg', 'http://suckhoe.vnexpress.net/tin-tuc/suc-khoe/tim-ra-ly-do-that-su-khien-phu-nu-khong-dat-cuc-khoai-3414260.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('768', 'Tự truyện của Ancelotti: Chỉ là tay mơ khi vào nghề HLV', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:48:30', 'Là ngôi sao sáng bậc nhất thế hệ cầu thủ của mình nhưng Carlo Ancelotti cũng gặp không ít gian truân khi chuyển sang nghiệp cầm quân, theo chia sẻ trong sách \"Lãnh đạo tĩnh lặng\" mà ông vừa cho ra mắt.', 'http://img.f3.thethao.vnecdn.net/2016/06/04/a-1465030244_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/hau-truong/tu-truyen-cua-ancelotti-chi-la-tay-mo-khi-vao-nghe-hlv-3414146.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('769', 'Vietnam targets 90 pct health insurance coverage by 2020', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:31:40', 'Vietnam has set out a new goal in welfare development, aiming for 90 percent of the population to have health insurance by 2020, according to the government portal.', 'http://img.f46.english.vnecdn.net/2016/06/04/phuc-1465015657-9838-1465015695_1465015714_180x108.jpg', 'http://e.vnexpress.net/news/news/vietnam-targets-90-pct-health-insurance-coverage-by-2020-3414302.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('770', 'Canada bức xúc vì ngoại trưởng Trung Quốc nổi cáu với phóng viên', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:22:15', 'Canada bày tỏ sự không hài lòng với Trung Quốc, sau khi ngoại trưởng nước này nổi cáu với một nhà báo Canada vì câu hỏi về nhân quyền.', 'http://img.f29.vnecdn.net/2016/06/04/1464901646583-1465028238_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/canada-buc-xuc-vi-ngoai-truong-trung-quoc-noi-cau-voi-phong-vien-3414338.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('771', 'Vietnamese retailers struggle against foreign rivals', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:00:24', 'Vietnamese companies are combining forces to avoid the prospect of losing the home retail market to foreign rivals.', 'http://img.f45.english.vnecdn.net/2016/06/04/a6-1465015570-3565-1465015692_180x108.jpg', 'http://e.vnexpress.net/news/business/vietnamese-retailers-struggle-against-foreign-rivals-3414301.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('772', 'Khách Trung Quốc chụp ảnh cưới giữa Paris ngập lụt', 'N/a', '2016-06-04 16:49:29', '2016-06-04 15:00:00', 'Bất chấp trận lụt tồi tệ nhất trong lịch sử hơn 100 năm qua, hai du khách Trung Quốc cùng ekip vẫn thực hiện bộ ảnh cưới tại Paris.', 'http://img.f34.dulich.vnecdn.net/2016/06/04/1-1465009811_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/khach-trung-quoc-chup-anh-cuoi-giua-paris-ngap-lut-3414214.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('773', 'Gần 3.500 lọ văcxin bị hỏng do bệnh viện bảo quản sai cách', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:50:56', '3.450 lọ văcxin tại Bệnh viện Đa khoa Đồng Nai được bảo quản sai cách đã ảnh hưởng đến chất lượng nên phải tiêu hủy.', 'http://img.f43.suckhoe.vnecdn.net/2016/06/04/vacxin-1-7512-1465009640_180x108.jpg', 'http://suckhoe.vnexpress.net/tin-tuc/suc-khoe/gan-3-500-lo-vacxin-bi-hong-do-benh-vien-bao-quan-sai-cach-3414161.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('774', 'Cảnh nồng nàn của Lưu Diệc Phi và ba tài tử trong phim mới', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:31:00', 'Ba người đàn ông do Lê Minh, Lưu Diệp, Dư Thiếu Quần đóng đều yêu Diệc Phi trong phim \"Dạ khổng tước\".', 'http://img.f12.giaitri.vnecdn.net/2016/06/04/nhung-canh-tinh-tu-cua-luu-diec-phi-cung-ba-tai-tu-trong-phim-moi-1465009950_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sau-man-anh/canh-nong-nan-cua-luu-diec-phi-va-ba-tai-tu-trong-phim-moi-3414195.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('775', 'Tăng gần 700 đồng một lít, giá xăng lên cao nhất 6 tháng', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:27:41', 'Giá bán lẻ mỗi lít xăng RON 92 được Petrolimex điều chỉnh lên mức 16.500 đồng từ 15h chiều nay.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/04/xang46-1465027919_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/tang-gan-700-dong-mot-lit-gia-xang-len-cao-nhat-6-thang-3414337.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('776', 'Cuộc sống của cô gái có 5 người chồng cùng lúc', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:20:43', 'Mỗi đêm, cô gái Ấn Độ sẽ ngủ với một người trong số 5 anh em trai nhà chồng. Có một bé trai được sinh ra nhưng không biết ai là bố.', 'http://img.f13.giadinh.vnecdn.net/2016/06/04/gai1-7159-1465016068_180x108.jpg', 'http://giadinh.vnexpress.net/tin-tuc/to-am/cuoc-song-cua-co-gai-co-5-nguoi-chong-cung-luc-3414310.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('777', 'Vợ tôi ứng xử quá kém', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:17:44', 'Mỗi lần cãi nhau em lại đòi về nhà ngoại rồi dọa ly hôn tôi.', 'http://img.f31.vnecdn.net/2016/06/04/kiemtien-1465013895_1465013903_1465013914_180x108.jpg', 'http://vnexpress.net/tin-tuc/tam-su/vo-toi-ung-xu-qua-kem-3405278.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('778', 'Dấu chân người Việt trên con đường tơ lụa', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:05:00', 'Thầy giáo trẻ đã mất hai tháng để chinh phục hành trình từ Kuwait, Iran của “vùng đất lửa” Trung Đông đến những đất nước gần như biệt lập ở Trung Á như Turkmenistan, Uzbekistan.', 'http://img.f34.dulich.vnecdn.net/2016/05/30/nguyenduc84-NHB09-JPG-8780-1464605666_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/dau-chan-nguoi-viet-tren-con-duong-to-lua-3411491.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('779', 'Voi trúng đạn gõ cửa cầu cứu người', 'N/a', '2016-06-04 16:49:29', '2016-06-04 14:00:00', 'Con voi đực tên Ben khiến các du khách hết sức bất ngờ khi xuất hiện tại nhà khách trong công viên Bumi Hills ở Zimbabwe.', 'http://img.f29.vnecdn.net/2016/06/04/VNEElephantTop-1465007471_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/voi-trung-dan-go-cua-cau-cuu-nguoi-3414103.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('780', 'Facebook chuẩn bị kế hoạch hậu Zuckerberg', 'N/a', '2016-06-04 16:49:52', '2016-06-04 13:13:22', 'Mạng xã hội lớn nhất thế giới đang đề nghị cổ đông bỏ phiếu cho đề xuất xóa bỏ quyền biểu quyết của CEO kiêm đồng sáng lập - Mark Zuckerberg nếu anh không còn lãnh đạo Facebook.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/04/zuckerberg-jpeg-6240-1465013415_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/facebook-chuan-bi-ke-hoach-hau-zuckerberg-3414164.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('781', 'Xe đạp Thống Nhất còn lại gì sau một thời vàng son', 'N/a', '2016-06-04 16:49:52', '2016-06-04 10:36:52', 'Thương hiệu xe đạp gắn bó 55 năm với người Việt đang có những bước chuyển mình để lấy lại vị thế của một thời \"vàng son\".', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/04/ThongNhatset-1465010516_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/xe-dap-thong-nhat-con-lai-gi-sau-mot-thoi-vang-son-3414133.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('782', 'Điện ảnh Việt và cuộc chiến giành miếng bánh trăm triệu USD', 'N/a', '2016-06-04 16:49:52', '2016-06-04 05:00:00', 'Khi thị trường điện ảnh Việt Nam chính thức cán mốc 100 triệu USD, việc chiếm thị phần cũng trở nên khốc liệt hơn, đặc biệt ở phân khúc rạp chiếu và phát hành. ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/cgv-9137-1463476279-6991-1464889240_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/dien-anh-viet-va-cuoc-chien-gianh-mieng-banh-tram-trieu-usd-3411958.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('783', 'Dân chung cư Bảy Hiền phải di dời khỏi công trình sai phép', 'N/a', '2016-06-04 16:49:52', '2016-06-04 07:44:35', 'Với tình trạng thang máy lắp tạm, hệ thống điện nước, phòng cháy chữa cháy chưa nghiệm thu, Sở Xây dựng TP HCM đề nghị chủ đầu tư vận động và hỗ trợ người dân di dời khỏi công trình sai phép Bảy Hiền Tower để đảm bảo an toàn. ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/04/a-tb-chung-cu-Bay-Hien-Tower-8058-1464978029_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/dan-chung-cu-bay-hien-phai-di-doi-khoi-cong-trinh-sai-phep-3414046.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('784', 'Trang bìa 10 tỷ USD của Forbes', 'N/a', '2016-06-04 16:49:52', '2016-06-04 03:00:00', 'Forbes vừa công bố danh sách Những phụ nữ tự thân giàu nhất nước Mỹ. Và vì không thể quyết định ai sẽ xuất hiện trên trang bìa ấn phẩm này, họ đã chọn tới 9 cá nhân.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/set-3-3711-1464951889_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/trang-bia-10-ty-usd-cua-forbes-3413872.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('785', 'Giá vàng nhảy vọt', 'N/a', '2016-06-04 16:49:52', '2016-06-04 08:03:50', 'Báo cáo việc làm của Mỹ không lạc quan như dự báo ngay lập tức khiến mỗi ounce vàng nhảy vọt 30 USD, vượt mốc 1.240 USD.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/04/gold-AFP-5672-1401238818-4321-1465001994_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-vang-nhay-vot-3414088.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('786', 'Công ty Trung Quốc kiếm bộn từ giấy vệ sinh Donald Trump', 'N/a', '2016-06-04 16:49:52', '2016-06-04 02:00:00', 'Loại giấy này có giá 0,5 USD một cuộn, với 3 phiên bản - Donald Trump cười, chỉ tay và bĩu môi.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/set4-1464952841_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/cong-ty-trung-quoc-kiem-bon-tu-giay-ve-sinh-donald-trump-3413943.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('787', 'Kinh nghiệm chọn mua điều hòa chất lượng', 'N/a', '2016-06-04 16:49:52', '2016-06-04 11:00:00', 'Công suất, tính năng hoạt động, nguồn gốc sản phẩm... là những điều cần quan tâm khi chọn mua điều hòa.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/04/3-6-201623-5202-1465003306_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/khuyen-mai/kinh-nghiem-chon-mua-dieu-hoa-chat-luong-3414076.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('788', 'HSBC: GDP quý II sẽ đạt 6,1%', 'N/a', '2016-06-04 16:49:52', '2016-06-04 10:53:34', 'Chỉ số Nhà quản trị mua hàng đạt mức cao, sản xuất công nghiệp lẫn xuất khẩu khá tốt... là những cơ sở khả quan cho việc GDP quý II tăng trưởng 6,1%, theo báo cáo vĩ mô, triển vọng thị trường tháng 6 của HSBC', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/04/doanh-nghiep-4694-1403151027-1-4444-3159-1465011535_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/hsbc-gdp-quy-ii-se-dat-6-1-3414198.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('789', 'Cam kết thuê lại căn hộ RichStar với 144 triệu đồng trên năm', 'N/a', '2016-06-04 16:49:52', '2016-06-04 10:00:00', 'Mức cam kết này được chủ đầu tư Novaland dành cho khách hàng mua căn hộ ngay trong tháng 6 này.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/02/Image-783313238-ExtractWord-1-6751-1298-1464850574_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/cam-ket-thue-lai-can-ho-richstar-voi-144-trieu-dong-tren-nam-3412863.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('790', 'Nguồn cung căn hộ tại Đà Nẵng tăng gần 50%', 'N/a', '2016-06-04 16:49:52', '2016-06-04 09:35:47', 'Nửa đầu năm 2016, thị trường chung cư tại Đà Nẵng đón nhận gần 1.000 căn hộ mới chào bán, tăng 44,6% so với cùng kỳ năm trước, trong đó các dự án trung cấp và bình dân chiếm 78,9% tổng nguồn cung, theo CBRE Việt Nam.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/04/chung-cu-tai-Son-Tra-DN-8092-1465006781_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/nguon-cung-can-ho-tai-da-nang-tang-gan-50-3414159.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('791', 'Mỗi lượng vàng tăng vài trăm nghìn đồng', 'N/a', '2016-06-04 16:49:52', '2016-06-04 09:23:16', 'Ngay từ khi mở cửa, mỗi lượng vàng đắt hơn 200.000 đồng nhưng đà tăng này vẫn không tương xứng với thế giới.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/04/doji5-500-4782-1465006607_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/moi-luong-vang-tang-vai-tram-nghin-dong-3414160.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('792', 'Căn hộ hai hướng nhìn ở trung tâm Sài Gòn', 'N/a', '2016-06-04 16:49:52', '2016-06-04 09:00:00', 'Các căn hộ \"The Duet” tại City Garden tận dụng tối đa ánh sáng tự nhiên, không gian mở, hướng sông Sài Gòn, đáp ứng tối đa nhu cầu người mua.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/the-duet-2-JPG-7593-1464948282_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/can-ho-hai-huong-nhin-o-trung-tam-sai-gon-3409181.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('793', 'Thanh toán điện tử thúc đẩy tăng trưởng GDP Việt Nam', 'N/a', '2016-06-04 16:49:52', '2016-06-04 00:00:00', 'Việc gia tăng sử dụng thẻ tín dụng, ghi nợ và trả trước đóng góp 880 triệu USD vào tổng giá trị GDP Việt Nam giai đoạn 2011-2015. ', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/paying-7959-1464941757_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/thuong-mai-dien-tu/tin-tuc/thanh-toan-dien-tu-thuc-day-tang-truong-gdp-viet-nam-3413764.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('794', 'Người mua trực tuyến muốn giao hàng trong bao lâu', 'N/a', '2016-06-04 16:49:52', '2016-06-04 00:00:00', 'Gần một phần tư người dùng Internet ở Bắc Mỹ cho rằng, 3 ngày là khoảng thời gian chấp nhận được để sản phẩm giao đến tay người mua.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/210811-6893-1464927586_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/thuong-mai-dien-tu/tin-quoc-te/nguoi-mua-truc-tuyen-muon-giao-hang-trong-bao-lau-3413584.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('795', 'Vinamilk tấn công vào Myanmar và Thái Lan', 'N/a', '2016-06-04 16:49:52', '2016-06-04 09:00:00', 'Thương hiệu sữa Việt có mặt tại 2 thị trường tiềm năng của khu vực Đông Nam Á, tiếp tục hiện thực hóa chiến lược toàn cầu.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/3-6-201615-2103-1464952592_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/vinamilk-tan-cong-vao-myanmar-va-thai-lan-3413921.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('796', '\'Bún chả Obama\' gây sốt ở Singapore', 'N/a', '2016-06-04 16:49:52', '2016-06-03 17:30:53', 'Một quán ăn tại đây bán được gấp rưỡi số suất bún chả so với trước, còn quán khác cũng lên kế hoạch đưa vào thực đơn \"suất ăn Obama\".', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/bun-cha-1-1937-1464948832_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/bun-cha-obama-gay-sot-o-singapore-3413887.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('797', 'Giá cua biển tăng vọt', 'N/a', '2016-06-04 16:49:52', '2016-06-03 11:07:10', 'Thời tiết bất lợi khiến cua biển khan hiếm, cộng với việc thương lái Trung Quốc tranh nhau gom hàng đã tạo nên cơn sốt chưa từng có về giá.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/cuasetop-1464926249_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-cua-bien-tang-vot-3413630.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('798', 'Nữ tỷ phú trẻ nhất Mỹ - từ đỉnh cao đến tay trắng', 'N/a', '2016-06-04 16:49:52', '2016-06-03 02:00:00', 'Elizabeth Holmes rất nổi tiếng tại Mỹ, từng được coi là Steve Jobs thứ 2, vì có khả năng thay đổi ngành y tế, như cách Apple thay đổi ngành điện thoại di động.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/02/holmes-1-5348-1464860143_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/nu-ty-phu-tre-nhat-my-tu-dinh-cao-den-tay-trang-3413151.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('799', 'Phó thủ tướng yêu cầu bảo vệ quyền lợi của dân chung cư Bảy Hiền', 'N/a', '2016-06-04 16:49:52', '2016-06-03 17:44:00', 'Văn phòng Chính phủ vừa có công văn chỉ đạo UBND TP HCM và Bộ Xây dựng làm rõ thông tin hơn 20 hộ dân chung cư Bảy Hiền Tower (Tân Bình) bất ngờ bị cắt điện nước, niêm phong nhà. ', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/a-tb-chung-cu-Bay-Hien-Tower-4461-1464947313_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/pho-thu-tuong-yeu-cau-bao-ve-quyen-loi-cua-dan-chung-cu-bay-hien-3413910.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('800', 'Giá xăng RON 92 có thể vượt 16.000 đồng một lít vào ngày mai', 'N/a', '2016-06-04 16:49:52', '2016-06-03 15:55:23', 'Mức điều chỉnh tối đa trong kỳ công bố giá ngày mai (4/6) có thể ở mức 600 đồng một lít với mặt hàng xăng được tiêu thụ phổ biến nhất hiện nay.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/PNTA0113-JPG-7113-1464943846_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-xang-ron-92-co-the-vuot-16-000-dong-mot-lit-vao-ngay-mai-3413829.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('801', 'Ông Lê Mạnh Hà: \'Doanh nghiệp đã khổ lắm rồi\'', 'N/a', '2016-06-04 16:49:52', '2016-06-03 15:13:29', 'Phó chủ nhiệm Văn phòng Chính phủ cho rằng việc xây dựng Nghị định về điều kiện kinh doanh cần chú trọng chuyện giảm thiểu, xoá bỏ các điều kiện bởi doanh nghiệp đang chịu khổ với hàng nghìn giấy phép con.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/dn-4891-1464938879-3728-1464941583_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/ong-le-manh-ha-doanh-nghiep-da-kho-lam-roi-3413763.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('802', 'Taylor Swift là nữ triệu phú tự thân trẻ nhất Mỹ', 'N/a', '2016-06-04 16:49:52', '2016-06-03 13:00:00', 'Theo Forbes, nữ ca sĩ 27 tuổi hiện có 250 triệu USD và là người trẻ nhất trong danh sách Những phụ nữ tự thân giàu nhất nước Mỹ.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/settay-1464927160_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/taylor-swift-la-nu-trieu-phu-tu-than-tre-nhat-my-3413587.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('803', 'Du lịch biển miền Trung thiệt hại nặng vì cá chết', 'N/a', '2016-06-04 16:49:52', '2016-06-03 11:16:42', 'Thống kê của Bộ Kế hoạch &amp; Đầu tư cho thấy lượng khách quốc tế đến một số tỉnh miền Trung giảm 20-50% trong dịp cao điểm tháng 5.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/02/mientrung-1464855737-6374-1464855771_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/du-lich-bien-mien-trung-thiet-hai-nang-vi-ca-chet-3413181.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('804', '85% người mua bất động sản Đà Nẵng đến từ Hà Nội', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:47:55', '5 tháng qua, các khách hàng từ Hà Nội và các tỉnh phía Bắc thống trị thị trường bất động sản nghỉ dưỡng Đà Nẵng, chiếm 75-85% giao dịch thành công, theo CBRE Việt Nam', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/a-tb-85-pt-nguoi-mua-bds-DN-de-2901-1239-1464921627_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/85-nguoi-mua-bat-dong-san-da-nang-den-tu-ha-noi-3413484.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('805', 'Dân chung cư Bảy Hiền bị cắt điện nước vì chủ đầu tư xây sai phép', 'N/a', '2016-06-04 16:49:52', '2016-06-03 08:09:21', '2 ngày qua, hơn 20 hộ dân mới dọn về chung cư Bảy Hiền Tower lâm cảnh đứng ngồi không yên khi toà nhà bị phong toả, cắt điện, nước do chủ đầu tư bị cơ quan chức năng \"tuýt còi\" về việc xây dựng sai phép và không có thiện chí khắc phục. ', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/cc-1-7758-1464916132_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/dan-chung-cu-bay-hien-bi-cat-dien-nuoc-vi-chu-dau-tu-xay-sai-phep-3413386.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('806', 'Phó thủ tướng hối thúc doanh nghiệp tư nhân \'cất cánh\'', 'N/a', '2016-06-04 16:49:52', '2016-06-03 20:21:09', 'Phó thủ tướng Vương Đình Huệ mong muốn các doanh nghiệp tư nhân mạnh dạn đổi mới, sáng tạo khi Việt Nam được đánh giá là thiên đường của khởi nghiệp, phát triển.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/detmay-1464956842-1637-1464957077_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/pho-thu-tuong-hoi-thuc-doanh-nghiep-tu-nhan-cat-canh-3413987.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('807', 'Phó thủ tướng: Dựa dẫm vào quen biết, doanh nghiệp không thể phát triển', 'N/a', '2016-06-04 16:49:52', '2016-06-03 16:21:49', 'Phó thủ tướng Vương Đình Huệ nhấn mạnh, Chính phủ luôn đồng hành, không phân biệt đối xử và theo nguyên tắc thị trường, tạo mọi điều kiện để doanh nghiệp phát triển.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/gapmatdoanhnhantrethanhdat3-1464942861_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/pho-thu-tuong-dua-dam-vao-quen-biet-doanh-nghiep-khong-the-phat-trien-3413788.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('808', 'Việt Nam còn \'tắc\' 22 tỷ USD vốn ODA', 'N/a', '2016-06-04 16:49:52', '2016-06-03 14:01:49', 'Trong hơn 20 tỷ USD tồn đọng, đại diện Chính phủ cho biết có 2,15 tỷ USD buộc phải giải ngân ngay trong năm nay.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/oda-8469-1464934096_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/viet-nam-con-tac-22-ty-usd-von-oda-3413719.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('809', 'OPEC tiếp tục không giảm sản xuất dầu', 'N/a', '2016-06-04 16:49:52', '2016-06-03 10:19:37', 'Cuộc họp của Tổ chức Các nước Xuất khẩu Dầu mỏ (OPEC) tại Vienna (Áo) hôm qua đã kết thúc với quyết định không thay đổi chính sách hiện tại.', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/03/000-BG3EV-4113-1464922808_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/quoc-te/opec-tiep-tuc-khong-giam-san-xuat-dau-3413572.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('810', 'Giá vàng tiếp tục đi xuống', 'N/a', '2016-06-04 16:49:52', '2016-06-03 08:37:38', 'Mỗi ounce vàng sụt gần 3 USD khi chốt phiên Mỹ tối qua sau khi đã để mất 0,4% trong ngày giao dịch trước đó do đồng USD có dấu hiệu tăng. ', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/Goldreuters630-1464917539_1464917565_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-vang-tiep-tuc-di-xuong-3413420.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('811', 'Bán 1,5 tỷ lít bia, Sabeco chi 2.100 tỷ đồng trả cổ tức', 'N/a', '2016-06-04 16:49:52', '2016-06-03 08:09:07', 'Cổ đông hãng bia lớn nhất Việt Nam sẽ nhận 3.000 đồng với mỗi cổ phần sở hữu.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/02/biasaigon-1464864196-2274-1464864209_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/ban-1-5-ty-lit-bia-sabeco-chi-2-100-ty-dong-tra-co-tuc-3413319.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('812', 'Doanh nghiệp phải mua khống hoá đơn khi quảng cáo online', 'N/a', '2016-06-04 16:49:52', '2016-06-03 05:00:00', 'Google chỉ cung cấp chứng từ nếu chi phí quảng cáo hằng tháng đạt 5.000 USD nên nhiều doanh nghiệp nhỏ phải mua hoá đơn \"khống\" để hợp lý hoá chi phí với cơ quan thuế.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/02/hoadon-3607-1464868672_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-phai-mua-khong-hoa-don-khi-quang-cao-online-3413351.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('813', 'Thứ trưởng Lao động: \'Tôi buồn vì cán bộ vô cảm\'', 'N/a', '2016-06-04 16:49:52', '2016-06-03 01:00:00', 'Chia sẻ về một trường hợp lao động bị tai nạn nhưng nộp hồ sơ một năm rưỡi vẫn không được giải quyết chế độ, Thứ trưởng Phạm Minh Huân cho biết ông rất buồn trước sự vô cảm của nhiều cán bộ, công chức', 'http://img.f28.kinhdoanh.vnecdn.net/2016/06/02/laodong4-6580-1464861104_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/thu-truong-lao-dong-toi-buon-vi-can-bo-vo-cam-3413131.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('814', 'Viet Capital Bank khai trương trụ sở mới chi nhánh Bình Dương', 'N/a', '2016-06-04 16:49:52', '2016-06-03 17:00:00', 'Trụ sở mới chi nhánh Bình Dương nằm tại số 316, Đại lộ Bình Dương, phường Phú Thọ Hòa, TP Thủ Dầu Một, tỉnh Bình Dương, khánh thành vào ngày 3/6.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/viet-capital-bank-2-5181-1464948829_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/cong-dong/viet-capital-bank-khai-truong-tru-so-moi-chi-nhanh-binh-duong-3413933.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('815', 'Hội thảo cập nhật tiêu chuẩn phòng sạch mới', 'N/a', '2016-06-04 16:49:52', '2016-06-03 14:00:00', 'Chương trình cung cấp kiến thức mới về tiêu chuẩn, hệ đếm tiểu phân online trong phòng sạch hãng TSI của Mỹ và hệ thống tiệt trùng phòng sạch Bioquell, Anh.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/9500-1648-1464927480-4822-1464936694_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/hoi-thao-cap-nhat-tieu-chuan-phong-sach-moi-3413575.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('816', 'Bậc thầy marketing Jay Abraham sắp đến Việt Nam', 'N/a', '2016-06-04 16:49:52', '2016-06-03 14:00:00', 'Lần đầu tiên Jay Abraham có mặt trong chương trình Wealth Summit tại Hà Nội và TP HCM vào tháng 6 tới.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/banner-JA-PNG_1464927484_1464927812_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/bac-thay-marketing-jay-abraham-sap-den-viet-nam-3409971.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('817', 'Mua căn hộ cao cấp Luxcity có cơ hội nhận xe sang', 'N/a', '2016-06-04 16:49:52', '2016-06-03 13:00:00', 'Sở hữu không gian sống tiện nghi trong căn hộ cao cấp tại quận 7, TP HCM, khách hàng còn có cơ hội nhận xe Audi A4 trị giá 1,5 tỷ đồng.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/02/Image-893054902-ExtractWord-1-4788-6010-1464862952_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/mua-can-ho-cao-cap-luxcity-co-co-hoi-nhan-xe-sang-3413128.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('818', 'ShipS lấy quyền lợi người giao hàng làm trọng tâm', 'N/a', '2016-06-04 16:49:52', '2016-06-03 11:00:00', 'Bằng cách gia tăng quyền lợi cho người giao hàng (shipper), ShipS dần rút ngắn khoảng cách với loại hình tương tự khi không chỉ tăng thu nhập mà còn giúp các doanh nghiệp quảng bá tốt hơn.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/2-6-201614-8600-1464919334_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/ships-lay-quyen-loi-nguoi-giao-hang-lam-trong-tam-3413297.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('819', 'Phong cách Pháp thập niên 70 tái hiện tại Tràng Tiền Plaza', 'N/a', '2016-06-04 16:49:52', '2016-06-03 10:00:00', 'Các quý cô trong trang phục cổ điển, thanh lịch nhưng không kém phần đương đại đã mang đến không gian đậm chất Pháp cho Trung tâm thương mại Tràng Tiền.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/2-6-20165-698984792-8340-1464920892_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/phong-cach-phap-thap-nien-70-tai-hien-tai-trang-tien-plaza-3413268.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('820', 'Quỹ \'Khí phách Việt\' trao hàng nghìn quà tặng dịp 1/6', 'N/a', '2016-06-04 16:49:52', '2016-06-03 10:00:00', '3.000 phần quà và 100 phần thưởng giá trị đã được trao cho các em thiếu nhi đạt thành tích cao trong học tập.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/02/Image-ExtractWord-2-Out-1405-1464858276_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/quy-khi-phach-viet-trao-hang-nghin-qua-tang-dip-1-6-3413152.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('821', 'Condotel tại Cocobay Đà Nẵng có giá bán từ 1,2 tỷ đồng', 'N/a', '2016-06-04 16:49:52', '2016-06-03 10:00:00', 'Có vị trí đẹp nhất khu du lịch - giải trí đẳng cấp quốc tế Cocobay Đà Nẵng, Coco Ocean Resort được chào bán giá từ 1,2 tỷ đồng cho khách đặt chỗ ngày 4/6.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/02/Image-422643690-ExtractWord-2-1818-4027-1464867011_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/condotel-tai-cocobay-da-nang-co-gia-ban-tu-1-2-ty-dong-3413076.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('822', 'Giá vàng SJC đi xuống', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:54:39', 'Mỗi lượng vàng miếng sáng nay giảm vài chục nghìn đồng, xuống quanh 33,18 triệu đồng.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/PNJaccdc-pnj1-6534-1426903896-4896-1464922218_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/hang-hoa/gia-vang-sjc-di-xuong-3413506.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('823', 'Đặt mục tiêu GDP năm 2017 tăng 6,8%', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:22:29', 'Chỉ thị 21 về xây dựng kế hoạch phát triển kinh tế xã hội năm 2017 vừa được Thủ tướng ban hành nêu rõ mục tiêu tổng quát là đảm bảo ổn định kinh tế vĩ mô, thúc đẩy tăng trưởng kinh tế.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/03/GDP-4167-1464920274_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/vi-mo/dat-muc-tieu-gdp-nam-2017-tang-6-8-3413493.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('824', 'STDA cùng 8 đại lý phân phối các dự án TNR Holdings Việt Nam', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:00:00', 'Theo thỏa thuận giữa TNR và STDA, Goldmark City và GoldSeason là 2 dự án đầu tiên áp dụng mô hình phân phối mới.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/02/Image-938078797-ExtractWord-1-2935-5745-1464869265_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/stda-cung-8-dai-ly-phan-phoi-cac-du-an-tnr-holdings-viet-nam-3413333.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('825', 'SCB tặng bảo hiểm nhân thọ 5 tỷ đồng cho cá nhân vay vốn', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:00:00', 'Khách vay tiêu dùng có tài sản đảm bảo hoặc không, tiểu thương vay bổ sung vốn kinh doanh... đều được hưởng ưu đãi từ nhà băng này.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/03/SCB-4053-1464917466_180x108.jpg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/cong-dong/scb-tang-bao-hiem-nhan-tho-5-ty-dong-cho-ca-nhan-vay-von-3413200.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('826', '\'Dẫn đầu công nghệ, hướng đến tương lai\' cùng Shell', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:00:00', 'Công ty Shell Việt Nam và Công ty Lê Hùng Sao Mai (LHSM) cam kết đem đến những sản phẩm dầu nhờn chất lượng cao, mang tính đột phá cho người tiêu dùng.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/02/2-6-201640-4373-1464855459_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/dan-dau-cong-nghe-huong-den-tuong-lai-cung-shell-3413051.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('827', 'Bí quyết làm mát nhà hàng và không gian mở hiệu quả', 'N/a', '2016-06-04 16:49:52', '2016-06-03 09:00:00', 'Với khả năng tiết kiệm điện, không tốn phí bảo trì, máy làm mát nhà hàng và không gian mở Symphony giúp tăng hiệu quả kinh doanh cho các nhà đầu tư.', 'http://img.f25.kinhdoanh.vnecdn.net/2016/06/03/Untitled-6185-1464916450_180x108.png', 'http://kinhdoanh.vnexpress.net/tin-tuc/doanh-nghiep/doanh-nghiep-viet/bi-quyet-lam-mat-nha-hang-va-khong-gian-mo-hieu-qua-3412990.html', '6', '2', '0', '0', '1', null, '52');
INSERT INTO `posts` VALUES ('828', 'Những phụ nữ xăm mặt càng nhiều càng đẹp', 'N/a', '2016-06-04 16:50:09', '2016-06-04 12:03:00', 'Xưa kia các cô gái ở bang Chin, Myanmar xăm mặt để không bị bắt làm nô lệ cho vua chúa thì ngày nay những hình xăm được xem là thước đo vẻ đẹp của họ.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/11-1464922296_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/nhung-phu-nu-xam-mat-cang-nhieu-cang-dep-3413563.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('829', '10 món Trung Quốc được yêu thích giữa lòng Hà Nội', 'N/a', '2016-06-04 16:50:09', '2016-06-04 10:06:32', 'Ngoài há cảo, bánh bao, xíu mại, lẩu xí quách và lẩu uyên ương cũng là những món đặc trưng cho hương vị ẩm thực của Trung Quốc.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/2-banh-bao-kim-sa-1464936194_180x108.jpg', 'http://dulich.vnexpress.net/photo/ha-noi/10-mon-trung-quoc-duoc-yeu-thich-giua-long-ha-noi-3413731.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('830', 'Đà Nẵng có đường trượt zipline đầu tiên dài 300 m', 'N/a', '2016-06-04 16:50:09', '2016-06-04 08:05:00', 'Đường trượt zipline, đu quay 360 độ, tháp tắm onsen là những dịch vụ mới được Đà Nẵng giới thiệu tại Hà Nội.', 'http://img.f33.dulich.vnecdn.net/2016/06/04/zipline-3295-1465000829_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/da-nang/da-nang-co-duong-truot-zipline-dau-tien-dai-300-m-3414070.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('831', 'Nhiều điểm du lịch nổi tiếng ở Paris chìm trong nước lũ', 'N/a', '2016-06-04 16:50:09', '2016-06-04 08:57:41', 'Các điểm tham quan như bảo tàng Louvre, Musée d’Orsay... đã đóng cửa do trận lụt tồi tệ nhất thế kỷ đang diễn ra tại Paris, Pháp.', 'http://img.f34.dulich.vnecdn.net/2016/06/04/7473192-3x2-700x467-9617-1465005147_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/nhieu-diem-du-lich-noi-tieng-o-paris-chim-trong-nuoc-lu-3414124.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('832', 'Du khách dùng hộ chiếu hết hạn của người khác', 'N/a', '2016-06-04 16:50:09', '2016-06-04 07:37:00', 'Một du khách Scotland vô cùng bất ngờ khi tại hải quan nước mình, họ dễ dàng để bà đi qua mà không phát hiện ra bà dùng hộ chiếu hết hạn 5 năm của chồng.', 'http://img.f36.dulich.vnecdn.net/2016/06/03/2-1464940024_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-dung-ho-chieu-het-han-cua-nguoi-khac-3413776.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('833', 'Hòn Yến nên thơ ở xứ \'hoa vàng cỏ xanh\'', 'N/a', '2016-06-04 16:50:09', '2016-06-04 05:05:00', 'Khi thủy triều rút xuống, một con đường xuyên biển sẽ lộ ra nối liền bờ với Hòn Sụn, thẳng tới Hòn Yến của huyện biển Tuy An.', 'http://img.f33.dulich.vnecdn.net/2016/05/21/3-1463805841_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/hon-yen-nen-tho-o-xu-hoa-vang-co-xanh-3406860.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('834', 'Hà Nội giới thiệu tour du lịch \'cảm xúc\'', 'N/a', '2016-06-04 16:50:09', '2016-06-04 09:16:24', 'Chiều 3/6, Sở Du lịch Hà Nội tổ chức Hội nghị giới thiệu sản phẩm du lịch mới \"Cảm xúc Hà Nội\" tại TP Đà Nẵng, nhằm tạo ra các điểm nhấn và trải nghiệm mới mẻ về một Thủ đô ngàn năm văn hiến đối với du khách.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/hanoi-1464950776-2517-1464950784_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-noi-gioi-thieu-tour-du-lich-cam-xuc-3413961.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('835', 'Vị khách đặc biệt của quán bún chả Thủ đô', 'N/a', '2016-06-04 16:50:09', '2016-06-04 09:00:00', 'Quán nổi như cồn không chỉ bởi món ngon, bia chuẩn mà còn là nơi được ông chủ nhà Trắng - Obama lựa chọn để thưởng thức trong chuyến công du Việt Nam mới đây.', 'http://img.f30.vnecdn.net/2016/06/02/3-1464859155_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-noi/vi-khach-dac-biet-cua-quan-bun-cha-thu-do-3413217.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('836', 'Thiên đường giải trí ở miền Trung', 'N/a', '2016-06-04 16:50:09', '2016-06-04 08:00:00', 'Cocobay - tổ hợp khép kín đầu tiên đặt tại miền Trung, Việt Nam quy tụ tất cả hoạt động ẩm thực, du lịch, giải trí, sáng tạo tiêu chuẩn quốc tế.', 'http://img.f34.dulich.vnecdn.net/2016/06/02/1-1464852967_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/thien-duong-giai-tri-o-mien-trung-3413116.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('837', 'Cặp vợ chồng làm sống dậy điệu Flamenco ở New York', 'N/a', '2016-06-04 16:50:09', '2016-06-03 20:03:00', 'Với khát khao đem điệu nhảy Flamenco chạm đến trái tim của mỗi người, vợ chồng người Tây Ban Nha đã bất chấp mọi khó khăn để hoàn thành mục tiêu đó.', 'http://img.f33.dulich.vnecdn.net/2016/06/03/flamenco-6-1464915382_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/cap-vo-chong-lam-song-day-dieu-flamenco-o-new-york-3413438.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('838', 'Món kem đen gây sốt ở New York', 'N/a', '2016-06-04 16:50:09', '2016-06-03 19:00:00', 'Trong thế giới đồ ăn luôn có những sắc màu rực rỡ thì một cây kem màu đen lại bất ngờ gây sự chú ý của đông đảo thực khách.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/blackicecream-1464923521_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/mon-kem-den-gay-sot-o-new-york-3412996.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('839', 'Những dòng thác mát lành ngay ngoại ô Hà Nội', 'N/a', '2016-06-04 16:50:09', '2016-06-03 16:03:00', 'Khi cơn mưa mùa hạ trút xuống cũng là lúc thác Mơ, thác Hoa, thác Tràn, thác Mâm Xôi… trở nên no nước và đổ xuống từ vách núi tạo ra những âm thanh du dương.', 'http://img.f36.dulich.vnecdn.net/2016/06/03/2-HA83127-15-copy-2-1464946296_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/nhung-dong-thac-mat-lanh-ngay-ngoai-o-ha-noi-3413489.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('840', '9 điều cấm đối với khách du lịch Trung Quốc', 'N/a', '2016-06-04 16:50:09', '2016-06-03 15:03:00', 'Giới chức Trung Quôc vừa đưa ra quy định gồm 9 hành vi bị cấm khi người nước này khi đi du lịch nước ngoài, trong đó có việc cấm đi vệ sinh bậy bạ nơi công cộng. ', 'http://img.f36.dulich.vnecdn.net/2016/06/03/fakegun1-1464916222_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/9-dieu-cam-doi-voi-khach-du-lich-trung-quoc-3413424.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('841', 'Hàng chục khách Tây dọn rác trên biển Mũi Né', 'N/a', '2016-06-04 16:50:09', '2016-06-03 13:55:45', 'Lời kêu gọi dọn sạch bãi biển của du khách Nga được hàng chục người nước ngoài hưởng ứng, cùng sự chung tay của các bạn trẻ địa phương và nhân viên một khu dịch vụ gần đó.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/1-1464921328_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/hang-chuc-khach-tay-don-rac-tren-bien-mui-ne-3413544.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('842', 'Tục dã ngoại cùng người chết ở Hy Lạp', 'N/a', '2016-06-04 16:50:09', '2016-06-03 12:03:00', 'Trong chuyến dã ngoại cùng người chết, những người thân trong gia đình không được phép khóc mà phải cười nói vui vẻ.', 'http://img.f36.dulich.vnecdn.net/2016/06/02/img2446-1464857449_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/tuc-da-ngoai-cung-nguoi-chet-o-hy-lap-3413193.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('843', '5 điểm du lịch xanh mát giữa miền Trung đổ lửa', 'N/a', '2016-06-04 16:50:09', '2016-06-03 09:14:56', 'Vườn thú hoang dã quy mô lớn đầu tiên ở Nghệ An và hang Tối - suối nước Moọc ở Quảng Bình là những điểm đến bạn không thể bỏ qua khi đến miền Trung thời gian này.', 'http://img.f36.dulich.vnecdn.net/2016/06/02/DSC-6481-JPG-3947-1464863994_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/5-diem-du-lich-xanh-mat-giua-mien-trung-do-lua-3412444.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('844', 'Du khách được cứu nhờ chụp ảnh selfie', 'N/a', '2016-06-04 16:50:09', '2016-06-03 08:09:00', 'Hai du khách bị lạc và mắc kẹt trên vách núi đá đã nảy ra ý tưởng chụp ảnh selfie của mình rồi gửi tín hiệu cấp cứu đến đội cứu hộ.', 'http://img.f33.dulich.vnecdn.net/2016/06/02/2-1464839012_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-duoc-cuu-nho-chup-anh-selfie-3412979.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('845', 'Cho chim ăn, du khách bị phạt vì tội xả rác bừa bãi', 'N/a', '2016-06-04 16:50:09', '2016-06-03 07:40:30', 'Vừa bị những con chim cướp mất đồ ăn trên tay, du khách đến từ North Yorkshire tiếp tục chịu án phạt 115 USD vì tội xả rác.', 'http://img.f34.dulich.vnecdn.net/2016/06/02/2-1464836364_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/cho-chim-an-du-khach-bi-phat-vi-toi-xa-rac-bua-bai-3412908.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('846', 'Khánh Hòa được đề nghị chấn chỉnh việc đón khách Trung Quốc', 'N/a', '2016-06-04 16:50:09', '2016-06-03 08:50:10', 'Đề nghị chấn chỉnh hoạt động đón khách Trung Quốc được Tổng cục Du lịch đưa ra trước nguy cơ hình ảnh du lịch Khánh Hòa bị ảnh hưởng nghiêm trọng.', 'http://img.f36.dulich.vnecdn.net/2016/06/03/13348722-1138505349546510-9166-7429-9505-1464918861_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/khanh-hoa-duoc-de-nghi-chan-chinh-viec-don-khach-trung-quoc-3413418.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('847', 'New York - thành phố đa dạng ngôn ngữ nhất thế giới', 'N/a', '2016-06-04 16:50:09', '2016-06-03 05:05:00', 'Một nghiên cứu của New York Times cho biết người dân ở New York hiện sử dụng khoảng 800 thứ tiếng khác nhau nên nơi đây được đánh giá là có sự đa dạng ngôn ngữ bậc nhất thế giới.', 'http://img.f36.dulich.vnecdn.net/2016/06/01/new-york-1464745449_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/new-york-thanh-pho-da-dang-ngon-ngu-nhat-the-gioi-3412226.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('848', 'Giảm 8 triệu đồng tour Nhật Bản 5 ngày', 'N/a', '2016-06-04 16:50:09', '2016-06-03 17:00:00', 'Ngoài tham quan khung cảnh quyến rũ, du khách còn được thưởng thức cherry giải nhiệt mùa hè ngay tại vườn và xem show diễn geisha độc đáo.', 'http://img.f36.dulich.vnecdn.net/2016/06/03/1-1464943561_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/chu-de/deal/giam-8-trieu-dong-tour-nhat-ban-5-ngay-3413860.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('849', 'Traveloka ưu đãi tháng 6', 'N/a', '2016-06-04 16:50:09', '2016-06-03 15:00:00', 'Từ 3-5/6, khi đặt vé máy bay tại App Traveloka, bạn sẽ được giảm đến 300.000 đồng một lần đặt, áp dụng cho tất cả hãng bay.', 'http://img.f35.dulich.vnecdn.net/2016/05/31/1-1464666725_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/traveloka-uu-dai-thang-6-3411731.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('850', 'Thế giới phép thuật nơi Thiên đường Bảo Sơn', 'N/a', '2016-06-04 16:50:09', '2016-06-03 14:00:00', 'Đoàn xe diễu hành nhiều màu sắc sẽ chở bạn đến \"Thế giới phép thuật\" ở Thiên đường Bảo Sơn mùa hè này.', 'http://img.f34.dulich.vnecdn.net/2016/06/03/1-1464927018_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ha-noi/the-gioi-phep-thuat-noi-thien-duong-bao-son-3413210.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('851', 'Món ngon Nhật Bản giải nhiệt ngày hè tại Shabu Kichoo', 'N/a', '2016-06-04 16:50:09', '2016-06-03 09:00:00', 'Shabu Kichoo, hệ thống nhà hàng lẩu nướng Nhật Bản vừa ra mắt thực đơn thanh nhiệt mùa hè ngon miệng, hấp dẫn.', 'http://img.f34.dulich.vnecdn.net/2016/06/01/Image-ExtractWord-0-Out-1883-1464773767_180x108.png', 'http://dulich.vnexpress.net/tin-tuc/chu-de/am-thuc/mon-ngon-nhat-ban-giai-nhiet-ngay-he-tai-shabu-kichoo-3412609.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('852', 'Vương quốc của những kỳ quan', 'N/a', '2016-06-04 16:50:09', '2016-06-03 08:00:00', 'Du khách như lạc vào thế giới của những đền đài rêu phong, chùa tháp cổ xưa ở Angkor Wat, cùng vẻ hoang sơ của thiên nhiên Koh Rong Samloen...', 'http://img.f33.dulich.vnecdn.net/2016/06/02/2-1464855759_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/vuong-quoc-cua-nhung-ky-quan-3413173.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('853', 'Du khách Nga kêu biển ngập rác, Bình Thuận vào cuộc xác minh', 'N/a', '2016-06-04 16:50:09', '2016-06-02 09:11:00', 'Trước thông tin một du khách Nga kêu gọi sự giúp đỡ khi đăng ảnh biển Mũi Né ngập rác thải, UBND tỉnh Bình Thuận yêu cầu kiểm tra, xác minh, nếu có phải khẩn trương xử lý.', 'http://img.f34.dulich.vnecdn.net/2016/06/02/13239122-1194027037295137-3097-7313-8597-1464832253_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/phan-thiet/du-khach-nga-keu-bien-ngap-rac-binh-thuan-vao-cuoc-xac-minh-3412809.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('854', 'Hệ thống hầm bọc thép chống bom dưới dinh Độc Lập', 'N/a', '2016-06-04 16:50:09', '2016-06-02 05:05:00', 'Được xây dựng vững chắc bằng bê tông, thép bọc tường dày 5 mm, khả năng chịu bom đến 2 tấn, hệ thống hầm bên dưới Dinh Độc Lập là nơi ẩn nấp của bộ phận trọng yếu trong chính phủ Việt Nam Cộng hòa.', 'http://img.f34.dulich.vnecdn.net/2016/05/26/2-1464237403_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/he-thong-ham-boc-thep-chong-bom-duoi-dinh-doc-lap-3409397.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('855', 'Du khách nhí bị xâm hại lúc nửa đêm', 'N/a', '2016-06-04 16:50:09', '2016-06-02 20:30:00', 'Khoảng 2h sáng, một bé gái 13 tuổi đã bị tấn công tình dục tại khu nghỉ mát Tenerife sau khi đi lạc khỏi anh trai mình.', 'http://img.f34.dulich.vnecdn.net/2016/06/02/1-1464833463_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-nhi-bi-xam-hai-luc-nua-dem-3412877.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('856', 'Siêu du thuyền có thể lặn sâu 240 m', 'N/a', '2016-06-04 16:50:09', '2016-06-02 19:00:00', 'Migaloo là siêu du thuyền kết hợp tàu ngầm vừa đáp ứng mọi nhu cầu xa xỉ như các du thuyền khác vừa có thể lặn sâu xuống dưới lòng biển.', 'http://img.f36.dulich.vnecdn.net/2016/06/01/1-1464765335_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/sieu-du-thuyen-co-the-lan-sau-240-m-3412483.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('857', 'Những sự thật về Geisha', 'N/a', '2016-06-04 16:50:09', '2016-06-02 12:03:00', 'Geisha không có thời gian nghỉ ngơi đúng nghĩa, không được nằm xem tivi vì phải giữ hình ảnh hay người chuyên giúp họ thay đồ là đàn ông.', 'http://img.f33.dulich.vnecdn.net/2016/06/01/1-1464768293_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/nhung-su-that-ve-geisha-3412532.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('858', 'Tà áo dài Việt trên báo nước ngoài', 'N/a', '2016-06-04 16:50:09', '2016-06-02 08:09:00', 'Theo nhiếp ảnh gia Pháp, người phụ nữ trong chiếc áo dài là đại diện cho vẻ đẹp Việt mà anh muốn cả thế giới được chiêm ngưỡng.', 'http://img.f33.dulich.vnecdn.net/2016/06/01/13321181-677488075788419-1305145617-o-1464755266_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/ta-ao-dai-viet-tren-bao-nuoc-ngoai-3412408.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('859', 'Vườn hoa tam giác mạch nở rộ ở Đà Lạt', 'N/a', '2016-06-04 16:50:09', '2016-06-02 16:03:00', 'Trước chùa Vạn Đức (Lâm Đồng), vườn hoa tam giác mạch khá rộng, đang thì nở rộ thu hút bất kỳ ai ghé ngang qua.', 'http://img.f34.dulich.vnecdn.net/2016/06/02/nguyenduc84-5-1464838888_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/vuon-hoa-tam-giac-mach-no-ro-o-da-lat-3412988.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('860', 'Tây Ban Nha cấm du khách mang theo đồ chơi tình dục', 'N/a', '2016-06-04 16:50:09', '2016-06-02 15:03:00', 'Khi đến các khu du lịch ở Mojacar, Tây Ban Nha, du khách sẽ bị phạt 1.000 euro nếu cố tình mang theo đồ chơi tình dục.', 'http://img.f34.dulich.vnecdn.net/2016/06/01/12-1464766977_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/tay-ban-nha-cam-du-khach-mang-theo-do-choi-tinh-duc-3412506.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('861', '7 hostel Đà Lạt dành cho nhóm du khách độc thân', 'N/a', '2016-06-04 16:50:09', '2016-06-02 14:05:00', 'Giá một đêm chỉ từ 80.000 đến 120.000 đồng/ người, các hostel giá rẻ nhưng vẫn có nhiều tiện lợi như Dalat Note, HomeFarm, Beepub... rất phù hợp cho những du khách đi lẻ hoặc theo nhóm độc thân.', 'http://img.f35.dulich.vnecdn.net/2016/05/23/Nha-cua-Tre-1463978711_180x108.jpg', 'http://dulich.vnexpress.net/photo/tu-van/7-hostel-da-lat-danh-cho-nhom-du-khach-doc-than-3407582.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('862', 'Nhiều tour hot mời gọi du khách mùa hè', 'N/a', '2016-06-04 16:50:09', '2016-06-02 08:41:00', 'Điệp Sơn, Kỳ Co, Sihanoukville là những điểm đến được nhiều công ty du lịch đưa vào sản phẩm chính trong mùa hè năm nay.', 'http://img.f35.dulich.vnecdn.net/2016/06/01/hinh1-1456207692-660x0-1387-1464754597_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/nhieu-tour-hot-moi-goi-du-khach-mua-he-3412117.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('863', 'Nơi du khách không được chào đón', 'N/a', '2016-06-04 16:50:09', '2016-06-02 07:37:00', '“Khách du lịch biến về nhà đi” hay “Du khách – kẻ khủng bố” là những thông điệp xuất hiện gần đây trên đảo Majorca của Tây Ban Nha.', 'http://img.f35.dulich.vnecdn.net/2016/06/01/tourist-go-home-jpeg-9721-1464751343_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/noi-du-khach-khong-duoc-chao-don-3412342.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('864', 'Tour Phú Quốc 4 ngày 3 đêm giá từ 6,95 triệu đồng', 'N/a', '2016-06-04 16:50:09', '2016-06-02 09:00:00', 'Du khách sẽ có cơ hội khám phá các điểm du lịch nổi tiếng như làng chài Hàm Ninh, suối Tranh, lặn ngắm san hô... Giá tour bao gồm vé máy bay.', 'http://img.f35.dulich.vnecdn.net/2016/06/02/1-1464829701_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/chu-de/deal/tour-phu-quoc-4-ngay-3-dem-gia-tu-6-95-trieu-dong-3412365.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('865', 'Vietravel ưu đãi 8 tỷ đồng mùa du lịch', 'N/a', '2016-06-04 16:50:09', '2016-06-02 14:00:00', 'Du khách đăng ký tour từ ngày 1/6 đến 29/8 được hoàn một phần chi phí và có cơ hội nhận \"tấm vé vàng\" 5 triệu đồng.', 'http://img.f36.dulich.vnecdn.net/2016/06/01/1-6-201644-824322715-2224-1464781587_180x108.jpeg', 'http://dulich.vnexpress.net/tin-tuc/khuyen-mai/vietravel-uu-dai-8-ty-dong-mua-du-lich-3412611.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('866', 'Số phận của những con voi làm du lịch ở Thái Lan', 'N/a', '2016-06-04 16:50:09', '2016-06-01 20:03:00', 'Một con voi chở khách 15 năm liền bất ngờ gục ngã và chết trên đường trong khu du lịch nổi tiếng đã khiến nhiều người phải suy ngẫm.', 'http://img.f33.dulich.vnecdn.net/2016/06/01/elephant-8192-1464766260_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/so-phan-cua-nhung-con-voi-lam-du-lich-o-thai-lan-3412493.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('867', 'Sơn Đoòng dưới ống kính nhiếp ảnh gia Thụy Sĩ', 'N/a', '2016-06-04 16:50:09', '2016-06-01 16:03:00', 'Nhiếp ảnh gia Urs Zihlmann ca ngợi Sơn Đoòng có vẻ đẹp đến nghẹt thở, và tới đây anh như lạc bước vào một thế giới khác.', 'http://img.f33.dulich.vnecdn.net/2016/06/01/3-1464746461_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/son-doong-duoi-ong-kinh-nhiep-anh-gia-thuy-si-3412283.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('868', 'Trải nghiệm sống cùng bộ tộc ăn thịt người', 'N/a', '2016-06-04 16:50:09', '2016-06-01 12:03:00', 'Sống cùng thổ dân bộ tộc Korowai, Paul khám phá được nhiều bí ẩn đằng sau tục ăn thịt người man rợ.', 'http://img.f33.dulich.vnecdn.net/2016/05/31/hangingoutwithcannibalsgeorgiarose377bodyimage1417631160-1464687178_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/dau-chan/trai-nghiem-song-cung-bo-toc-an-thit-nguoi-3411943.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('869', 'Bánh bạch tuộc ‘làm mưa làm gió’ ở đường phố Sài Gòn', 'N/a', '2016-06-04 16:50:09', '2016-06-01 10:31:00', 'Có xuất xứ từ Nhật Bản, món ăn thơm ngon với vị khác lạ này gần đây đã thu hút đông đảo bạn trẻ ở Sài Gòn.', 'http://img.f34.dulich.vnecdn.net/2016/05/31/Anh1-3400-1464689691_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/ho-chi-minh/banh-bach-tuoc-lam-mua-lam-gio-o-duong-pho-sai-gon-3412071.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('870', 'Sydney lung linh trong lễ hội ánh sáng', 'N/a', '2016-06-04 16:50:09', '2016-06-01 19:00:00', 'Chương trình ánh sáng rực rỡ sắc màu là một phần của lễ hội \"ánh sáng, âm nhạc và ý tưởng\" - Vivid Sydney diễn ra trong 3 tuần từ 27/5 đến 18/6.', 'http://img.f35.dulich.vnecdn.net/2016/05/31/7-1464664003_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/sydney-lung-linh-trong-le-hoi-anh-sang-3411727.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('871', 'Du khách suýt chết tại bể bơi trong nhà', 'N/a', '2016-06-04 16:50:09', '2016-06-01 15:03:00', 'Một nhóm du khách đang bơi thì bị một mảng trần rộng rơi từ trần nhà xuống, nhưng may mắn không ai bị thương.', 'http://img.f35.dulich.vnecdn.net/2016/06/01/1-1464742394_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/du-khach-suyt-chet-tai-be-boi-trong-nha-3412195.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('872', '5 sở thú ở Singapore khiến trẻ em thích mê', 'N/a', '2016-06-04 16:50:09', '2016-06-01 14:05:00', 'Sở thú ở Singapore không chỉ là nơi bé tham quan các loài hoang dã mà còn được thử nhiều trải nghiệm tương tác như cho động vật ăn và dùng bữa cùng chúng.', 'http://img.f34.dulich.vnecdn.net/2016/05/31/1-5330-1464670768_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/singapore/5-so-thu-o-singapore-khien-tre-em-thich-me-3411732.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('873', 'Cầu đáy kính dài nhất thế giới hoàn thành', 'N/a', '2016-06-04 16:50:09', '2016-06-01 08:09:00', 'Cây cầu kiệt tác có sức chứa 800 người một lúc vốn không phải là điểm đến dành cho những người yếu tim.', 'http://img.f35.dulich.vnecdn.net/2016/06/01/bottom-glass-bridge-5323-1464741154_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/cau-day-kinh-dai-nhat-the-gioi-hoan-thanh-3412180.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('874', 'Hang động mới ở Quảng Bình mở cửa cho khách du lịch', 'N/a', '2016-06-04 16:50:09', '2016-06-01 09:00:00', 'Bên cạnh khai thác mới tuyến hang Va - hang Nước Nứt, Quảng Bình cũng mở rộng các tuyến cũ như xuyên động Thiên Đường, hang Tối.', 'http://img.f35.dulich.vnecdn.net/2016/05/31/IMG-8823-1434437197-660x0-1799-1464689070_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/quang-binh/hang-dong-moi-o-quang-binh-mo-cua-cho-khach-du-lich-3411649.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('875', 'Mối ân tình giữa GS Trần Thanh Vân và các nhà khoa học Nobel', 'N/a', '2016-06-04 16:50:20', '2016-06-04 11:48:33', '21 giáo sư Nobel toàn thế giới đã thành công thông qua hội nghị gặp gỡ do giáo sư Trần Thanh Vân tổ chức. Vì mối quan hệ này nên khi ông mời đến Việt Nam, họ đều sẵn sàng. ', 'http://img.f29.vnecdn.net/2016/06/04/tranthanhvan1-7546-1465013576_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/moi-an-tinh-giua-gs-tran-thanh-van-va-cac-nha-khoa-hoc-nobel-3414170.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('876', 'Máy làm kem 15 triệu của sinh viên Bách khoa', 'N/a', '2016-06-04 16:50:20', '2016-06-04 08:46:48', 'Từ sản phẩm chế tạo, các chàng trai Đại học Bách khoa tự tay cuộn những ly kem mát lạnh mời thầy cô, bạn bè.', 'http://img.f30.vnecdn.net/2016/06/03/lamkem-8313-1464950177_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/may-lam-kem-15-trieu-cua-sinh-vien-bach-khoa-3413833.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('877', 'Điều gì xảy ra khi nhịn hắt hơi', 'N/a', '2016-06-04 16:50:20', '2016-06-04 08:13:23', 'Hắt hơi là phản xạ của cơ thể nhằm loại bỏ các dị vật ra khỏi đường hô hấp, do đó nhịn hắt hơi có thể gây ra nhiều tác hại xấu đến sức khỏe.', 'http://img.f31.vnecdn.net/2016/06/04/nh-1465000417_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/dieu-gi-xay-ra-khi-nhin-hat-hoi-3414072.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('878', 'Đây là giống chó gì?', 'N/a', '2016-06-04 16:50:20', '2016-06-04 06:00:00', 'Dì tôi đi nước ngoài, gửi lại con cho này cho tôi chăm sóc. Xin hỏi đây là giống chó gì? (Dai Dong)', 'http://img.f31.vnecdn.net/2016/06/03/image1464922719-1464949727_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/day-la-giong-cho-gi-3413949.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('879', 'Quái vật biển cổ dài thống trị đại dương kỷ Jura', 'N/a', '2016-06-04 16:50:20', '2016-06-03 21:00:00', 'Các nhà khoa học Anh tìm thấy bộ xương hóa thạch 165 triệu năm tuổi của một loài vật ở kỷ Jura thuộc họ xà đầu long, có kích thước khổng lồ và nhiều đặc điểm chưa từng thấy trước đây.', 'http://img.f29.vnecdn.net/2016/06/03/Hinh1-1464947577_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/quai-vat-bien-co-dai-thong-tri-dai-duong-ky-jura-3413926.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('880', 'Những loài hoa có độc tính mạnh nhất trong tự nhiên', 'N/a', '2016-06-04 16:50:20', '2016-06-03 20:00:00', 'Đỗ quyên, thủy tiên hay tử đằng là những loài hoa đẹp quyến rũ, nhưng lại có độc tính rất mạnh.', 'http://img.f29.vnecdn.net/2016/06/03/hoa4-1464941485_180x108.jpg', 'http://vnexpress.net/photo/moi-truong/nhung-loai-hoa-co-doc-tinh-manh-nhat-trong-tu-nhien-3413817.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('881', 'Tủ lạnh không dùng điện giữ thực phẩm tươi sống gần một tháng', 'N/a', '2016-06-04 16:50:20', '2016-06-03 15:44:05', 'Zeer Pot là giải pháp lý tưởng thay thế tủ lạnh thông thường ở những khu vực nắng nóng và không có điện.', 'http://img.f30.vnecdn.net/2016/06/03/VNEPot-1464941770_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/san-pham-cong-nghe/tu-lanh-khong-dung-dien-giu-thuc-pham-tuoi-song-gan-mot-thang-3413849.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('882', 'Sáng kiến biến giao lộ tử thần thành nơi an toàn', 'N/a', '2016-06-04 16:50:20', '2016-06-03 15:24:27', 'Ngã tư giữa con đường Hollywood Boulevard và Highland Avenue nổi tiếng là một trong những giao lộ nguy hiểm nhất đối với người đi bộ ở Los Angeles, Mỹ.', 'http://img.f32.vnecdn.net/2016/06/03/VNELADOT-1464939572_180x108.png', 'http://vnexpress.net/tin-tuc/khoa-hoc/sang-kien-bien-giao-lo-tu-than-thanh-noi-an-toan-3413552.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('883', '7 nhà khoa học danh tiếng đến Việt Nam', 'N/a', '2016-06-04 16:50:20', '2016-06-03 12:17:54', 'Hàng trăm nhà khoa học quốc tế, trong đó có giáo sư Ngô Bảo Châu và 6 người đạt giải Nobel sẽ tới Việt Nam tham dự \"Gặp gỡ Việt Nam\" lần thứ 12 tại Bình Định.', 'http://img.f32.vnecdn.net/2016/06/03/ngobaochau-2843-1464930025_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trong-nuoc/7-nha-khoa-hoc-danh-tieng-den-viet-nam-3413695.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('884', 'Ca phẫu thuật cắt bỏ khối u cho cá vàng', 'N/a', '2016-06-04 16:50:20', '2016-06-03 12:18:14', 'Đội ngũ phẫu thuật viên phải đưa con cá vàng ra ngoài môi trường nước trong ca phẫu thuật 45 phút với chi phí gần 300 USD.', 'http://img.f31.vnecdn.net/2016/06/03/ca-vang-vuot-300-km-phau-thuat-cat-khoi-u-to-bang-co-the-1464928300_180x108.jpg', 'http://vnexpress.net/photo/khoa-hoc/ca-phau-thuat-cat-bo-khoi-u-cho-ca-vang-3413680.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('885', 'Vật chất tối được tạo ra từ lỗ đen', 'N/a', '2016-06-04 16:50:20', '2016-06-03 12:05:43', 'Nghiên cứu mới của các nhà khoa học Mỹ cho thấy vật chất tối được tạo ra từ lỗ đen nguyên thủy, xuất hiện trong một phần nghìn giây đầu tiên sau vụ nổ Big Bang.', 'http://img.f32.vnecdn.net/2016/06/03/anh1-1464917508_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/vat-chat-toi-duoc-tao-ra-tu-lo-den-3413477.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('886', 'Đôi rắn treo ngược trên trần nhà dân để giao phối', 'N/a', '2016-06-04 16:50:20', '2016-06-03 12:03:22', 'Một người đàn ông ở South Carolina, Mỹ vô cùng bất ngờ khi bắt gặp hai con rắn treo mình trên trần nhà giao phối trong tư thế xoắn chặt vào nhau.', 'http://img.f32.vnecdn.net/2016/06/03/CaptureJPG-1464924397_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/chuyen-la/doi-ran-treo-nguoc-tren-tran-nha-dan-de-giao-phoi-3413488.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('887', 'Nắng màu gì?', 'N/a', '2016-06-04 16:50:20', '2016-06-03 08:06:53', 'Xin hỏi nắng có màu gì? (Song Thanh)', 'http://img.f29.vnecdn.net/2016/06/03/img20160602115334hdr1464865509-1464915836_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/hoi-dap/nang-mau-gi-3413441.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('888', 'Những cuộn sách Biển Chết cuối cùng ở Hang Đầu lâu', 'N/a', '2016-06-04 16:50:20', '2016-06-03 07:00:00', 'Cơ quan Cổ vật Israel phát động cuộc khai quật khảo cổ quy mô rộng ở cụm hang động trên sa mạc Judea nhằm tìm kiếm những cuộn sách Biển Chết cuối cùng.', 'http://img.f29.vnecdn.net/2016/06/02/VNEDeadSeaScrolls1-1464859604_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/nhung-cuon-sach-bien-chet-cuoi-cung-o-hang-dau-lau-3409959.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('889', 'Bên trong những cơ sở đông lạnh xác chờ hồi sinh', 'N/a', '2016-06-04 16:50:20', '2016-06-02 22:00:00', 'Đông lạnh xác là nỗ lực nhằm cứu sống con người bằng cách đóng băng cơ thể bệnh nhân mắc chứng nan y mà y học hiện nay chưa có cách chữa trị.', 'http://img.f31.vnecdn.net/2016/06/02/VNE-Cryonic-4-1464860657_180x108.jpg', 'http://vnexpress.net/photo/ky-thuat-moi/ben-trong-nhung-co-so-dong-lanh-xac-cho-hoi-sinh-3413311.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('890', 'Những hầm vàng bất khả xâm phạm trên thế giới', 'N/a', '2016-06-04 16:50:20', '2016-06-02 20:00:00', 'Độ kiên cố của những căn hầm chứa vàng lớn nhất thế giới đủ đập tan mọi ý đồ xâm phạm của kẻ trộm.', 'http://img.f32.vnecdn.net/2016/06/02/4-1464854164_180x108.jpg', 'http://vnexpress.net/photo/khoa-hoc/nhung-ham-vang-bat-kha-xam-pham-tren-the-gioi-3413148.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('891', 'Núi lửa có cấu tạo như thế nào', 'N/a', '2016-06-04 16:50:20', '2016-06-02 15:42:33', 'Núi lửa có thể phun trào một lượng khổng lồ dung nham nóng chảy cùng khí tích tụ dưới lòng đất, đe dọa chôn vùi mọi sự sống ở xung quanh miệng núi.', 'http://img.f29.vnecdn.net/2016/06/02/nui-lua-co-cau-tao-nhu-the-nao-1464851320_180x108.jpg', 'http://vnexpress.net/infographics/moi-truong/nui-lua-co-cau-tao-nhu-the-nao-3413103.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('892', 'Dải Ngân Hà có khối lượng lớn gấp 700 tỷ lần Mặt Trời', 'N/a', '2016-06-04 16:50:20', '2016-06-02 15:00:00', 'Phép đo chính xác nhất do các nhà khoa học Canada tiến hành cho thấy tổng khối lượng các ngôi sao, hố đen, bụi, vật chất tối và vật thể bay không xác định của dải Ngân Hà lớn gấp 700 tỷ lần Mặt Trời.', 'http://img.f32.vnecdn.net/2016/06/02/VNEMilkyWay-1464849478_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/dai-ngan-ha-co-khoi-luong-lon-gap-700-ty-lan-mat-troi-3413088.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('893', 'Trứng \'rồng non\' trong hang động nở sau một thập kỷ chờ đợi', 'N/a', '2016-06-04 16:50:20', '2016-06-02 13:57:00', 'Hàng triệu du khách vỡ òa trong niềm xúc động khi chứng kiến hai ấu trùng nở ra từ 500 quả trứng của con manh giông mẹ, loài động vật lưỡng cư kỳ lạ và quý hiếm với biệt danh \"rồng non\".', 'http://img.f32.vnecdn.net/2016/06/02/VNEOlm1-1464842528_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/trung-rong-non-trong-hang-dong-no-sau-mot-thap-ky-cho-doi-3412855.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('894', 'Quá khứ đau buồn của chú khỉ đột bị vườn thú Mỹ bắn chết', 'N/a', '2016-06-04 16:50:20', '2016-06-02 11:49:04', 'Khỉ đột 17 tuổi Harambe bị bắn chết để cứu một bé trai ngã vào chuồng khỉ, từng mất mẹ và em trai trong sự cố rò rỉ khí độc cách đây 15 năm tại vườn thú khác.', 'http://img.f30.vnecdn.net/2016/06/02/VNEHarambe3-1464835840_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/qua-khu-dau-buon-cua-chu-khi-dot-bi-vuon-thu-my-ban-chet-3412879.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('895', 'UFO xuất hiện gần khu căn cứ quân sự của Mỹ', 'N/a', '2016-06-04 16:50:20', '2016-06-02 14:48:10', 'Một vật thể bay bí ẩn xuất hiện gần khu căn cứ quân sự của Mỹ, làm dấy lên nghi ngờ về sự hiện diện của người ngoài hành tinh.', 'http://img.f30.vnecdn.net/2016/06/02/nh-1464852204_180x108.png', 'http://vnexpress.net/tin-tuc/khoa-hoc/chuyen-la/ufo-xuat-hien-gan-khu-can-cu-quan-su-cua-my-3413113.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('896', 'Túi chống trộm siêu bền dao rạch không thủng', 'N/a', '2016-06-04 16:50:20', '2016-06-02 10:46:24', 'Một công ty công nghệ ở Mỹ cho ra đời loại túi chống trộm làm từ chất liệu vải đặc biệt không thể cắt, rạch và trang bị kỹ thuật chống đánh cắp dữ liệu tiên tiến nhất thế giới.', 'http://img.f31.vnecdn.net/2016/06/02/VNEBag-1464828801_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/san-pham-cong-nghe/tui-chong-trom-sieu-ben-dao-rach-khong-thung-3412812.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('897', 'Tinh trùng khỉ đột bị bắn chết ít khả năng dùng để nhân giống', 'N/a', '2016-06-04 16:50:20', '2016-06-02 09:31:45', 'Mẫu tinh trùng thu được từ khỉ đột bị bắn chết có thể chỉ dược dùng cho mục đích nghiên cứu.', 'http://img.f31.vnecdn.net/2016/06/02/kgf-1464832142_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/tinh-trung-khi-dot-bi-ban-chet-it-kha-nang-dung-de-nhan-giong-3412773.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('898', 'Những sinh vật lạ trong hang đầy khí độc 5,5 triệu năm', 'N/a', '2016-06-04 16:50:20', '2016-06-02 07:00:00', 'Môi trường ẩm ướt chứa đầy khí cực độc với bóng tối ngự trị suốt 5,5 triệu năm mang đến cho hang động Movile ở Romania nhiều đặc điểm kỳ lạ.', 'http://img.f31.vnecdn.net/2016/06/01/anh1-1464776009_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/nhung-sinh-vat-la-trong-hang-day-khi-doc-5-5-trieu-nam-3412661.html', '6', '7', '0', '0', '1', null, '54');
INSERT INTO `posts` VALUES ('899', 'Đại học Kinh doanh và Công nghệ tuyển sinh Y đa khoa và Dược', 'N/a', '2016-06-04 16:50:31', '2016-06-04 09:02:01', 'Trường tuyển sinh 200 chỉ tiêu đào tạo Y đa khoa và 200 chỉ tiêu Dược học với mức điểm nhận hồ sơ xét tuyển là 20.', 'http://img.f31.vnecdn.net/2016/06/04/kd-va-cn-3374-1449062501-2309-1465002884_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/dai-hoc-kinh-doanh-va-cong-nghe-tuyen-sinh-y-da-khoa-va-duoc-3414111.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('900', 'Truyện Kiều vào đề Văn lớp 10 chuyên Ngoại ngữ', 'N/a', '2016-06-04 16:50:31', '2016-06-04 12:09:52', 'Cho hai đoạn thơ thuộc phần trích Cảnh ngày xuân trong Truyện Kiều của Nguyễn Du, đề thi môn Ngữ văn vào lớp 10 sáng 4/6 trường chuyên Ngoại ngữ (Đại học Ngoại ngữ, Đại học Quốc gia Hà Nội) yêu cầu thí sinh nêu cảm nhận để lấy 6 điểm.', 'http://img.f32.vnecdn.net/2016/06/04/de3-1465016616_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/truyen-kieu-vao-de-van-lop-10-chuyen-ngoai-ngu-3414316.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('901', 'Những sinh viên mê công nghệ', 'N/a', '2016-06-04 16:50:31', '2016-06-04 00:00:00', 'Chứng kiến nhiều ý tưởng khởi nghiệp không thể áp dụng trong thực tế vì thiếu hiểu biết về công nghệ, Hoàng Việt Hưng quyết tâm học thêm IT.', 'http://img.f30.vnecdn.net/2016/06/03/HUNG-3071-1464915485-4057-1464918626_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/nhung-sinh-vien-me-cong-nghe-3413207.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('902', 'Học bổng một tỷ đồng cho chương trình MBA', 'N/a', '2016-06-04 16:50:31', '2016-06-04 09:00:00', 'Mỗi suất học bổng trị giá 91 triệu đồng, dành cho những ai theo học thạc sĩ Quản trị kinh doanh chuẩn Mỹ của Đại học Quốc tế Sài Gòn.', 'http://img.f31.vnecdn.net/2016/06/02/2-6-201618-2956-1464844560_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-bong-mot-ty-dong-cho-chuong-trinh-mba-3413006.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('903', 'Tỷ lệ chọi lớp 10 chuyên Khoa học tự nhiên, Ngoại ngữ', 'N/a', '2016-06-04 16:50:31', '2016-06-03 19:30:00', 'Hàng nghìn thí sinh dự thi trong khi chỉ tiêu tuyển sinh vài trăm khiến tỷ lệ chọi vào các trường chuyên Ngoại ngữ, chuyên Khoa học tự nhiên (Đại học Quốc gia Hà Nội) còn cao hơn một số đại học top đầu.', 'http://img.f29.vnecdn.net/2016/06/03/PNTA5212-JPG-8364-1464953457_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ty-le-choi-lop-10-chuyen-khoa-hoc-tu-nhien-ngoai-ngu-3413970.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('904', 'Ông Đinh La Thăng chỉ đạo ổn định trường Đại học Hoa Sen', 'N/a', '2016-06-04 16:50:31', '2016-06-03 12:53:00', 'Bí thư Thành ủy TP HCM yêu cầu các sở, ngành nhanh chóng ổn định tình hình, không làm ảnh hưởng đến hoạt động chung tại Đại học Hoa Sen', 'http://img.f29.vnecdn.net/2016/06/03/12727281461457424911-1464933276_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/ong-dinh-la-thang-chi-dao-on-dinh-truong-dai-hoc-hoa-sen-3413691.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('905', 'Bob Kerrey: \'Tôi muốn hành động để bù đắp đau thương\'', 'N/a', '2016-06-04 16:50:31', '2016-06-03 12:46:03', 'Hướng tới một tương lai tốt đẹp hơn tại Việt Nam qua dự án Đại học Fulbright, ông Bob Kerrey muốn bù đắp phần nào đau thương đã gây ra trong vụ thảm sát.', 'http://img.f30.vnecdn.net/2016/06/03/Bob-Kerrey-ok-5339-1464932451_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bob-kerrey-toi-muon-hanh-dong-de-bu-dap-dau-thuong-3413362.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('906', 'Kỷ yếu tái hiện thời học sinh thập niên 80', 'N/a', '2016-06-04 16:50:31', '2016-06-03 11:34:10', '33 học sinh lớp 12C2 trường THPT Tân Kỳ (Nghệ An) đã tái hiện thời học sinh những năm 80-90 thế kỷ trước với áo cánh, quần loe, nữ thắt bím tóc hai bên.', 'http://img.f29.vnecdn.net/2016/06/03/bo-anh-ky-yeu-tai-hien-thoi-hoc-sinh-thap-nien-80-1464919067_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/ky-yeu-tai-hien-thoi-hoc-sinh-thap-nien-80-3413495.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('907', '100% học sinh Đà Nẵng sẽ được học bơi', 'N/a', '2016-06-04 16:50:31', '2016-06-03 08:37:03', 'Đà Nẵng đặt mục tiêu đến năm 2017, 100% trường học sẽ được lắp đặt bể bơi di động và tất cả học sinh được học bơi. ', 'http://img.f31.vnecdn.net/2016/06/02/boi3-1464859724_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/100-hoc-sinh-da-nang-se-duoc-hoc-boi-3413267.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('908', 'Học sinh Việt thuở đội mũ rơm đến trường', 'N/a', '2016-06-04 16:50:31', '2016-06-03 01:00:00', 'Hành trang đến trường của học sinh thời chiến ngoài sách vở còn có cáng cứu thương, xẻng, cuốc để đào hầm và một chiếc mũ rơm tránh bom đạn.', 'http://img.f30.vnecdn.net/2016/06/02/mu-rom-di-hoc-1464866805_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/hoc-sinh-viet-thuo-doi-mu-rom-den-truong-3413293.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('909', 'TP HCM có 67 điểm thi THPT quốc gia', 'N/a', '2016-06-04 16:50:31', '2016-06-03 15:55:35', 'Kỳ thi THPT quốc gia năm nay Sài Gòn có 5 trường đại học chủ trì các cụm thi với 67 điểm trên địa bàn.', 'http://img.f32.vnecdn.net/2016/06/03/giaoduc37791144672198841261450314997-1464943432_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/tp-hcm-co-67-diem-thi-thpt-quoc-gia-3413799.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('910', 'Sống ảo vào đề Văn trường chuyên Sư phạm Hà Nội', 'N/a', '2016-06-04 16:50:31', '2016-06-03 14:39:28', 'Cho đoạn trích khoảng 10 câu trong tác phẩm \"Nếu biết trăm năm là hữu hạn\" của Phạm Lữ Ân, đề Văn trường chuyên THPT chuyên Sư phạm (Đại học Sư phạm Hà Nội) yêu cầu thí sinh viết suy nghĩ về việc nhiều bạn trẻ sa vào sống ảo, ham mê Facebook mà quên mất cuộc đời thực.', 'http://img.f31.vnecdn.net/2016/06/03/song-ao-vo-tinh-voi-nguoi-than-vao-de-thi-van-chuyen-su-pham-1464938161_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/song-ao-vao-de-van-truong-chuyen-su-pham-ha-noi-3413753.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('911', 'Đề và đáp án môn Toán thi vào lớp 10 chuyên Sư phạm Hà Nội', 'N/a', '2016-06-04 16:50:31', '2016-06-03 13:50:01', 'Sáng 3/6, khoảng 5.000 học sinh làm bài thi môn Toán, tiếp tục cuộc chạy đua giành suất vào học trường THPT chuyên Đại học sư phạm Hà Nội.', 'http://img.f32.vnecdn.net/2016/06/03/de-thi-va-dap-an-mon-toan-thi-vao-lop-10-chuyen-dai-hoc-su-pham-1464932794_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/de-va-dap-an-mon-toan-thi-vao-lop-10-chuyen-su-pham-ha-noi-3413713.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('912', 'Máy tính nào được phép đem vào phòng thi THPT quốc gia', 'N/a', '2016-06-04 16:50:31', '2016-06-03 08:53:21', 'Các máy tính không có chức năng soạn thảo văn bản và không có thẻ nhớ để lưu dữ liệu đều được phép mang vào phòng thi.', 'http://img.f30.vnecdn.net/2016/06/02/DSC-3269-5420-1404909137-7698-6644-3201-1464861496_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/may-tinh-nao-duoc-phep-dem-vao-phong-thi-thpt-quoc-gia-3413283.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('913', 'Bộ Giáo dục yêu cầu xử lý hiện tượng chạy trường, chạy lớp', 'N/a', '2016-06-04 16:50:31', '2016-06-03 08:49:09', 'Bộ Giáo dục và Đào tạo nghiêm cấm việc tổ chức các trường chuyên, lớp chọn ở cấp mầm non, tiểu học và trung học cơ sở, đồng thời yêu cầu thanh tra, xử lý nghiêm khắc hiện tượng tiêu cực nhằm chạy trường, chạy lớp.', 'http://img.f29.vnecdn.net/2016/06/02/xinhoc2-1354205868-500x0-6213-1464860930_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/bo-giao-duc-yeu-cau-xu-ly-hien-tuong-chay-truong-chay-lop-3413276.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('914', 'Cách phát âm duy nhất của chữ Z', 'N/a', '2016-06-04 16:50:31', '2016-06-03 01:00:00', 'Z là chữ cái ít xuất hiện nhất trong các từ vựng tiếng Anh và cũng là một trong những chữ có cách phát âm đơn giản nhất.', 'http://img.f30.vnecdn.net/2016/05/31/131026avatarchucaizluachay-1464682469_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hoc-tieng-anh/cach-phat-am-duy-nhat-cua-chu-z-3411942.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('915', 'Giảng viên 84 tuổi thành thạo công nghệ dạy học online', 'N/a', '2016-06-04 16:50:31', '2016-06-03 00:00:00', 'Giáo sư W.Wendt có tài khoản Twitter lẫn Facebook, thường viết email bằng iPhone 6, giảng bài bằng slide PowerPoint và thu âm giọng nói của mình để dạy online.', 'http://img.f31.vnecdn.net/2016/06/02/23b1a1jpeg20361463975612-1464867261_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/giang-vien-84-tuoi-thanh-thao-cong-nghe-day-hoc-online-3413273.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('916', 'Trường nào dạy công nghệ thông tin mà không phải thi', 'N/a', '2016-06-04 16:50:31', '2016-06-03 10:25:00', 'Em chuẩn bị kết thúc chương trình lớp 12, nhưng gần đây đầu óc lúc nào cũng căng thẳng, mất ngủ thường xuyên vì nỗi lo vào đại học.', 'http://img.f29.vnecdn.net/2016/06/03/IMG9633JPG-1464918597_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/truong-nao-day-cong-nghe-thong-tin-ma-khong-phai-thi-3413448.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('917', 'Sinh viên an ninh giúp dân gặt lúa', 'N/a', '2016-06-04 16:50:31', '2016-06-02 21:27:59', 'Hơn 400 sinh viên Học viện An ninh nhân dân vừa kết thúc nửa tháng cùng ăn, cùng ở, cùng làm với người dân Chiêm Hoá, Tuyên Quang.', 'http://img.f29.vnecdn.net/2016/06/02/sv-1464863926_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/sinh-vien-an-ninh-giup-dan-gat-lua-3413318.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('918', 'Giấy khen \'từng mặt\' đánh đố phụ huynh, học sinh', 'N/a', '2016-06-04 16:50:31', '2016-06-02 17:32:31', 'Học sinh lớp 1 ở Hà Nội nhận được giấy khen cuối năm ghi \'Đạt danh hiệu học sinh khen từng mặt\' khiến phụ huynh không hiểu con mình được khen gì.', 'http://img.f31.vnecdn.net/2016/06/02/top-1464859413_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/giay-khen-tung-mat-danh-do-phu-huynh-hoc-sinh-3413198.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('919', '5.000 học sinh chạy đua vào lớp 10 chuyên Sư phạm Hà Nội', 'N/a', '2016-06-04 16:50:31', '2016-06-02 12:20:23', 'Ngày 2/6, Trường THPT chuyên Sư phạm (Đại học Sư phạm Hà Nội) bắt đầu tuyển 480 học sinh vào lớp 10 nhưng lượng thí sinh dự thi đông gấp 10 lần.', 'http://img.f29.vnecdn.net/2016/06/02/PNTA5204-1464839955_180x108.jpg', 'http://vnexpress.net/photo/giao-duc/5-000-hoc-sinh-chay-dua-vao-lop-10-chuyen-su-pham-ha-noi-3413031.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('920', 'Hai học sinh đạt giải quốc tế với đề tài nghiên cứu chữa ung thư', 'N/a', '2016-06-04 16:50:31', '2016-06-02 11:03:14', 'Với đề tài \"Nghiên cứu khả năng gắn và tiêu diệt một số tế bào ung thư của kháng thể nhân\", hai học sinh tại Đà Lạt đã mang về giải Ba cho đoàn Việt Nam tại Hội thi khoa học kỹ thuật Quốc tế vừa diễn ra tại Mỹ.', 'http://img.f29.vnecdn.net/2016/06/02/hocsinh-1464838108_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/hai-hoc-sinh-dat-giai-quoc-te-voi-de-tai-nghien-cuu-chua-ung-thu-3412969.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('921', 'Thành \'chuyên gia\' sửa máy tính nhờ mê game', 'N/a', '2016-06-04 16:50:31', '2016-06-02 01:02:00', 'Từ những lần cài Win thất bại, xóa sạch dữ liệu quan trọng của anh trai..., Đinh Thế Tùng dần hiểu cách vận hành của chiếc máy tính so với bạn bè đồng trang lứa.', 'http://img.f30.vnecdn.net/2016/06/01/12-1464755435_180x108.jpg', 'http://vnexpress.net/tin-tuc/giao-duc/cong-dan-so/thanh-chuyen-gia-sua-may-tinh-nho-me-game-3412247.html', '6', '4', '0', '0', '1', null, '56');
INSERT INTO `posts` VALUES ('922', 'Trailer bom tấn 160 triệu USD \'Warcraft\' của Ngô Ngạn Tổ hot nhất tuần', 'N/a', '2016-06-04 16:50:40', '2016-06-04 12:32:00', 'Phim giả tưởng có sự tham gia của tài tử Hoa ngữ gây sốt với fan châu Á khi vừa ra mắt ở Bắc Kinh hôm 1/6.', 'http://img.f11.giaitri.vnecdn.net/2016/06/04/ngo-ngan-to-warcraft-9912-1465010862_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/diem-phim/trailer-bom-tan-160-trieu-usd-warcraft-cua-ngo-ngan-to-hot-nhat-tuan-3414141.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('923', 'Chi Pu diện váy cưới ren xuyên thấu', 'N/a', '2016-06-04 16:50:40', '2016-06-04 10:44:46', 'Nữ diễn viên phim \"Yêu\" trở thành \"nàng thơ\" truyền tải nét bay bổng trong các mẫu váy cưới của Chung Thanh Phong.', 'http://img.f11.giaitri.vnecdn.net/2016/06/04/chi-pu-hoa-co-dau-quyen-ru-trong-vay-cuoi-ren-1465009214_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/chi-pu-dien-vay-cuoi-ren-xuyen-thau-3414200.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('924', 'Nhan sắc 8 nàng Hoàng Dung của \'Anh hùng xạ điêu\'', 'N/a', '2016-06-04 16:50:40', '2016-06-04 00:07:00', 'Châu Tấn, Chu Nhân, Ông Mỹ Linh ghi dấu ấn với vai nữ chính trong \"Anh hùng xạ điêu\".', 'http://img.f11.giaitri.vnecdn.net/2016/06/03/tinh-2957-1464920358_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/nhan-sac-8-nang-hoang-dung-cua-anh-hung-xa-dieu-3413118.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('925', 'Sao phim \'Glee\' không thừa nhận tội ấu dâm trong phiên luận tội', 'N/a', '2016-06-04 16:50:40', '2016-06-04 10:30:31', 'Mark Salling không nhận tội khiêu dâm trẻ em và phiên xét xử sẽ diễn ra ngày 12/7.', 'http://img.f9.giaitri.vnecdn.net/2016/06/04/embedmarksallingt-1465010367_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/quoc-te/sao-phim-glee-khong-thua-nhan-toi-au-dam-trong-phien-luan-toi-3414179.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('926', 'Angelina Jolie khoe nét xuân thì trong bộ ảnh năm 15 tuổi', 'N/a', '2016-06-04 16:50:40', '2016-06-04 09:54:21', 'Bộ hình được thực hiện khi minh tinh Hollywood là cô gái tuổi teen và được biết đến với danh nghĩa con gái của nam diễn viên Jon Voight.', 'http://img.f9.giaitri.vnecdn.net/2016/06/04/angelina-jolie-khoe-net-xuan-thi-trong-bo-anh-nam-15-tuoi-1465008861_180x108.jpg', 'http://giaitri.vnexpress.net/photo/quoc-te/angelina-jolie-khoe-net-xuan-thi-trong-bo-anh-nam-15-tuoi-3414145.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('927', '\'Me Before You\' - phim lãng mạn khiến khán giả cười nhiều hơn khóc', 'N/a', '2016-06-04 16:50:40', '2016-06-04 05:10:00', 'Chuyển thể từ tiểu thuyết lãng mạn từng khiến hàng triệu con tim thổn thức, bộ phim của \"Mẹ Rồng\"Emilia Clark lại chứa nhiều khoảnh khắc hài hước và lạc quan.', 'http://img.f10.giaitri.vnecdn.net/2016/06/03/top5-1351-1464945392_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/diem-phim/me-before-you-phim-lang-man-khien-khan-gia-cuoi-nhieu-hon-khoc-3413797.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('928', 'Trịnh Kim Chi vào vai bà hoàng gặp nhiều sóng gió nơi cung cấm', 'N/a', '2016-06-04 16:50:40', '2016-06-04 12:04:25', 'Trong vở diễn \"Kỳ án Cung Tâm Kế\", nữ diễn viên đóng vai hoàng hậu quyến rũ phải hứng chịu bi kịch.', 'http://img.f11.giaitri.vnecdn.net/2016/06/04/trinh-kim-chi-top-6708-1465015522_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/san-khau-my-thuat/san-khau/trinh-kim-chi-vao-vai-ba-hoang-gap-nhieu-song-gio-noi-cung-cam-3414262.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('929', 'Vợ cũ Phan Thanh Bình khoe dáng với áo truyền thống', 'N/a', '2016-06-04 16:50:40', '2016-06-04 12:00:00', 'Thảo Trang làm mẫu cho trang phục áo dài của nhà thiết kế Minh Châu.', 'http://img.f9.giaitri.vnecdn.net/2016/06/04/vo-cu-thanh-binh-1465007528_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/vo-cu-phan-thanh-binh-khoe-dang-voi-ao-truyen-thong-3414178.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('930', 'Hoa hậu Hàn Quốc, Trung Quốc dự Đêm hội chân dài 10', 'N/a', '2016-06-04 16:50:40', '2016-06-04 11:39:13', 'Hai người đẹp chiến thắng trong cuộc thi Miss World Korea 2015 và Miss World China 2013 đang chuẩn bị váy áo để đọ sắc cùng các mỹ nhân showbiz Việt.', 'http://img.f9.giaitri.vnecdn.net/2016/06/04/top-9477-1465013142_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/hoa-hau-han-quoc-trung-quoc-du-dem-hoi-chan-dai-10-3414228.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('931', 'Ý Lan diện đồ tông đen trắng dự sự kiện', 'N/a', '2016-06-04 16:50:40', '2016-06-04 09:24:15', 'Con gái ca sĩ Thái Thanh khoe gu thời trang tại lễ ra mắt chương trình \"Người hát tình ca\" do cô làm giám khảo.', 'http://img.f9.giaitri.vnecdn.net/2016/06/03/IMG7984JPG-1464970609_1464970623_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/y-lan-dien-do-tong-den-trang-du-su-kien-3414009.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('932', 'Thí sinh viêm màng não khiến Thu Minh nhớ Trần Lập', 'N/a', '2016-06-04 16:50:40', '2016-06-04 09:20:05', 'Nữ ca sĩ cho biết phần trình diễn ca khúc tự sáng tác của chàng trai 24 tuổi tại Vietnam Idol khiến chị rưng rưng nhớ người đồng nghiệp.', 'http://img.f9.giaitri.vnecdn.net/2016/06/04/thu-minh-q-1514-1465002420-6904-1465007419_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/thi-sinh-viem-mang-nao-khien-thu-minh-nho-tran-lap-3414062.html', '6', '6', '0', '0', '1', null, '58');
