INSERT INTO `posts` VALUES ('1270', 'Bầu giải trí ở Mỹ bức xúc vì bị Trường Giang hủy sô', 'N/a', '2016-06-07 00:20:48', '2016-06-06 15:12:40', 'Bầu Tony Ninh nói việc bị Trường Giang tự ý bỏ sô ở Mỹ ảnh hưởng uy tín của ông, còn phía danh hài cho biết anh bỏ diễn vì không có Hoài Linh đi chung.', 'http://img.f9.giaitri.vnecdn.net/2016/06/06/truong-giang-top-9274-1465200060_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/trong-nuoc/bau-giai-tri-o-my-buc-xuc-vi-bi-truong-giang-huy-so-3414701.html', '6', '6', '0', '0', '1', null, '58');
