INSERT INTO `posts` VALUES ('1293', 'Ngọc Sơn gây phấn khích trong đêm nhạc Bolero', 'N/a', '2016-06-07 00:20:48', '2016-06-06 10:11:19', 'Danh ca khiến khán giả lắc lư theo các giai điệu được phối lại sôi động cùng các ca khúc quen thuộc của huyền thoại âm nhạc Michael Jackson.', 'http://img.f12.giaitri.vnecdn.net/2016/06/06/ngoc-son-gay-phan-khich-trong-dem-nhac-bolero-1465181626_180x108.jpg', 'http://giaitri.vnexpress.net/photo/lang-nhac/ngoc-son-gay-phan-khich-trong-dem-nhac-bolero-3414817.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('1294', 'Thư Kỳ ôn chuyện cũ cùng người tình tin đồn', 'N/a', '2016-06-07 00:20:48', '2016-06-06 09:12:55', 'Phùng Đức Luân cho biết anh gặp nữ diễn viên để trò chuyện tâm tình như hai người bạn chứ không phải đôi tình nhân.', 'http://img.f9.giaitri.vnecdn.net/2016/06/06/thu2-2961-1465177302_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/quoc-te/thu-ky-on-chuyen-cu-cung-nguoi-tinh-tin-don-3414805.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('1295', 'Người một chân trổ tài trượt patin gây xúc động', 'N/a', '2016-06-07 00:20:48', '2016-06-06 09:11:48', 'Chỉ một chân lành lặn, bên còn lại sử dụng chân giả, Đoàn Ngọc Bảo khéo léo trên giày patin, vượt chướng ngại vật ở sân chơi \'\"Người bí ẩn\".', 'http://img.f10.giaitri.vnecdn.net/2016/06/06/doan-ngoc-bao-1-7422-1465178162_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/nguoi-mot-chan-tro-tai-truot-patin-gay-xuc-dong-3414812.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('1296', 'Trang trí nail chủ đề \'Hương thời gian\'', 'N/a', '2016-06-07 00:20:48', '2016-06-06 08:00:00', 'Mẫu thiết kế là sự kết hợp hài hòa giữa gam màu sáng, đậm cùng điểm nhấn là hoa hồng và những viên pha lê lấp lánh.', 'http://img.f12.giaitri.vnecdn.net/2016/06/04/Image-278915750-ExtractWord-0-6875-5623-1465003544_180x108.jpeg', 'http://giaitri.vnexpress.net/tin-tuc/thoi-trang/lam-dep/trang-tri-nail-chu-de-huong-thoi-gian-3414096.html', '6', '6', '0', '0', '1', null, '58');
