INSERT INTO `posts` VALUES ('1298', 'Nhan sắc các Hoa hậu Việt Nam ngày ấy - bây giờ', 'N/a', '2016-06-07 00:20:48', '2016-06-05 16:37:38', 'Nhiều năm sau đăng quang, Thu Thủy, Hà Kiều Anh, Nguyễn Thị Huyền... vẫn được khen ngợi với vẻ ngoài không mấy thay đổi.', 'http://img.f11.giaitri.vnecdn.net/2016/06/04/nhan-sac-cac-hoa-hau-viet-nam-ngay-ay-bay-gio-1465019980_180x108.jpg', 'http://giaitri.vnexpress.net/photo/sao-dep-sao-xau/nhan-sac-cac-hoa-hau-viet-nam-ngay-ay-bay-gio-3414328.html', '6', '6', '0', '0', '1', null, '58');
INSERT INTO `posts` VALUES ('1299', 'Vĩnh Thụy tình tứ hôn trán Hoàng Thùy Linh', 'N/a', '2016-06-07 00:20:48', '2016-06-05 22:44:10', 'Xuất hiện với ngoại hình quyến rũ, cả hai thể hiện nhiều cử chỉ thân mật trong buổi ra mắt MV \"I\'m gonna break\".', 'http://img.f11.giaitri.vnecdn.net/2016/06/05/vinh-thuy-tinh-tu-hon-tran-hoang-thuy-linh-1465138872_180x108.jpg', 'http://giaitri.vnexpress.net/photo/trong-nuoc/vinh-thuy-tinh-tu-hon-tran-hoang-thuy-linh-3414686.html', '6', '6', '0', '0', '1', null, '58');
