INSERT INTO `posts` VALUES ('1303', 'Mai Phương Thúy và dàn diễn viên phim \'Âm tính\' sau 6 năm', 'N/a', '2016-06-07 00:20:48', '2016-06-05 00:20:00', 'Hoa hậu Việt Nam 2006 thưa dần các hoạt động giải trí, Ngọc Lan tìm được hạnh phúc sau hai mối tình không thành, còn Kim Thoa ra nước ngoài sống nhiều năm nay.', 'http://img.f11.giaitri.vnecdn.net/2016/06/04/kllljj-1464931893_1464931900_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/mai-phuong-thuy-va-dan-dien-vien-phim-am-tinh-sau-6-nam-3413082.html', '6', '6', '0', '0', '1', null, '58');
