INSERT INTO `posts` VALUES ('1307', '\'Fast 8\' khoe hậu trường đâm xe và cháy nổ trên băng tuyết', 'N/a', '2016-06-07 00:20:48', '2016-06-05 10:04:21', 'Bom tấn của Vin Diesel tung video hậu trường đầu tiên hé lộ cảnh hành động quay ở Iceland.', 'http://img.f11.giaitri.vnecdn.net/2016/06/05/top3-1405-1465094842_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/fast-8-khoe-hau-truong-dam-xe-va-chay-no-tren-bang-tuyet-3414517.html', '6', '6', '0', '0', '1', null, '58');
