INSERT INTO `posts` VALUES ('1318', 'Đà Nẵng khởi tố vụ chìm tàu làm chết 3 người', 'N/a', '2016-06-07 00:21:07', '2016-06-06 16:43:13', 'Thủ trưởng cơ quan cảnh sát điều tra Đà Nẵng khẳng định sẽ xử lý đến cùng những người liên quan tai nạn chìm tàu trên sông Hàn để lại tai tiếng cho thành phố.', 'http://img.f30.vnecdn.net/2016/06/06/taudl3-1465204707_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/da-nang-khoi-to-vu-chim-tau-lam-chet-3-nguoi-3415221.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1319', 'Vì sao cảnh sát Mỹ nổ súng nhiều nhất thế giới', 'N/a', '2016-06-07 00:21:07', '2016-06-06 16:04:28', 'Tỷ lệ các vụ nổ súng gây tử vong của cảnh sát Mỹ cao gấp 18 lần so với cảnh sát Đan Mạch và 100 lần so với cảnh sát Phần Lan.', 'http://img.f31.vnecdn.net/2016/06/06/canhsatnosung-1465203635_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/vi-sao-canh-sat-my-no-sung-nhieu-nhat-the-gioi-3415206.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1320', 'Lao vào nhà dân, dí mảnh sành khống chế bé 18 tháng tuổi', 'N/a', '2016-06-07 00:21:07', '2016-06-06 15:13:49', 'Vỹ nhảy khỏi xe máy, lao vào nhà dân bế thốc bé gái đang chơi trước cửa mang lên tầng 2 cố thủ bên trong.', 'http://img.f30.vnecdn.net/2016/06/06/begai-1465200753_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/lao-vao-nha-dan-di-manh-sanh-khong-che-be-18-thang-tuoi-3415108.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1321', 'Kẻ đoạt mạng chủ nhà cướp đôi hoa tai bị khởi tố', 'N/a', '2016-06-07 00:21:07', '2016-06-06 14:55:29', 'Sau chầu nhậu ở nhà hàng xóm, Thắng đã sát hại bà lão chủ nhà hòng cướp đôi hoa tai bằng vàng, đâm trọng thương người chồng lao vào cứu vợ.', 'http://img.f31.vnecdn.net/2016/06/06/vithang60351464620892-1465194179_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/ke-doat-mang-chu-nha-cuop-doi-hoa-tai-bi-khoi-to-3415073.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1322', 'Thiếu úy công an lấy tiền tỷ của cơ quan cá độ bóng đá', 'N/a', '2016-06-07 00:21:07', '2016-06-06 14:34:25', 'Trong vòng hơn nửa năm, thiếu úy công an đã \"thụt két\" hơn 2,3 tỷ đồng của đơn vị để cá độ bóng đá qua mạng.', 'http://img.f30.vnecdn.net/2016/06/06/thieuuy-1465195509_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/thieu-uy-cong-an-lay-tien-ty-cua-co-quan-ca-do-bong-da-3415071.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1323', 'Hai mẹ con thiếu nữ bị \'yêu râu xanh\' hãm hại', 'N/a', '2016-06-07 00:21:07', '2016-06-06 13:03:50', 'Trong vòng 3 ngày, nam thanh niên 22 tuổi ở Đăk Lăk được cho đã giở trò đồi bại với hai mẹ con thiếu nữ 16 tuổi.', 'http://img.f29.vnecdn.net/2016/06/06/yeurauxanh1-1465191308_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/hai-me-con-thieu-nu-bi-yeu-rau-xanh-ham-hai-3414993.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1324', 'Nhận diện quân phục mới của công an', 'N/a', '2016-06-07 00:21:07', '2016-06-06 10:19:13', 'Hôm nay, công an trên toàn quốc bắt đầu mặc quân phục mới với một số chi tiết thay đổi, chức vụ sẽ được in luôn trên bảng tên.', 'http://img.f31.vnecdn.net/2016/06/06/nhan-dien-quan-phuc-moi-cua-cong-an-1465183985_180x108.jpg', 'http://vnexpress.net/photo/phap-luat/nhan-dien-quan-phuc-moi-cua-cong-an-3414842.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1325', 'Đặc nhiệm đạp ngã tên cướp sau cuộc truy đuổi', 'N/a', '2016-06-07 00:21:07', '2016-06-06 16:36:02', 'Giật sợi dây chuyền của người phụ nữ, tên cướp phóng xe bạt mạng trên đường để tẩu thoát nhưng bị cảnh sát đuổi theo đạp ngã.  ', 'http://img.f31.vnecdn.net/2016/06/06/cuop-1465205855_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/dac-nhiem-dap-nga-ten-cuop-sau-cuoc-truy-duoi-3415216.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1326', 'Hàng chục giang hồ đi ôtô, nổ súng thanh toán nhau', 'N/a', '2016-06-07 00:21:07', '2016-06-06 09:50:54', 'Nhóm thanh niên ở Bình Định chạy nhiều ôtô chở súng, lựu đạn, dao và mã tấu ra Quảng Ngãi tìm đối thủ để giải quyết mâu thuẫn.', 'http://img.f29.vnecdn.net/2016/06/06/nosung-1465181793_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/hang-chuc-giang-ho-di-oto-no-sung-thanh-toan-nhau-3414810.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1327', 'Hơn 10 thanh niên xông vào phòng cấp cứu chém gục hai người', 'N/a', '2016-06-07 00:21:07', '2016-06-06 09:32:52', 'Nhóm thanh niên đánh nhầm 4 người bên ngoài bệnh viện ở quận Bình Tân (TP HCM) trước khi xông vào phòng cấp cứu chém gục hai nạn nhân khác.', 'http://img.f30.vnecdn.net/2016/06/06/3-1465155854_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/hon-10-thanh-nien-xong-vao-phong-cap-cuu-chem-guc-hai-nguoi-3414743.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1328', 'Kế hoạch trộm hàng chục iPhone, iPad của 2 nam sinh', 'N/a', '2016-06-07 00:21:07', '2016-06-06 00:00:00', 'Lợi dụng sơ suất trong khâu bảo vệ hàng của công ty mình đang làm thêm, Trí cùng bạn lên kế hoạch trộm hàng chục iPhone đem bán.', 'http://img.f32.vnecdn.net/2016/06/05/sinhvien-9428-1465139519_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/ke-hoach-trom-hang-chuc-iphone-ipad-cua-2-nam-sinh-3414693.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1329', 'Muốn làm công an cần những tiêu chuẩn gì?', 'N/a', '2016-06-07 00:21:07', '2016-06-06 00:00:00', 'Tiêu chuẩn thi vào các trường công an năm nay được quy định như thế nào? Thí sinh có được nộp hồ sơ nhiều trường cùng một lúc không?', 'http://img.f31.vnecdn.net/2016/05/29/minhhoacongan-1464485253_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/tu-van/muon-lam-cong-an-can-nhung-tieu-chuan-gi-3410763.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1330', 'Nổ súng vì cạnh tranh thông tắc vệ sinh', 'N/a', '2016-06-07 00:21:07', '2016-06-06 11:15:20', 'Bị dán đè tờ quảng cáo thông tắc vệ sinh, nhóm của Quảng hẹn gặp đối thủ và xô xát, nổ súng xảy ra.', 'http://img.f31.vnecdn.net/2016/06/06/s-1465178412_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/no-sung-vi-canh-tranh-thong-tac-ve-sinh-3414834.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1331', 'Nam bảo vệ trung niên bị sát hại ở trường học', 'N/a', '2016-06-07 00:21:07', '2016-06-06 09:23:53', 'Xác nam bảo vệ nhiều thương tích được người dân phát hiện tại trường Trung cấp nghề số 1 (huyện Phú Xuyên, Hà Nội).', 'http://img.f30.vnecdn.net/2016/06/06/baovebisathai-1465179807_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/nam-bao-ve-trung-nien-bi-sat-hai-o-truong-hoc-3414863.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1332', 'Lá cây chứa ma tuý cực mạnh, giá triệu USD nhập lậu vào Việt Nam', 'N/a', '2016-06-07 00:21:07', '2016-06-05 22:32:47', 'Hơn một tấn lá cây lạ chứa chất ma túy mới, cực mạnh, được cho có giá hàng triệu USD nhập qua đường hàng không vào Việt Nam.', 'http://img.f31.vnecdn.net/2016/06/05/lacay-8149-1465137710_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/la-cay-chua-ma-tuy-cuc-manh-gia-trieu-usd-nhap-lau-vao-viet-nam-3414684.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1333', 'Nghi án cựu cầu thủ đầu độc hai con rồi tự sát', 'N/a', '2016-06-07 00:21:07', '2016-06-05 17:22:35', 'Cơ quan chức năng tình nghi do bế tắc trong tình cảm, nhân lúc vợ đi vắng anh Bình đầu độc hai con gái rồi tự sát.', 'http://img.f31.vnecdn.net/2016/06/05/HTBINH-1465120934_180x108.png', 'http://vnexpress.net/tin-tuc/phap-luat/nghi-an-cuu-cau-thu-dau-doc-hai-con-roi-tu-sat-3414639.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1334', 'Những căng thẳng khi dẫn đoàn Tổng thống Obama ở Sài Gòn', 'N/a', '2016-06-07 00:21:07', '2016-06-05 01:08:58', 'Ngoài phương án chính được mật vụ Mỹ tin tưởng, hài lòng, Phòng CSGT Công an TP HCM còn chuẩn bị 3 \"kịch bản\" dự phòng để không xảy ra sai sót khi dẫn đường cho phái đoàn của Tổng thống Obama.', 'http://img.f32.vnecdn.net/2016/06/05/Untitled1-1465117949_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/nhung-cang-thang-khi-dan-doan-tong-thong-obama-o-sai-gon-3414379.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1335', 'Thiếu phụ mưu trí để nhận dạng \'yêu râu xanh\'', 'N/a', '2016-06-07 00:21:07', '2016-06-05 00:00:00', 'Để nhận dạng giọng nói của kẻ hiếp dâm mình, chị Hà đã mưu trí vờ hỏi: \"Ngày mai anh còn quay lại không?\" khiến kẻ háo sắc buột miệng trả lời: \"Có\".', 'http://img.f29.vnecdn.net/2016/06/04/hiepdam-1465045609_180x108.jpg', 'http://vnexpress.net/tin-tuc/phap-luat/thieu-phu-muu-tri-de-nhan-dang-yeu-rau-xanh-3414377.html', '6', '8', '0', '0', '1', null, '59');
INSERT INTO `posts` VALUES ('1336', 'Djokovic sẽ trở thành tay vợt vĩ đại nhất', 'N/a', '2016-06-07 00:21:20', '2016-06-06 10:47:36', 'Chức vô địch Roland Garros đầu tiên trong sự nghiệp đã đưa tay vợt số 1 thế giới, Novak Djokovic xác lập nên nhiều kỷ lục.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/06/18/nole-4801.jpg', 'http://bongdaplus.vn/tin-bai/95/156072/djokovic-se-tro-thanh-tay-vot-vi-dai-nhat.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('1337', 'Djokovic giành Grand Slam đầu tiên trên sân đất nện', 'N/a', '2016-06-07 00:21:20', '2016-06-05 21:19:23', 'Màn lội ngược dòng ấn tượng trước Murray ở trận chung kết Roland Garros năm nay đã mang lại danh hiệu Grand Slam đầu tiên trên mặt sân đất nện trong sự nghiệp cho tay vợt số 1 thế giới, Novak Djokovic.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/06/18/djokovic-4801.jpg', 'http://bongdaplus.vn/tin-bai/95/156002/djokovic-gianh-grand-slam-dau-tien-tren-san-dat-nen.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('1338', 'Ánh Viên phá sâu chuẩn A Olympic, xô đổ kỷ lục SEA Games', 'N/a', '2016-06-07 00:21:20', '2016-06-05 09:31:00', 'Với thành tích 4 phút 07 giây 96 ở nội dung 400m tự do Giải Arena Pro Swim Series Indianapolis, kình ngư Nguyễn Thị Ánh Viên đã phá sâu chuẩn A Olympic, đồng thời cũng xô đổ luôn kỷ lục SEA Games.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/05/56/anh-vien.jpg', 'http://bongdaplus.vn/tin-bai/100/155964/anh-vien-pha-sau-chuan-a-olympic-xo-do-ky-luc-sea-games.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('1339', 'Djokovic vs Murray: Cuộc chiến giữa các vị thần', 'N/a', '2016-06-07 00:21:20', '2016-06-05 08:49:31', 'Djokovic và Murray sẽ đối đầu nhau tại chung kết Roland Garros 2016, diễn ra vào 20h00 tối nay (5/6) và đây là trận chung kết thứ 7 của cặp đấu này ở các giải Grand Slam.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/05/18/djokovicvsmurray-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155961/djokovic-vs-murray-cuoc-chien-giua-cac-vi-than.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('1340', 'Hạ gục nhà ĐKVĐ Serena, Muguruza giành Grand Slam đầu tiên', 'N/a', '2016-06-07 00:21:20', '2016-06-04 22:15:54', 'Hạt giống số 4, Garbine Muguruza đã gây bất ngờ lớn khi đánh bại nhà ĐKVĐ Roland Garros, Serena Williams trong trận chung kết chỉ sau 2 set đấu, để giành danh hiệu Grand Slam đầu tiên trong sự nghiệp.', 'http://img.v96.bdpcdn.net/Assets/Media/2016/06/04/18/muguruza-480.jpg', 'http://bongdaplus.vn/tin-bai/95/155921/ha-guc-nha-dkvd-serena-muguruza-gianh-grand-slam-dau-tien.bdplus', '6', '5', '0', '0', '1', null, '62');
INSERT INTO `posts` VALUES ('1341', 'Gấu phá hỏng cuộc tập trận của 3.000 binh sĩ', 'N/a', '2016-06-14 00:24:26', '2016-06-07 11:03:56', 'Một con gấu đã xông vào căn cứ quân sự của quân đội Phần Lan, khiến hơn 3.000 binh sĩ hoảng hốt.', 'http://img.f31.vnecdn.net/2016/06/07/1025629564-1465270643_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/quoc-phong/gau-pha-hong-cuoc-tap-tran-cua-3-000-binh-si-3415552.html', '6', '1', '1', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1343', 'Triều Tiên bị nghi tái khởi động cơ sở sản xuất plutonium', 'N/a', '2016-06-07 11:04:11', '2016-06-07 11:03:51', 'Cơ quan Năng lượng Nguyên tử Quốc tế cho rằng Triều Tiên có thể đã khởi động tái chế nhiên liệu hạt nhân đã qua sử dụng để thu thập plutonium sản xuất vũ khí hạt nhân. ', 'http://img.f30.vnecdn.net/2016/06/07/8989914589899143-1465271768_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/trieu-tien-bi-nghi-tai-khoi-dong-co-so-san-xuat-plutonium-3415535.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1345', 'Nam thanh niên dùng kim tiêm dính máu \'xin đểu\' trên xe khách', 'N/a', '2016-06-07 11:04:11', '2016-06-07 11:03:06', 'Được cho 30.000 đồng, nam thanh niên không nhận, hắn giơ kim tiêm dính máu ra dọa để đòi thêm tiền. ', 'http://img.f31.vnecdn.net/2016/06/07/1-1465267904_180x108.jpg', 'http://vnexpress.net/tin-tuc/cong-dong/video/nam-thanh-nien-dung-kim-tiem-dinh-mau-xin-deu-tren-xe-khach-3415521.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1346', 'Clinton has delegates to clinch White House nomination', 'N/a', '2016-06-07 11:04:11', '2016-06-07 11:00:40', 'Hillary Clinton has received commitments from enough delegates to clinch the Democratic presidential nomination, according to the Associated Press and U.S. networks, ensuring she will be the first woman to lead a major U.S. party in the race for the White House.', 'http://img.f46.english.vnecdn.net/2016/06/07/HilaryClintonJPG-1465264550-5116-1465264584_180x108.jpg', 'http://e.vnexpress.net/news/world/clinton-has-delegates-to-clinch-white-house-nomination-3415478.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1347', 'Gửi tiền trúng xe sang tại SHB', 'N/a', '2016-06-07 11:04:11', '2016-06-07 11:00:00', 'Khách gửi tiết kiệm được quay số trúng thưởng xe Lexus ES250 hơn 2,2 tỷ đồng và nhiều quà tặng giá trị khác, từ nay đến 27/8.', 'http://img.f27.kinhdoanh.vnecdn.net/2016/06/07/6-6-201658-4439-1465262885_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/ebank/cong-dong/gui-tien-trung-xe-sang-tai-shb-3415313.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1348', 'Serena passes suspended Sharapova as top woman earner', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:59:58', 'Serena Williams may have been second best at the French Open on Saturday but the American has moved ahead of fellow tennis player Maria Sharapova to top the women\'s earnings list in sports, according to Forbes Magazine.', 'http://img.f48.english.vnecdn.net/2016/06/07/serenawilliams-1465263087-1997-1465263167_180x108.jpg', 'http://e.vnexpress.net/news/world/sports/serena-passes-suspended-sharapova-as-top-woman-earner-3415453.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1349', 'Neymar chi 10.000 đôla mỗi ngày thuê biệt thự tại Mỹ', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:58:55', 'Tới Mỹ chơi từ đầu hè, tiền đạo Neymar không ngần ngại chi 10.000 đôla mỗi ngày để thuê một biệt thự sang trọng tại khu Beverly Hills.', 'http://img.f1.thethao.vnecdn.net/2016/06/07/1-1465270141_180x108.jpg', 'http://thethao.vnexpress.net/photo/copa-america/neymar-chi-10-000-dola-moi-ngay-thue-biet-thu-tai-my-3415576.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1350', 'Chồng liên tục sử dụng vũ lực với con trai 2 tuổi', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:58:19', 'Có bữa anh đang ngủ (ngủ ngày, ngủ nướng) con làm ồn anh điên lên hất con một cái chới với.', 'http://img.f31.vnecdn.net/2016/06/07/doroi-1465271666_1465271674_180x108.jpg', 'http://vnexpress.net/tin-tuc/tam-su/chong-lien-tuc-su-dung-vu-luc-voi-con-trai-2-tuoi-3415560.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1351', 'Del Bosque: \'Morata đủ tài để sánh ngang Suarez và Lewandowski\'', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:55:26', 'HLV tuyển Tây Ban Nha, Vicente Del Bosque cho rằng, tiền đạo trẻ Alvaro Morata nhiều khả năng sẽ trở thành một trong những chân sút hay nhất của tương lai.', 'http://img.f2.thethao.vnecdn.net/2016/06/07/morata-9560-1465267500_180x108.jpg', 'http://thethao.vnexpress.net/tin-tuc/tin-tuc/del-bosque-morata-du-tai-de-sanh-ngang-suarez-va-lewandowski-3415527.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1352', 'Vợ chồng Tú Vi sản xuất phim điện ảnh đầu tay', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:54:24', 'Diễn viên Văn Anh là đạo diễn phim điện ảnh \"Đời cho ta bao lần đôi mươi\" trong khi vợ anh quán xuyến vai trò nhà sản xuất dự án chung đầu tiên của cả hai.', 'http://img.f12.giaitri.vnecdn.net/2016/06/07/tu-vi-van-anh-top-8078-1465270478_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/vo-chong-tu-vi-san-xuat-phim-dien-anh-dau-tay-3415546.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1353', 'Nga dọa đáp trả tương xứng NATO ở châu Âu', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:52:13', 'Nga đang chuẩn bị đáp trả \"tương xứng\" đối với sự mở rộng của Tổ chức Hiệp ước Bắc Đại Tây Dương (NATO) về phía biên giới nước này.', 'http://img.f29.vnecdn.net/2016/06/07/2016-05-17T130910Z-674616939-L-3050-2953-1465270928_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/nga-doa-dap-tra-tuong-xung-nato-o-chau-au-3415548.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1354', 'Trà Ngọc Hằng hôn Hiếu Nguyễn say đắm', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:49:10', 'Bộ đôi diễn cảnh thân mật trong MV mới giữa sự bất ngờ cũng như hò reo của đông đảo diễn viên quần chúng.', 'http://img.f10.giaitri.vnecdn.net/2016/06/07/tra-ngoc-hang-hon-hieu-nguyen-say-dam-1465268796_180x108.jpg', 'http://giaitri.vnexpress.net/photo/lang-nhac/tra-ngoc-hang-hon-hieu-nguyen-say-dam-3415543.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1355', 'Turkish school bus accident kills several people', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:47:56', 'At least 14 people died and 26 others were injured after a school bus plunged into an irrigation canal in Turkey\'s southern Osmaniye province, according to Dogan news agency.', 'http://news.img.video.english.vnecdn.net/web/2016/06/07/turkish-school-bus-accident-kills-several-people-1465260587_180x108.jpg', 'http://e.vnexpress.net/video/video/turkish-school-bus-accident-kills-several-people-3415400.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1356', 'Các thành viên ABBA biểu diễn cùng nhau sau hơn 30 năm', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:46:02', 'Nhóm nhạc huyền thoại của Thụy Điển biểu diễn cùng nhau trong sự kiện kỷ niệm 50 năm gặp gỡ của Björn Ulvaeus và Benny Andersson.', 'http://img.f9.giaitri.vnecdn.net/2016/06/07/Abbat-1465267437_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/gioi-sao/quoc-te/cac-thanh-vien-abba-bieu-dien-cung-nhau-sau-hon-30-nam-3415522.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1357', 'Tặng gói nội thất 400 triệu đồng khi mua Vinhomes Dragon Bay', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:44:00', 'Ưu đãi này được Vingroup dành cho tất cả khách hàng có giao dịch thành công tại buổi mở bán dự án ngày 12/6 tới tại Vinpearl Hạ Long Bay Resort, Quảng Ninh.', 'http://img.f26.kinhdoanh.vnecdn.net/2016/06/06/6-6-20162-9256-1465208820_180x108.jpeg', 'http://kinhdoanh.vnexpress.net/tin-tuc/bat-dong-san/tang-goi-noi-that-400-trieu-dong-khi-mua-vinhomes-dragon-bay-3415247.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1358', 'Chàng trai sống hơn một năm không có trái tim trong cơ thể', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:43:09', 'Trước khi tiến hành phẫu thuật cấy ghép tim, một chàng trai 25 tuổi người Mỹ phải đeo balo chứa trái tim nhân tạo 24/7 suốt 555 ngày.', 'http://img.f29.vnecdn.net/2016/06/07/VNEStanLarkin-1465269119_180x108.jpg', 'http://vnexpress.net/tin-tuc/khoa-hoc/chang-trai-song-hon-mot-nam-khong-co-trai-tim-trong-co-the-3415355.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1359', 'Người đàn ông đột nhiên nói tiếng Pháp trôi chảy sau ca mổ não', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:41:15', 'Tỉnh dậy sau ca phẫu thuật mạch máu não không suôn sẻ, người đàn ông Italy 50 tuổi không còn nói tiếng mẹ đẻ mà chuyển sang dùng tiếng Pháp.', 'http://img.f43.suckhoe.vnecdn.net/2016/06/07/des-medecins-pratiquent-une-ci-5229-1887-1465264160_180x108.jpg', 'http://suckhoe.vnexpress.net/tin-tuc/suc-khoe/nguoi-dan-ong-dot-nhien-noi-tieng-phap-troi-chay-sau-ca-mo-nao-3415444.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1360', 'Liam Hemsworth từng bỏ vai trong ‘Người đàn bà cuồng dâm’', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:41:14', 'Tài tử “Independence Day: Resurgence” suýt góp mặt trong bộ phim về tình dục của đạo diễn Lars von Trier nhưng sau đó nhường lại vai cho Shia LaBeouf.', 'http://img.f9.giaitri.vnecdn.net/2016/06/07/page-1465269136_180x108.jpg', 'http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/liam-hemsworth-tung-bo-vai-trong-nguoi-dan-ba-cuong-dam-3415545.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1361', 'Mẹo làm cua nhanh mà không bị kẹp', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:39:36', 'Đá lạnh là giải pháp giúp bạn làm cua nhanh chóng, không còn sợ bị cua kẹp.', 'http://img.f16.giadinh.vnecdn.net/2016/06/07/11016-1465267598_180x108.jpg', 'http://giadinh.vnexpress.net/tin-tuc/noi-tro/meo-lam-cua-nhanh-ma-khong-bi-kep-3407231.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1362', 'Cá voi sát thủ tự tử đòi thoát khỏi công viên giải trí', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:39:06', 'Các nhà hoạt động vì quyền động vật đang kêu gọi một công viên giải trí ở Mỹ thả tự do cho con cá voi sát thủ cái sau khi nó tự làm mình mắc cạn và nằm bất động suốt 10 phút ngay trong chương trình biểu diễn.', 'http://news.img.video.vnecdn.net/web/2016/06/07/ca-voi-sat-thu-tu-tu-doi-thoat-khoi-cong-vien-giai-tri-1465266785_180x108.jpg', 'http://video.vnexpress.net/khoa-hoc/ca-voi-sat-thu-tu-tu-doi-thoat-khoi-cong-vien-giai-tri-3415514.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1363', 'Sư tử định vồ bé trai trong sở thú Nhật Bản', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:39:00', 'Con sư tử lao vào định vồ lấy cậu bé hai tuổi nhưng đập mặt vào tấm kính chắn trong sở thú Nhật Bản', 'http://news.img.video.vnecdn.net/web/2016/06/06/su-tu-dinh-vo-be-trai-trong-so-thu-nhat-ban-1465195722_180x108.jpg', 'http://video.vnexpress.net/the-gioi/su-tu-dinh-vo-be-trai-trong-so-thu-nhat-ban-3415089.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1364', 'Chó nghiệp vụ vây bắt tội phạm cùng cảnh sát cơ động', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:37:24', 'Tấn công kẻ buôn bán ma tuý, truy đuổi mục tiêu, tìm xác người... là công việc của gần 700 con chó nghiệp vụ do Bộ Công an quản lý.', 'http://img.f29.vnecdn.net/2016/06/07/a2-1465264086_180x108.jpg', 'http://vnexpress.net/photo/phap-luat/cho-nghiep-vu-vay-bat-toi-pham-cung-canh-sat-co-dong-3415484.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1365', 'Lập luận nực cười của Trung Quốc để bác phán quyết Biển Đông', 'N/a', '2016-06-07 11:04:11', '2016-06-07 10:35:57', 'Bắc Kinh cố tình phớt lờ Điều 288(4) của UNCLOS để đưa ra lý do mơ hồ nhằm bác bỏ thẩm quyền xét xử vụ kiện \"đường lưỡi bò\" của tòa quốc tế. ', 'http://img.f30.vnecdn.net/2016/06/07/5-1465270210_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/phan-tich/lap-luan-nuc-cuoi-cua-trung-quoc-de-bac-phan-quyet-bien-dong-3415577.html', '6', '1', '0', '0', '1', null, '51');
INSERT INTO `posts` VALUES ('1366', 'Blogger Mỹ gợi ý địa chỉ ăn bánh mì ngon ở Sài Gòn', 'N/a', '2016-06-07 11:06:09', '2016-06-07 10:30:00', 'Tiệm Huỳnh Hoa, Hồng Hoa hay 37 Nguyễn Trãi đều là các địa chỉ không thể bỏ qua đối với người mê bánh mì ở Sài Gòn. ', 'http://img.f33.dulich.vnecdn.net/2016/06/06/banh-mi-huynh-hoa-5631-1465186695_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/blogger-my-goi-y-dia-chi-an-banh-mi-ngon-o-sai-gon-3414296.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1367', 'Du khách Anh ngã xuống thác ở Fansipan trước khi mất tích', 'N/a', '2016-06-07 11:06:09', '2016-06-07 09:12:50', 'Aiden Webb để bạn gái ở khách sạn, một mình lên đường leo Fansipan, Lào Cai, hôm 3/6. Trước khi mất liên lạc, Webb cho biết anh đã bị ngã xuống thác, chảy nhiều máu. ', 'http://img.f34.dulich.vnecdn.net/2016/06/07/2-1465264170_1465267975_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/sapa/du-khach-anh-nga-xuong-thac-o-fansipan-truoc-khi-mat-tich-3415446.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1368', 'Cá voi tự tử để thoát khỏi cuộc sống ở khu giải trí', 'N/a', '2016-06-07 11:06:09', '2016-06-07 09:51:51', 'Rất nhiều du khách đã bị sốc khi chứng kiến một chú cá voi cố gắng tự tử ngay trước mắt họ.', 'http://img.f35.dulich.vnecdn.net/2016/06/07/1-1465266264_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/quoc-te/ca-voi-tu-tu-de-thoat-khoi-cuoc-song-o-khu-giai-tri-3415497.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1369', 'Lần đầu rước tượng vua Bảo Đại', 'N/a', '2016-06-07 11:06:09', '2016-06-07 08:35:00', 'Tượng sáp vua Bảo Đại và hoàng hậu Nam Phương sẽ được rước bằng xe ngựa vào qua trung tâm Đà Lạt về dinh I.', 'http://img.f34.dulich.vnecdn.net/2016/06/07/cover-1639-1465272282_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/da-lat/lan-dau-ruoc-tuong-vua-bao-dai-3415276.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1370', 'Tai nạn tàu khách ở Bỉ làm 3 người chết', 'N/a', '2016-06-07 11:06:09', '2016-06-07 07:58:24', 'Tai nạn tàu lửa diễn ra vào 11h đêm 5/6 ở Bỉ khiến 3 người chết, 40 người bị thương.', 'http://img.f34.dulich.vnecdn.net/2016/06/07/Untitled1-1465260225_180x108.jpg', 'http://vnexpress.net/tin-tuc/the-gioi/tai-nan-tau-khach-o-bi-lam-3-nguoi-chet-3415392.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1371', '6 địa điểm ngủ lều bên bãi biển đang hot ở Việt Nam', 'N/a', '2016-06-07 11:06:09', '2016-06-07 07:00:00', 'Khu du lịch sinh thái Sao Biển, Coco Beach Camp, Sơn Mỹ Beach.... là những nơi cắm trại ngắm trời biển rất thích hợp cho kỳ nghỉ hè của bạn.', 'http://img.f34.dulich.vnecdn.net/2016/06/06/resort-coco-beach-camp-lagi3-4577-1465205359_180x108.png', 'http://dulich.vnexpress.net/tin-tuc/cong-dong/tu-van/6-dia-diem-ngu-leu-ben-bai-bien-dang-hot-o-viet-nam-3415240.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1372', 'Anh chàng vòng quanh thế giới bằng xe bán sandwich', 'N/a', '2016-06-07 11:06:09', '2016-06-07 05:05:00', 'Franco Paltrinieri bắt đầu hành trình từ Argentina đến Alaska, Mỹ từ năm 2012, chỉ với nguồn thu duy nhất là tiền bán sandwich thịt nướng trên chính chiếc xe van của mình.', 'http://img.f34.dulich.vnecdn.net/2016/06/06/1-1465197225_180x108.jpg', 'http://dulich.vnexpress.net/photo/anh-video/anh-chang-vong-quanh-the-gioi-bang-xe-ban-sandwich-3415111.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1373', 'Tour Trương Gia Giới - Phượng Hoàng Cổ Chấn từ 9,9 triệu đồng', 'N/a', '2016-06-07 11:06:09', '2016-06-07 09:00:00', 'Tour trọn gói 6 ngày tới chốn bồng lai tiên cảnh dưới hạ giới, nơi đã tạo cảm hứng cho đạo diễn lừng danh Hollywood thực hiện siêu phẩm Avatar.', 'http://img.f34.dulich.vnecdn.net/2016/06/06/1-1465206734_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/chu-de/deal/tour-truong-gia-gioi-phuong-hoang-co-chan-tu-9-9-trieu-dong-3415243.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1374', 'Mùa hè trong mơ nơi thiên đường hạnh phúc', 'N/a', '2016-06-07 11:06:09', '2016-06-07 08:00:00', 'Đến vùng đất Bhutan, du khách sẽ chiêm ngưỡng thiên nhiên hùng vĩ, kiến trúc pháo đài kết hợp tu viện độc đáo và huyền bí.', 'http://img.f34.dulich.vnecdn.net/2016/06/04/2-1465001542_180x108.jpg', 'http://dulich.vnexpress.net/tin-tuc/viet-nam/mua-he-trong-mo-noi-thien-duong-hanh-phuc-3413729.html', '6', '3', '0', '0', '1', null, '53');
INSERT INTO `posts` VALUES ('1394', 'Thúc đẩy quan hệ hợp tác quốc phòng Việt Nam - Pháp và Ấn Độ', 'N/a', '2016-06-07 11:14:44', '2016-06-07 10:04:43', ' Chiều 6-6, tại Phủ Chủ tịch, Chủ tịch nước Trần Đại Quang tiếp xã giao Bộ trưởng Quốc phòng Pháp Jean-Yves Le Drian và Bộ trưởng Quốc phòng Ấn Độ Manohar Parrika đang có chuyến thăm, làm việc tại Việt Nam.\n\n    Chủ tịch nước Trần Đại Quang thăm và làm việc với Cục An ninh mạng\n ', 'http://static.cand.com.vn/Files/Image/chienthang/2016/06/07/thumb_340_62fb3471-5fd1-496f-9e7e-9293c43d2e88.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Thuc-day-quan-he-hop-tac-quoc-phong-Viet-Nam-Phap-va-An-Do-395421/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1395', 'Những “con rối” trong tay Việt Tân<span class=\'txt_num_comment\'>3</span>', 'N/a', '2016-06-07 11:14:44', '2016-05-17 08:46:50', ' Nhìn lại lịch sử phát triển của Việt Tân, từ một tổ chức ô hợp được Hoàng Cơ Minh thành lập ở Ubon, Thái Lan năm 1983, Việt Tân đã thiết lập mạng lưới các chi hội trên khắp nước Mỹ. Sau khi nhận tiền quyên góp, Việt Tân đã tự tô vẽ thành“Mặt trận Quốc gia Thống nhất Giải phóng Việt Nam” với mục đích lật đổ chính quyền Việt Nam.\n\n    Đừng biến mình thành “quân cờ” làm hại đất nước\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/05/17/thumb_340_c7ab105c-a774-4835-a6b2-ca0bb2ee2f14.jpg', 'http://cand.com.vn/Chong-dien-bien-hoa-binh/duNG-BieN-MiNH-THaNH-QuaN-Co-LaM-Hai-daT-NuoC-392941/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1396', 'Đã đủ cơ ở kết luận nguyên nhân hải sản chết hàng loạt ', 'N/a', '2016-06-07 11:14:44', '2016-05-14 20:55:39', ' “Có thể nói đã đủ cơ sở để khẳng định sẽ có được câu trả lời với căn cứ khoa học thuyết phục, được quốc tế thừa nhận” – Thứ trưởng Bộ Khoa học và Công nghệ (KH&CN) Phạm Công Tạc khẳng định với báo chí về hiện tượng thủy hải sản chết hàng loạt vừa qua tại 4 tỉnh miền Trung. ', 'http://static.cand.com.vn/Files/Image/Content/thanhbinh/2016/05/14/thumb_340_9040c2f1-888e-4a9f-9d64-cfd0a5ee33b6.5.16-2.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/da-du-co-o-ket-luan-nguyen-nhan-hai-san-chet-hang-loat-392710/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1397', 'Phó Thủ tướng Nguyễn Xuân Phúc yêu cầu đẩy nhanh tiến độ khắc phục sự cố cầu Ghềnh', 'N/a', '2016-06-07 11:14:44', '2016-04-03 14:51:38', ' Ngày 3-4, Phó Thủ tướng Nguyễn Xuân Phúc đã trực tiếp đến hiện trường chỉ đạo công tác khắc phục sự cố sập cầu Ghềnh tại thành phố Biên Hoà, Đồng Nai khiến tuyến đường sắt Bắc – Nam bị gián đoạn. ', 'http://static.cand.com.vn/Files/Image/Content/bichthuy/2016/04/03/thumb_340_03f5fbb6-8a69-4a30-aae5-82979525054d.4.16.TH.pho.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Pho-Thu-tuong-Nguyen-Xuan-Phuc-yeu-cau-day-nhanh-tien-do-khac-phuc-su-co-cau-Ghenh-387941/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1398', 'Vụ nhà 8B Lê Trực: Không kiên quyết, người dân sẽ mất niềm tin <span class=\'txt_num_comment\'>2</span>', 'N/a', '2016-06-07 11:14:44', '2016-04-03 11:20:22', ' Vụ việc dự án nhà 8B Lê Trực mặc dù các cơ quan chức năng của TP Hà Nội đã triển khai tiến hành cưỡng chế, tháo dỡ phần sai phạm. Tuy nhiên vẫn còn có rất nhiều ý kiến xung quanh vụ việc này như việc tiến hành phá dỡ vẫn còn chậm, ảnh hưởng đến các hộ dân đang sinh sống xung quanh tòa nhà này.\n\n    Vụ chủ cao ốc 8B Lê Trực “xin giữ nguyên sai phạm”: Đừng để pháp luật thành trò đùa2\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/04/03/thumb_340_db0975dc-6367-47ff-b6ea-58bcb2ff3b1c.JPG', 'http://cand.com.vn/Van-de-hom-nay-thoi-su/Khong-kien-quyet-nguoi-dan-se-mat-niem-tin-vao-su-cong-bang-cua-phap-luat-387896/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1399', 'Đại tướng Trần Đại Quang được giới thiệu giữ chức vụ Chủ tịch nước', 'N/a', '2016-06-07 11:14:44', '2016-03-31 22:19:02', ' Cuối giờ chiều 31-3, Chủ tịch Quốc hội Nguyễn Thị Kim Ngân đã thay mặt Ủy ban Thường vụ Quốc hội đọc Tờ trình danh sách đề cử để Quốc hội bầu Chủ tịch nước. Theo đó, đồng chí Đại tướng Trần Đại Quang, Uỷ viên Bộ Chính trị, Bộ trưởng Bộ Công an được giới thiệu giữ chức vụ Chủ tịch nước.\n\n    Đồng chí Nguyễn Thị Kim Ngân được bầu giữ chức vụ Chủ tịch Quốc hội khoá XIII\n ', 'http://static.cand.com.vn/Files/Image/Content/anbinh/2016/03/31/thumb_340_ea9b4358-bcb1-43a8-ad7f-ff0a9f36ce18.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/dai-tuong-Tran-dai-Quang-duoc-gioi-thieu-giu-chuc-vu-Chu-tich-nuoc-387640/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1400', 'Cần cơ chế gì để TP Hồ Chí Minh trở lại vị trí số 1?', 'N/a', '2016-06-07 11:14:44', '2016-03-29 12:54:45', ' Trao đổi với PV bên lề Quốc hội 29/3 về tuyên bố TP HCM phải trở thành đặc khu kinh tế, Bí thư Thành ủy TP HCM Đinh La Thăng cho biết: “Suy nghĩ của tôi bây giờ chỉ là “Bao giờ Thành phố trở lại vị trí số 1, và để được là số 1 thì cần cơ chế gì?” ', 'http://static.cand.com.vn/Files/Image/Content/bichthuy/2016/03/29/thumb_340_d325cee6-1308-4da1-9ef0-1b0c1b6b957a.3.16.TH.coche-a.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Can-co-che-gi-de-TP-Ho-Chi-Minh-tro-lai-vi-tri-so-mot-387339/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1401', 'Lấy ý kiến cử tri nơi cư trú của đại biểu Quốc hội Trương Hòa Bình', 'N/a', '2016-06-07 11:14:44', '2016-03-27 17:21:33', ' Chiều 27-3, tại hội trường UBND phường 11, quận Tân Bình, TP Hồ Chí Minh, 92 đại biểu cư tri thuộc Tổ dân 5, Khu phố 1, Phường 11 đã gặp gỡ và cho ý kiến đối với đồng chí Trương Hòa Bình, người ứng cử đại biểu Quốc hội khóa 14, nhiệm kỳ 2016-2021.\n\n    Đồng chí Nguyễn Thiện Nhân tiếp xúc cử tri tại Bắc Giang\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/03/27/thumb_340_2a9d37f3-d474-4741-87a6-d280b7e0b418.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Lay-y-kien-cu-tri-noi-cu-tru-cua-dai-bieu-Quoc-hoi-Truong-Hoa-Binh-387139/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1402', 'Tổng Bí thư: “Dư luận vẫn râm ran chuyện chạy chức, chạy quyền“<span class=\'txt_num_comment\'>1</span>', 'N/a', '2016-06-07 11:14:44', '2016-03-26 22:14:07', ' Tổng Bí thư yêu cầu Hội nghị thẳng thắn thảo luận xem có chuyện ấy chạy chức chạy quyền hay không, mức độ thế nào, để cho rõ ràng, minh bạch. ', 'http://static.cand.com.vn/Files/Image/Content/daiviet/2016/03/26/thumb_340_06e0c546-5d0d-4b00-9527-5c1012044622.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Tong-Bi-thu-du-luan-van-ram-ran-chuyen-chay-chuc-chay-quyen-387058/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1403', 'Thủ tướng Nguyễn Tấn Dũng chủ trì phiên họp cuối cùng của Chính phủ', 'N/a', '2016-06-07 11:14:44', '2016-03-26 09:40:17', ' Thủ tướng: “Phiên họp này, Chính phủ làm việc bình thường sau đó chúng ta kết thúc nhiệm kỳ Chính phủ của 20 đồng chí”. ', 'http://static.cand.com.vn/Files/Image/Content/daiviet/2016/03/26/thumb_340_7eb72a65-6293-41f4-87da-d5182ee4727c.cand.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Thu-tuong-Nguyen-Tan-dung-chu-tri-phien-hop-cuoi-cung-cua-Chinh-phu-387030/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1404', 'Đơn vị chọn nhà thầu Trung Quốc phải chịu toàn bộ trách nhiệm trước nhân dân<span class=\'txt_num_comment\'>8</span>', 'N/a', '2016-06-07 11:14:44', '2016-03-25 22:54:29', ' Liên quan đến việc nhà thầu Trung Quốc trúng gói thầu cung cấp đường ống gang dẻo cho dự án đường ống nước sông Đà (giai đoạn 2), nhiều đại biểu Quốc hội cho rằng: Phải công khai toàn bộ tiêu chuẩn kỹ thuật và chi phí và phải chịu toàn bộ trách nhiệm trước nhân dân về lựa chọn này. ', 'http://static.cand.com.vn/Files/Image/Content/daiviet/2016/03/25/thumb_340_dde94738-bfc4-43db-b207-a1b1f4599ffb.vo-duong-ong-nuoc-song-da-f6d0d.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/don-vi-chon-nha-thau-Trung-Quoc-phai-chiu-toan-bo-trach-nhiem-truoc-nhan-dan-386907/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1405', 'Bảo đảm quân nhu phục vụ các nhiệm vụ đặc biệt, bảo vệ chủ quyền Tổ quốc', 'N/a', '2016-06-07 11:14:44', '2016-03-24 07:56:02', ' Ngày 23-3, Cục Quân nhu, Bộ Quốc phòng tổ chức lễ kỷ niệm 70 năm truyền thống ngành Quân nhu Quân đội (25-3-1946 – 25-3-2016) và đón nhận Huân chương Quân công hạng Nhì.\n\n    Chủ tịch nước Trương Tấn Sang thăm và làm việc tại Ninh Bình\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/03/24/thumb_340_e53f9359-68ae-45d6-96cf-f94cf8dba2dc.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Bao-dam-quan-nhu-phuc-vu-cac-nhiem-vu-dac-biet-bao-ve-chu-quyen-To-quoc-386758/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1406', 'Bộ trưởng Trần Đại Quang tiếp Cố vấn cấp cao của Thủ tướng Nhật Bản', 'N/a', '2016-06-07 11:14:44', '2016-03-17 23:00:37', ' Chiều 17-3, tại Hà Nội, Đại tướng Trần Đại Quang, Bộ trưởng Bộ Công an Việt Nam tiếp ngài Isao Iijima, Cố vấn cấp cao của Thủ tướng Nhật Bản và Đoàn doanh nhân Nhật Bản sang thăm chính thức Việt Nam. Dự buổi tiếp có đại lãnh đạo các đơn vị chức năng Bộ Công an Việt Nam. ', 'http://static.cand.com.vn/Files/Image/Content/thanhbinh/2016/03/17/thumb_340_04273b7b-373b-4ee5-afda-c4732d4ff215.3.16-2.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Bo-truong-Tran-dai-Quang-tiep-Co-van-cap-cao-cua-Thu-tuong-Nhat-Ban-385944/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1407', 'Tạm đình chỉ GĐ Vườn Quốc gia Ba Vì nếu phát hiện tham nhũng<span class=\'txt_num_comment\'>1</span>', 'N/a', '2016-06-07 11:14:44', '2016-03-05 09:15:04', ' Bộ trưởng Bộ NN&PTNT vừa có quyết định tạm đình chỉ công tác điều hành của Giám đốc Vườn Quốc gia Ba Vì và các cán bộ liên quan nếu có dấu hiệu tham nhũng hoặc cản trở công tác thanh tra. ', 'http://static.cand.com.vn/Files/Image/Content/linhchi/2016/03/04/thumb_340_878cf992-2779-48ff-9e42-5c75030d433f.3.16-2.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Bo-truong-Bo-NN-PTNT-Co-the-tam-dinh-chi-Gd-Vuon-Quoc-gia-Ba-Vi-384511/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1408', 'Kỳ vọng bản lĩnh, trí tuệ lãnh đạo đất nước của Ban Chấp hành Trung ương Đảng khóa XII', 'N/a', '2016-06-07 11:14:44', '2016-01-28 10:58:58', ' Ngày 27-1, Ban Chấp hành Trung ương Đảng khoá XII đã họp Hội nghị lần thứ nhất bầu Bộ Chính trị, Tổng Bí thư, Ban Bí thư, Ủy ban Kiểm tra Trung ương, Chủ nhiệm Ủy ban Kiểm tra Trung ương. Theo đó, sáng 28-1, Đại hội XII họp phiên bế mạc tại hội trường, nghe báo cáo kết quả biểu quyết một số vấn đề trong các văn kiện Đại hội XII (nếu có). ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/01/28/thumb_340_67e337d7-3643-4df3-b582-ddf946a1e6a1.jpg', 'http://cand.com.vn/dai-hoi-dang-lan-thu-Xii/Ky-vong-ban-linh-tri-tue-lanh-dao-dat-nuoc-cua-Ban-Chap-hanh-Trung-uong-dang-khoa-Xii-381216/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1409', 'Các đại biểu bỏ phiếu bầu Ban Chấp hành Trung ương khóa XII<span class=\'txt_num_comment\'>1</span>', 'N/a', '2016-06-07 11:14:44', '2016-01-26 21:50:58', ' 10h15 phút sáng nay (26-1), Đại hội đại biểu toàn quốc lần thứ XII Đảng Cộng sản Việt Nam tiến hành bỏ phiếu bầu cử Ban Chấp hành Trung Ương khóa XII gồm 180 Ủy viên chính thức và 20 Ủy viên dự khuyết. ', 'http://static.cand.com.vn/Files/Image/Content/trangdung/2016/01/26/thumb_340_c4e98a2e-3df1-49e0-afc5-b221a62d5edc.jpg', 'http://cand.com.vn/dai-hoi-dang-lan-thu-Xii/Cac-dai-bieu-bo-phieu-bau-Ban-Chap-hanh-Trung-uong-khoa-Xi-380972/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1410', 'Chủ tịch nước: Chúng ta phải đứng trên đôi chân của chính mình', 'N/a', '2016-06-07 11:14:44', '2015-12-31 08:31:26', ' Trước thềm năm mới 2016, Chủ tịch nước Trương Tấn Sang đã trả lời phỏng vấn báo chí, chia sẻ những đánh giá và nhận định về cơ hội và thách thức đối với đất nước và dân tộc.\n\n ', 'http://static.cand.com.vn/Files/Image/Content/linhchi/2015/12/30/thumb_340_1feceaf6-c3bf-4bed-9521-d59c9b12f147.jpg', 'http://cand.com.vn/Van-de-hom-nay-thoi-su/Chu-tich-nuoc-Chung-ta-phai-dung-tren-doi-chan-cua-chinh-minh-378063/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1411', 'Tập đoàn Dệt may Việt Nam đón nhận Danh hiệu Anh hùng Lao động ', 'N/a', '2016-06-07 11:14:44', '2015-12-27 09:02:41', ' Sáng 26-12, tại Hà Nội, Tập đoàn Dệt may Việt Nam (Vinatex) tổ chức kỉ niệm 20 năm thành lập và đón nhận danh hiệu Anh hùng Lao động. Tới dự có Chủ tịch nước Trương Tấn Sang, Phó Thủ tướng Chính phủ Vũ Văn Ninh. ', 'http://static.cand.com.vn/Files/Image/Content/hientk/2015/12/27/thumb_340_7b8b01fb-9af3-4cf6-9eee-968cef8a0b4a.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Tap-doan-det-may-Viet-Nam-don-nhan-danh-hieu-anh-hung-Lao-dong-377619/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1412', ' Xây đắp thêm tình hữu nghị truyền thống tốt đẹp Việt Nam- Campuchia', 'N/a', '2016-06-07 11:14:44', '2015-12-26 19:07:48', ' Sáng ngày 26-12, tại cửa khẩu Quốc tế Lệ Thanh, xã Ia Nan, huyện Đức Cơ, tỉnh Gia Lai đã diễn ra Lễ khánh thành cột mốc 30 trên biên giới đất liền Việt Nam- Campuchia và khánh thành đoạn đường nối hai trạm kiểm soát cửa khẩu Quốc tế Lệ Thanh (Việt Nam) và O Za Dao  (Campuchia).   ', 'http://static.cand.com.vn/Files/Image/Content/nguyenbinh/2015/12/26/thumb_340_6eccc09a-8132-4c7e-9e00-bf79e435f199.12.15n.b-2.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Xay-dap-them-tinh-huu-nghi-truyen-thong-tot-dep-Viet-Nam-Campuchia-377597/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1413', 'Chủ tịch Quốc hội Nguyễn Sinh Hùng hội kiến Tổng Bí thư, Chủ tịch nước Trung Quốc Tập Cận Bình', 'N/a', '2016-06-07 11:14:44', '2015-12-24 09:08:45', ' Chiều 23-12, trong khuôn khổ chuyến thăm hữu nghị chính thức Trung Quốc, tại Đại lễ đường Nhân dân, thủ đô Bắc Kinh, Chủ tịch Quốc hội Nguyễn Sinh Hùng và Đoàn đại biểu Cấp cao Quốc hội nước ta đã hội kiến Tổng Bí thư Ban Chấp hành Trung ương Đảng Cộng sản Trung Quốc, Chủ tịch nước Cộng hòa Nhân dân Trung Hoa Tập Cận Bình.\n\n    Chủ tịch Quốc hội Nguyễn Sinh Hùng sẽ thăm hữu nghị chính thức nước Cộng hòa Nhân dân Trung Hoa\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2015/12/24/thumb_340_0b950e06-e32d-4b08-96ac-8335968f1026.jpg', 'http://cand.com.vn/Su-kien-Binh-luan-thoi-su/Chu-tich-Quoc-hoi-Nguyen-Sinh-Hung-hoi-kien-Tong-Bi-thu-Chu-tich-nuoc-Trung-Quoc-Tap-Can-Binh-377299/', '6', '1', '0', '0', '1', null, '70');
INSERT INTO `posts` VALUES ('1414', 'Liệu “lệ làng” có dung túng ảnh khỏa thân khi “phép vua” đã định?', 'N/a', '2016-06-07 11:52:23', '2016-06-07 09:36:29', ' “Tôi muốn chụp ảnh khỏa thân để lưu giữ tuổi thanh xuân, có gì sai?”, “Có nhiều cách để thể hiện vẻ đẹp hình thể tại sao cứ phải là chụp ảnh khỏa thân”… Đó là những ý kiến trái chiều của dư luận xã hội ngay sau khi Bộ VHTTDL đưa ra quyết định cấm chụp ảnh khỏa thân với một số đối tượng hoạt động trong lĩnh vực nghệ thuật. ', 'http://static.cand.com.vn/Files/Image/thanhbinh/2016/06/07/thumb_340_1d6bbfbe-3c41-4d4f-87c9-e8062a7e113a.6.16-5.jpg', 'http://cand.com.vn/Tieu-diem-van-hoa/Lieu-le-lang-co-dung-tung-anh-khoa-than-khi-phep-vua-da-dinh-395437/', '6', '6', '1', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1415', 'Ảnh đẹp dự thi Vì bình yên cuộc sống năm 2016 tuần thứ 4', 'N/a', '2016-06-07 11:51:22', '2016-06-05 17:26:17', ' Ban tổ chức cuộc thi ảnh Vì bình yên cuộc sống 2016 xin giới thiệu một số tấm ảnh xuất sắc tuần thứ 4.\n\n    Thể lệ cuộc thi ảnh “Vì bình yên cuộc sống” năm 2016\n ', 'http://static.cand.com.vn/Files/Image/Content/trangdung/2016/05/30/thumb_340_4984d100-83b8-4f03-9986-1bdcfff38565.jpeg', 'http://cand.com.vn/Thi-anh-vi-bycs/Anh-dep-du-thi-Vi-binh-yen-cuoc-song-nam-2016-tuan-thu-4-394519/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1416', 'Thể lệ cuộc thi ảnh “Vì bình yên cuộc sống” năm 2016', 'N/a', '2016-06-07 11:51:22', '2016-05-16 14:19:05', ' Kỷ niệm 70 năm Báo Công an nhân dân phát hành số đầu tiên (1/11/1946 – 1/11/2016), Báo CAND Tổ chức cuộc thi ảnh với chủ đề “Vì bình yên cuộc sống”. ', 'http://static.cand.com.vn/Files/Image/Content/trangdung/2016/05/10/thumb_340_a043bd59-d3b2-4765-8b53-34022d881a97.5.jpg', 'http://cand.com.vn/Thi-anh-vi-bycs/The-le-cuoc-thi-anh-Vi-binh-yen-cuoc-song-nam-2016-392113/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1417', 'Hà Nội đẹp ngỡ ngàng mùa lá vàng rơi', 'N/a', '2016-06-07 11:51:22', '2016-05-04 18:47:59', ' Những con phố trải dài với hàng cây cổ thụ như Phan Đình Phùng, Hoàng Hoa Thám…được phủ kín bởi lớp lá vàng khiến con đường trở nên đẹp ngỡ ngàng, làm lòng người xao xuyến, bâng khuâng.\n\n    Muôn lá vàng rơi1/ Tại sao lá vàng trong mùa thu?/ Lá vàng... gánh nỗi truân chuyên\n\n ', 'http://static.cand.com.vn/Files/Image/Content/xuanbui/2016/05/04/thumb_340_86d010e4-c85b-4e56-9f31-a88bef5ba0d9.JPG', 'http://cand.com.vn/Giai-tri-van-hoa/Ha-Noi-dep-ngo-ngang-mua-la-vang-roi-391406/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1418', 'Đạo diễn Long Vân: Duyên nợ với Sài Gòn', 'N/a', '2016-06-10 14:01:32', '2016-04-28 22:46:23', ' Nói đạo diễn Long Vân là người nhiều duyên nợ với Sài Gòn thật không ngoa chút nào. Trong cuộc đời làm phim của mình, những bộ phim lớn để lại dấu ấn đậm nét nhất của ông đều có hai chữ Sài Gòn như: “Biệt động Sài Gòn”, “Hẹn gặp lại Sài Gòn”, “Giải phóng Sài Gòn” và sau này với vai trò cố vấn của phim truyền hình dài tập “Những đứa con biệt động Sài Gòn”. ', 'http://static.cand.com.vn/Files/Image/Content/nguyenbinh/2016/04/28/thumb_340_22c9c486-50a9-4c8f-95b4-ad2fff8c9446.4.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/SBd_dao-dien-Long-Van-duyen-no-voi-Sai-Gon-390792/', '6', '6', '1', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1419', 'Rời chính trường làm bạn với thơ', 'N/a', '2016-06-07 11:51:22', '2016-04-28 08:58:18', ' Đứng đầu một ngành, lĩnh vực, quản lý, chỉ đạo hàng nghìn con người nhưng khi ra diễn đàn Quốc hội, không ít vị bộ trưởng căng thẳng vì đụng câu hỏi, chất vấn khó của đại biểu. Ở góc độ khác, nhiều vị bình tĩnh “giải mã” và lựa lời làm dịu không khí gay cấn trước nghị trường. Khi trở về với không gian tĩnh lặng, đó là lúc “tức cảnh sinh tình”, cởi áo bộ trưởng, hồn đã là thi sĩ… ', 'http://static.cand.com.vn/Files/Image/Content/honghai/2016/04/27/thumb_340_ccb0c458-1d30-4bbf-8fcb-85d604fcbece.4.16.jpg', 'http://cand.com.vn/Tieu-diem-van-hoa/SdB-Roi-chinh-truong-lam-ban-voi-tho-390519/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1420', 'Hãng phim truyện Việt Nam sẽ kinh doanh điện ảnh sau khi cổ phần hóa', 'N/a', '2016-06-07 11:51:22', '2016-04-27 21:05:12', ' Ngày 27-4, với việc Hãng phim Hoạt hình Việt Nam lên sàn Sở Giao dịch Chứng khoán Hà Nội để bán đấu giá cổ phần, các hãng phim Nhà nước đã chính thức bị xóa sổ.\n\n    Tại 3 hãng phim truyện nhà nước: Nóng bỏng chuyện cổ phần hóa\n ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/04/27/thumb_340_b4261d05-80d6-4171-9c33-69de5d8a574e.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/Hang-phim-truyen-Viet-Nam-se-kinh-doanh-dien-anh-sau-khi-co-phan-hoa-390607/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1421', 'Lá bối khắc Tam tạng kinh Phật 2000 năm tuổi được đưa về Việt Nam triển lãm', 'N/a', '2016-06-07 11:51:22', '2016-04-26 22:10:40', ' Bản kinh cổ thuộc một phần Bộ thủ bản Tam tạng kinh Phật được khắc trên lá bối 2000 năm tuổi lần đầu tiên được đưa về Việt Nam và Cố đô Huế là nơi được chọn để trưng bày bản kinh cổ quý giá này.\n ', 'http://static.cand.com.vn/Files/Image/Content/anhkhoa/2016/04/26/thumb_340_9d24adf1-7528-46a7-b762-d37e7beb432c.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/La-boi-khac-Tam-tang-kinh-Phat-2000-nam-tuoi-duoc-dua-ve-Viet-Nam-trien-lam-390499/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1422', 'Độc đáo không gian tre ở Hội An', 'N/a', '2016-06-07 11:51:22', '2016-04-26 15:48:06', ' Có một ngôi nhà xinh đẹp nằm ẩn mình giữa bốn bề toàn cây xanh được tô điểm bởi những vật dụng độc đáo tất cả đều được chế tác từ cây tre. Điều này đã để lại ấn tượng cho nhiều du khách mỗi khi có dịp ghé đến. Đó là căn nhà Taboo Bamboo WorkShop của anh Võ Tấn Tân (38 tuổi), trú thôn 2, xã Cẩm Thanh, TP Hội An, Quảng Nam. ', 'http://static.cand.com.vn/Files/Image/Content/nguyenbinh/2016/04/26/thumb_340_52cf1b9c-83bc-4796-8fbc-65e282236786.jpg', 'http://cand.com.vn/van-hoa/doc-dao-khong-gian-tre-o-Hoi-an-390415/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1423', 'Nhà văn Chu Lai: “Món nợ chiến tranh” chưa lúc nào vơi1', 'N/a', '2016-06-07 11:51:22', '2016-04-25 18:27:39', ' Vẫn vẻ ngoài xù xì, “bụi bặm” và cách trò chuyện ồn ào “bốc lửa”, nhà văn Chu Lai một lần nữa gây hứng thú đặc biệt cho các cán bộ chiến sĩ trong những chia sẻ về quãng đời cầm súng và cầm bút của ông ', 'http://static.cand.com.vn/Files/Image/Content/honghai/2016/04/22/thumb_340_4fc38928-60c1-46d9-bf67-e9ccc64fa1e3.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/Nha-van-Chu-Lai-Mon-no-chien-tranh-chua-luc-nao-voi-390031/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1424', 'Khi hoa hậu, nhà văn văng tục, chửi bậy trên Facebook1', 'N/a', '2016-06-07 11:51:22', '2016-04-22 09:24:19', ' Đa số người dùng Facebook là những người biết chữ và như vậy, đều biết rất rõ chữ nào là \"sạch\", chữ nào là \"bẩn\", là nên dùng hay không. Những người có sự ảnh hưởng nhất định đến công chúng, chắc chắn cũng là những người hiểu rõ sự lan tỏa đến cộng đồng, nhất là những người trẻ, từ những điều mình nói hay viết ra sao ', 'http://static.cand.com.vn/Files/Image/Content/daiviet/2016/04/22/thumb_340_f6056d8f-66ba-402c-8bda-467f7a1cd567.jpg', 'http://cand.com.vn/van-hoa/Khong-the-co-suy-thoi-noi-tuc-chui-bay-tren-Facebook-389974/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1425', 'Duy trì tổ chức vũ hội kèn hơi tại đường Trần Hưng Đạo ', 'N/a', '2016-06-07 11:51:22', '2016-04-21 18:02:07', ' Sau lần tổ chức thí điểm vũ hội kèn hơi đường phố lần đầu tiên (tối 26-3) thu được nhiều thành công, UBND thành phố Đà Nẵng đã chỉ đạo quận phối hợp với Trung tâm Văn hóa thành phố và Trường Trung học Văn hóa Nghệ thuật Đà Nẵng tổ chức vũ hội kèn hơi đường phố lần thứ 2, tiến đến duy trì tổ chức hàng tháng để phục vụ người dân và khách du lịch. ', 'http://static.cand.com.vn/Files/Image/Content/hientk/2016/04/21/thumb_340_96db4613-b993-4f81-a012-2ad22ddf8e12.4.16-lehoiken.JPG', 'http://cand.com.vn/Chuyen-dong-van-hoa/duy-tri-to-chuc-vu-hoi-ken-hoi-tai-duong-Tran-Hung-dao-389921/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1426', 'Văn Mai Hương trẻ trung rạng ngời chấm Idol Kids', 'N/a', '2016-06-07 11:51:22', '2016-04-13 10:53:04', ' Cùng nhạc sỹ Hoài An và ca sỹ Isacc, Văn Mai Hương đã ngồi vào chiếc ghế nóng cuối cùng của Vietnam Idol Kids mùa đầu tiên. Nữ ca sỹ “Mona Lisa” cho biết, cô như tìm lại tuổi thơ của mình qua hình ảnh những em bé háo hức đến sân chơi ca hát dành cho thiếu nhi. ', 'http://static.cand.com.vn/Files/Image/Content/linhchi/2016/04/13/thumb_340_83d8228d-5b56-4738-ace8-b1667f8536d7.4.16-3.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/Van-Mai-Huong-tre-trung-rang-ngoi-cham-idol-Kids-389002/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1427', '60 nghìn vé miễn phí trong đêm nhạc nhớ Trịnh 2016', 'N/a', '2016-06-07 11:51:22', '2016-04-12 16:32:28', ' 60.000 vé được phát ra miễn phí tại TP HCM và Huế;sau nhiều năm vắng bóng, ca sỹ Trịnh Vĩnh Trinh sẽ hát trở lại trong chương trình 15 năm nhớ Trịnh Công Sơn diễn ra tại TP HCM vào ngày 22-4 và tại Huế vào ngày 1-5 tới đây. ', 'http://static.cand.com.vn/Files/Image/Content/bichthuy/2016/04/12/thumb_340_7428de51-e23c-437f-a3ea-588468588c66.4.16.TH.ve2.jpg', 'http://cand.com.vn/Tieu-diem-van-hoa/60-nghin-ve-mien-phi-trong-dem-nhac-nho-Trinh-2016-388897/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1428', 'Nguyễn Văn Vĩnh: Người đầu tiên phổ cập chữ quốc ngữ, khai sinh văn học dịch và báo chí Việt Nam', 'N/a', '2016-06-07 11:51:22', '2016-04-11 18:58:59', ' Dưới những góc nhìn mới mẻ tại buổi tọa đàm “Học giả Nguyễn Văn Vĩnh -  \"Người man di hiện đại\" do Hội Nhà văn Hà Nội tổ chức ngày 11/4, các nhà văn, nhà nghiên cứu đã đánh giá cao những đóng góp của Nguyễn Văn Vĩnh (1882 – 1936) – người vừa được giải Văn hóa Phan Châu Trinh vinh danh là Danh nhân Văn hóa Việt Nam vào tháng 3/2016. ', 'http://static.cand.com.vn/Files/Image/Content/chienthang/2016/04/11/thumb_340_4dc881fe-0931-4f75-b955-81982b9b87cc.JPG', 'http://cand.com.vn/Tieu-diem-van-hoa/Nguyen-Van-Vinh-Nguoi-dau-tien-pho-cap-chu-quoc-ngu-khai-sinh-van-hoc-dich-va-bao-chi-Viet-Nam-388816/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1429', 'Phát hiện di tích khảo cổ có niên đại khoảng 80 vạn năm ', 'N/a', '2016-06-07 11:51:22', '2016-04-11 14:38:03', ' Lần đầu tiên giới khảo cổ học Việt Nam phát hiện di tích sơ kỳ Đá cũ trong tầng văn hóa, nằm cùng tectit có tuổi 77-80 vạn năm. Đây là bước ngoặt trong nhận thức giai đoạn bình minh của lịch sử dân tộc. ', 'http://static.cand.com.vn/Files/Image/Content/thanhbinh/2016/04/11/thumb_340_2a8f0696-2d5d-46bd-85fa-eaa915b2ecc7.4.16-10.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/Phat-hien-di-tich-khao-co-co-nien-dai-khoang-80-van-nam-388757/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1430', 'Chen chân xem bơi chải ở đất Tổ', 'N/a', '2016-06-07 11:51:22', '2016-04-10 19:05:59', ' Sáng 10/4 (tức 4/3 âm lịch), hội thi bơi chải TP Việt Trì mở rộng đã diễn ra tại hồ Công viên Văn Lang (Việt Trì). Đây là một trong những hoạt động đầu tiên nằm trong khuôn khổ các hoạt động Giỗ tổ Hùng Vương – Lễ hội Đền Hùng 2016. ', 'http://static.cand.com.vn/Files/Image/Content/nguyenbinh/2016/04/10/thumb_340_2bbfff74-54cf-470e-ba7f-d2beeafc7cfa.jpg', 'http://cand.com.vn/Chuyen-dong-van-hoa/Chen-chan-xem-boi-chai-o-dat-To-388662/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1431', 'NSƯT Kim Tiến: Hào quang một thuở...', 'N/a', '2016-06-07 11:51:22', '2016-03-24 20:36:25', ' Những ngày này NSƯT Kim Tiến vô cùng bận rộn. Sau khi hoàn tất hồ sơ ứng cử đại biểu Quốc hội, dạy một lớp học tại nhà và vẫn đi làm các chương trình khi được mời, bà còn thêm bận rộn bởi điện thoại reo vang liên tục... ', 'http://static.cand.com.vn/Files/Image/Content/honghai/2016/03/17/thumb_340_d23e787e-8994-4cea-bbff-2afda4c34757.jpg', 'http://cand.com.vn/van-hoa/NSuT-Kim-Tien-Hao-quang-mot-thuo-385916/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1432', 'Nữ đạo diễn \"của hiếm\" của điện ảnh Việt Nam', 'N/a', '2016-06-07 11:51:22', '2016-03-09 08:49:54', ' Đặng Thái Huyền là một “của hiếm” như thế của điện ảnh Việt Nam, khi còn rất trẻ, tên tuổi chị đã được xướng lên trên bục vinh quang trong nhiều Liên hoan phim (LHP), ở cả điện ảnh lẫn truyền hình... ', 'http://static.cand.com.vn/Files/Image/Content/nguyenbinh/2016/03/09/thumb_340_424155fc-5318-420c-8286-2f5cadaf8117.b.jpg', 'http://cand.com.vn/van-hoa/Nu-dao-dien-dang-Thai-Huyen-Nhieu-khi-toi-quen-minh-la-phu-nu-384894/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1433', 'Đạo diễn “Taxi, em tên gì?” giải mã thắc mắc xung quanh nội dung phim25', 'N/a', '2016-06-07 11:51:22', '2016-03-02 21:26:35', ' Sau buổi công chiếu thành công tại TP. HCM, tối 1-3, nhà sản xuất Thiên Phúc, nhà phát hành CGV đã chính thức giới thiệu dự án phim “Taxi, em tên gì? tại CGV Vincom, 191 Bà Triệu, Hà Nội. ', 'http://static.cand.com.vn/Files/Image/Content/thanhbinh/2016/03/02/thumb_340_dd9b17b5-150d-4ee2-8ba9-41f1b10e3a21.3.2016-8.jpg', 'http://cand.com.vn/Giai-tri-van-hoa/dao-dien-Taxi-em-ten-gi-giai-ma-thac-mac-xung-quanh-noi-dung-phim-384210/', '6', '6', '0', '0', '1', null, '71');
INSERT INTO `posts` VALUES ('1436', 'Bí kíp võ công', '<div class=\"content clearfix\">\r\n<div class=\"field field-name-body field-type-text-with-summary field-label-hidden\">\r\n<div class=\"field-items\">\r\n<div class=\"field-item even\">\r\n<p>C&oacute; 1 anh ch&agrave;ng một h&ocirc;m đang đi nhặt l&aacute; đ&aacute; ống bơ th&igrave; nhặt được 1 quyển b&iacute; k&iacute;p.</p>\r\n\r\n<p>Nghi l&agrave; v&otilde; học thượng thừa n&ecirc;n anh ta giấu mang về nh&agrave; đọc.</p>\r\n\r\n<p>Trang 1 : Mi&ecirc;u tả về v&otilde; c&ocirc;ng. C&oacute; thể h&ocirc; mưa gọi gi&oacute;. Độc bộ thi&ecirc;n hạ. Đưa người ta l&ecirc;n đỉnh cao của v&otilde; học.</p>\r\n\r\n<p>Trang 2 : Cần phải tự cung mới c&oacute; thể luyện &hellip;</p>\r\n\r\n<p>Sau một hồi đắn đo suy nghĩ. Anh ta hạ quyết t&acirc;m vung đao tự cung.</p>\r\n\r\n<p>Xong việc anh n&eacute;n đau đọc tiếp b&iacute; k&iacute;p</p>\r\n\r\n<p>Trang 3 : .. kh&ocirc;ng tự cung cũng kh&ocirc;ng sao &hellip;</p>\r\n\r\n<p>Anh ta đi&ecirc;n cuồng. Nổi lửa l&ecirc;n đốt ngay quyển b&iacute; k&iacute;p.</p>\r\n\r\n<p>Khi s&aacute;ch ch&aacute;y gần hết. Trang cuối quyển b&iacute; k&iacute;p bay ra ,ch&agrave;ng trai đưa mắt nh&igrave;n</p>\r\n\r\n<p>Trang cuối : Nếu đ&atilde; tự cung th&igrave; sau khi luyện th&agrave;nh b&iacute; k&iacute;p của qu&yacute; sẽ mọc lại &hellip;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n', '2016-06-14 00:20:21', '2016-06-14 00:19:36', 'Có 1 anh chàng một hôm đang đi nhặt lá đá ống bơ thì nhặt được 1 quyển bí kíp.\r\nNghi là võ học thượng thừa nên anh ta giấu mang về nhà đọc.', '/resources/upload/images/ba19427ab1e98365a56b422c853d874a.jpg', 'Bi-kip-vo-cong', '6', '11', '1', '0', '2', 'baotoan.95@gmail.com', null);
