INSERT INTO `posts` VALUES ('1459', 'Climate change implicated in France floods', 'N/a', '2016-06-14 11:00:54', '2016-06-14 10:12:31', 'Torrential rains which caused flooding in France recently bore the unmistakable fingerprint of climate change, according to research to be submitted to a scientific journal next week.', 'http://img.f47.english.vnecdn.net/2016/06/13/flood8JPG-1465812183-2730-1465812204_180x108.jpg', 'http://e.vnexpress.net/news/development/climate-change-implicated-in-france-floods-3419272.html', '6', '1', '0', '0', '1', null, '51');
