INSERT INTO `posts` VALUES ('1505', 'Fakes flood market as Vietnam struggles to combat counterfeit products', 'N/a', '2016-06-14 17:14:52', '2016-06-14 16:51:44', 'Each year, Vietnam’s market management authorities detect about 10,000 cases related to counterfeiting and piracy, but only 14 percent are ever brought to court.', 'http://img.f46.english.vnecdn.net/2016/06/14/HQmongcai11-1465893395_1465893421_180x108.jpg', 'http://e.vnexpress.net/news/news/fakes-flood-market-as-vietnam-struggles-to-combat-counterfeit-products-3419804.html', '6', '1', '0', '0', '1', null, '51');
