INSERT INTO `posts` VALUES ('1508', 'Đám đông phá tiệc cưới bằng rải vàng mã, mở nhạc đám ma', 'N/a', '2016-06-14 17:14:52', '2016-06-14 16:44:56', 'Mạng xã hội đang lan truyền clip ghi lại cảnh một đám đông kéo đến phá đám cưới  trên đường Nguyễn Chí Thanh, thị trấn Quảng Phú, huyện Cư M\'gar, tỉnh Đắk Lắk. Chú rể cưới vợ lần thứ hai và đã có nhiều con lớn với người vợ cũ. Nhiều người đội khăn trắng khóc lóc thảm thiết. Sự việc diễn ra trong thời gian khoảng 20 phút trong ngày 12/6.', 'http://news.img.video.vnecdn.net/web/2016/06/14/dam-dong-pha-tiec-cuoi-bang-rai-vang-ma-mo-nhac-dam-ma-1465897292_180x108.jpg', 'http://video.vnexpress.net/camera-ban-doc/dam-dong-pha-tiec-cuoi-bang-rai-vang-ma-mo-nhac-dam-ma-3419894.html', '6', '1', '0', '0', '1', null, '51');
